INSERT INTO `books` (`id`, `name`, `slug`, `summary`, `description`, `category_id`, `thumb`, `author`, `hot_book`, `views`, `active`, `created_at`, `updated_at`) VALUES
(1, 'Batman', 'batman', 'adad', '<p>rqrqr</p>', 3, 'storage/images/o7aXq5sFJJkMRMovYg2P10ysQH46hzTPapmkigSL.webp', 'DDS', 0, 3, 1, '2022-05-11 10:42:08', '2022-05-11 10:42:08'),
(3, 'CHIỀU TÀ RƠI RỤNG', 'chieu-ta-roi-rung', 'fqfqf', '<p>T&aacute;c giả: Tri&ecirc;m Y<br />\r\n<br />\r\nSố chương: 10 chương<br />\r\n<br />\r\nDịch: B&eacute; Đẹp &ndash; Những ng&agrave;y xanh th&acirc;n &aacute;i<br />\r\n<br />\r\nHỗ trợ beta: Xanh Xanh, Jen<br />\r\n<br />\r\nThể loại: Ng&ocirc;n t&igrave;nh, hiện đại, trinh th&aacute;m, b&aacute;o th&ugrave;, ph&aacute; &aacute;n, HE.<br />\r\n<br />\r\nVăn &aacute;n<br />\r\n<br />\r\nMột h&ograve;n đảo hoang giữa l&ograve;ng s&ocirc;ng, 5 nam 5 nữ, m&aacute;u.<br />\r\n<br />\r\nNgười nối tiếp nhau chết, giết v&igrave; tiền của? Giết v&igrave; t&igrave;nh? Hay l&agrave; v&igrave; b&aacute;o th&ugrave;?<br />\r\n<br />\r\nNgười tiếp theo phải chết l&agrave; bạn hay l&agrave; t&ocirc;i đ&acirc;y?<br />\r\n<br />\r\nMặt trời ngả về T&acirc;y tr&ecirc;n s&ocirc;ng, m&agrave;u &aacute;nh chiều t&agrave; đỏ quạch như m&agrave;u m&aacute;u.<br />\r\n<br />\r\nRốt cuộc c&oacute; bao nhi&ecirc;u m&aacute;u tươi đ&atilde; tạo th&agrave;nh m&agrave;u đỏ tr&ecirc;n s&ocirc;ng Lạc Quỳ thế n&agrave;y?<br />\r\n<br />\r\nNgười dịch c&oacute; lời muốn n&oacute;i:<br />\r\n<br />\r\nT&ecirc;n truyện lấy từ t&ecirc;n của nh&acirc;n vật Cảnh Nhật Tịch, &ldquo;Tịch&rdquo; c&oacute; c&oacute; nghĩa &ldquo;chiều t&agrave;&rdquo;.<br />\r\n<br />\r\nLộ Hiểu Nguyệt: Lộ l&agrave; con đường, Hiểu c&oacute; nghĩa l&agrave; s&aacute;ng tỏ. Trong truyện, Lộ Hiểu Nguyệt cũng l&agrave; nh&acirc;n vật dẫn chuyện cũng như l&agrave; người l&agrave;m s&aacute;ng tỏ mọi việc.<br />\r\n<br />\r\nTruyện n&agrave;y được viết từ năm 2004, v&igrave; thế c&oacute; thể motip kh&aacute; cũ, nhưng theo m&igrave;nh đ&aacute;nh gi&aacute; văn phong truyện n&agrave;y kh&aacute; ổn, kiểu thơ nhưng kh&ocirc;ng bị lố qu&aacute;.<br />\r\n<br />\r\nBối cảnh truyện (c&oacute; vẻ như) lấy cảm hứng từ quyển And there there were none của Agatha Christie (1 tập trong Conan cũng c&oacute; motip kiểu thế n&agrave;y). N&oacute;i chung l&agrave; v&igrave; c&oacute; 10 chương v&agrave; l&agrave; người dịch n&ecirc;n Đẹp kh&ocirc;ng thấy c&oacute; g&igrave; bất ngờ =))) Đ&acirc;y l&agrave; một truyện ngắn ổn để nh&acirc;m nhi trong l&uacute;c rảnh rỗi chờ Tết thui chứ kh&ocirc;ng qu&aacute; xuất sắc đ&acirc;u n&egrave;~</p>', 3, 'storage/images/OL6JS021BNCCBfQvRBi9EjiW3b0cnSGhhphOSs6D.jpg', 'Tao', 0, 4, 1, '2022-05-11 10:41:44', '2022-05-11 10:41:44'),
(5, 'THẾ GIỚI HOÀN MỸ', 'the-gioi-hoan-my', 'qrqw', '<p><strong><em>Dịch giả:</em></strong><em>&nbsp;Ronkute, Yencute, Ryuken, kukhoai, bokinhvan, Kensin_Kaoru, Big Bang, Luciferht, Độc Nh&acirc;n</em><br />\r\n<br />\r\nMột hạt bụi c&oacute; thể lấp biển, một cọng cỏ ch&eacute;m hết mặt trời mặt trăng v&agrave; ng&ocirc;i sao, trong nh&aacute;y mắt ở giữa long trời lỡ đất. Quần h&ugrave;ng c&ugrave;ng nổi l&ecirc;n, vạn tộc mọc l&ecirc;n san s&aacute;t như rừng, chư th&aacute;nh tranh b&aacute;, loạn khắp đất trời. Hỏi mặt đất bao la, cuộc đời thăng trầm? Một thiếu ni&ecirc;n theo trong đất hoang đi ra, tất cả bắt đầu từ nơi n&agrave;y.</p>', 2, 'storage/images/5giqWrVUgv8UPgJ3OUGEYYl4FjgknlBMDYvyhsL2.jpg', 'TIID', 1, 1809, 1, '2022-05-11 10:41:23', '2022-06-24 15:08:15'),
(6, 'HOÀNG HÔN', 'hoang-hon', 'Hoàng Hôn\r\nTác Giả: Mai Thập Nguyệt', '<p>Ho&agrave;ng H&ocirc;n<br />\r\n<br />\r\nT&aacute;c Giả: Mai Thập Nguyệt<br />\r\n<br />\r\nThể Loại: Nguy&ecirc;n Sang, Ng&ocirc;n T&igrave;nh, Hiện Đại, HE, T&igrave;nh Cảm, Đ&ocirc; Thị T&igrave;nh Duy&ecirc;n, G&oacute;c Nh&igrave;n Nữ Ch&iacute;nh.<br />\r\n<br />\r\nĐộ D&agrave;i: 14 Chương + Lời Của T&aacute;c Giả.<br />\r\n<br />\r\nEditor: Doris<br />\r\n<br />\r\n____________________<br />\r\n<br />\r\nVăn &Aacute;n<br />\r\n<br />\r\nT&igrave;nh cảm thời thanh xu&acirc;n, cuối c&ugrave;ng trở n&ecirc;n tiếc nuối.<br />\r\n<br />\r\nThiếu ni&ecirc;n L&acirc;m Nhiễm ngang nhi&ecirc;n trở th&agrave;nh thời thanh xu&acirc;n của Chu Mộ.<br />\r\n<br />\r\nSau khi gặp lại, người đ&agrave;n &ocirc;ng trầm ổn ở tr&ecirc;n b&agrave;n tiệc cười n&oacute;i vui vẻ c&ugrave;ng một đ&aacute;m người.<br />\r\n<br />\r\nBởi v&igrave; c&oacute; người khi gặp lại nhau cũng sẽ động l&ograve;ng.<br />\r\n<br />\r\nL&acirc;m Nhiễm, cảm ơn anh đ&atilde; xuất hiện ở thời thanh xu&acirc;n của em, lắp đầy những thiếu s&oacute;t.<br />\r\n<br />\r\nC&acirc;u chuyện ngắn đ&atilde; kết th&uacute;c, rất ngắn, chỉ l&agrave; một c&acirc;u chuyện xưa ch&ocirc;n s&acirc;u dưới đ&aacute;y l&ograve;ng.<br />\r\n<br />\r\nTag: Đ&ocirc; thị t&igrave;nh duy&ecirc;n<br />\r\n<br />\r\nLập &yacute;: Trưởng th&agrave;nh</p>', 3, 'storage/images/tnkboO71KTAyoXOnTck0mewBruKWL9vEtfiesupn.jpg', 'Bạch Hi Thần', 0, 13, 1, '2022-05-11 10:40:44', '2022-05-11 10:40:44'),
(7, 'TOÀN CHỨC PHÁP SƯ CHI MẠC THIÊN', 'toan-chuc-phap-su-chi-mac-thien', 'Tác giả: Tiêu Vô Trân\r\nThể loại: Truyện Khác', '<p><span style=\"font-size:16px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&aacute;c giả: Ti&ecirc;u V&ocirc; Tr&acirc;n<br />\r\n<br />\r\nThể loại: Truyện Kh&aacute;c<br />\r\n<br />\r\nGiới thiệu:<br />\r\n<br />\r\nTruyện To&agrave;n Chức Ph&aacute;p Sư Chi Mạc Thi&ecirc;n của t&aacute;c giả Ti&ecirc;u V&ocirc; Tr&acirc;n n&oacute;i về h&agrave;nh tr&igrave;nh của Mạc Thi&ecirc;n sau khi xuy&ecirc;n qua to&agrave;n chức ph&aacute;p sư, trở th&agrave;nh song b&agrave;o thai huynh đệ với Mạc Ph&agrave;m<br />\r\n<br />\r\nNơi m&agrave; thế giới t&ocirc;n s&ugrave;ng ma ph&aacute;p<br />\r\n<br />\r\nNơi m&agrave; y&ecirc;u ma ở khắp loại lục đục nội đấu<br />\r\n<br />\r\nH&atilde;y đ&oacute;n xem cuộc h&agrave;nh tr&igrave;nh của Mạc Thi&ecirc;n tại thế giới mới nh&eacute;.</span></span></p>', 2, 'storage/images/4siqLTXHLdBPzM208FyFjy3zFc0PM89nOmSALdj0.jpg', 'Tiêu Vô Trân', 1, 3225, 1, '2022-05-11 10:40:25', '2022-05-11 10:40:25'),
(15, 'BÁ VƯƠNG VŨ TRỤ', 'ba-vuong-vu-tru', 'Tác giả: James Huỳnh\r\nThể loại: Xuyên Không, Dị Giới, Truyện Khác, Tiên Hiệp, Kiếm Hiệp, Huyền Huyễn', '<p>T&aacute;c giả: James Huỳnh<br />\r\n<br />\r\nThể loại: Xuy&ecirc;n Kh&ocirc;ng, Dị Giới, Truyện Kh&aacute;c, Ti&ecirc;n Hiệp, Kiếm Hiệp, Huyền Huyễn<br />\r\n<br />\r\nGiới thiệu:<br />\r\n<br />\r\nTruyện về một thằng nh&oacute;c 12 tuổi, mồ c&ocirc;i v&agrave; trẻ tr&acirc;u đang ở trường đột nhi&ecirc;n v&agrave;o một thế giới kh&aacute;c</p>', 4, 'storage/images/C1WEPOJLbgqNI4JMCeGCF3UeVoJCh0nwDFzzVi4A.jpg', 'eqwewer', 1, 2727, 1, '2022-05-11 10:40:08', '2022-05-11 10:40:08'),
(16, 'MA GIÁO CHI CHỦ', 'ma-giao-chi-chu', 'Văn án:\r\n\r\nLục Quân Hi mang theo hệ thống biến dị của trò chơi Kiếm Tam xuyên việt đến thế giới võ hiệp.\r\nChỉ có tại đây võ hiệp thế giới trung tướng Minh Giáo phát triển trở thành vi đệ nhất giáo phái, hắn tài năng đạt được cơ hội về nhà.\r\nVì về nhà, Lục Quân Hi cắn răng quyết định đem Tây Phương Ma Giáo như mặt trời ban trưa thôn tính.\r\nNhưng mà...\r\n\"Thỉnh hiệp sĩ đầu tiên trở thành Minh Giáo giáo chủ.\"\r\nNgọc La Sát: Làm giáo chủ phu nhân chẳng lẽ không tốt?', '<p>Văn &aacute;n:<br />\r\n<br />\r\nLục Qu&acirc;n Hi mang theo hệ thống biến dị của tr&ograve; chơi Kiếm Tam xuy&ecirc;n việt đến thế giới v&otilde; hiệp.<br />\r\nChỉ c&oacute; tại đ&acirc;y v&otilde; hiệp thế giới trung tướng Minh Gi&aacute;o ph&aacute;t triển trở th&agrave;nh vi đệ nhất gi&aacute;o ph&aacute;i, hắn t&agrave;i năng đạt được cơ hội về nh&agrave;.<br />\r\nV&igrave; về nh&agrave;, Lục Qu&acirc;n Hi cắn răng quyết định đem T&acirc;y Phương Ma Gi&aacute;o như mặt trời ban trưa th&ocirc;n t&iacute;nh.<br />\r\nNhưng m&agrave;...<br />\r\n&quot;Thỉnh hiệp sĩ đầu ti&ecirc;n trở th&agrave;nh Minh Gi&aacute;o gi&aacute;o chủ.&quot;<br />\r\nNgọc La S&aacute;t: L&agrave;m gi&aacute;o chủ phu nh&acirc;n chẳng lẽ kh&ocirc;ng tốt?<br />\r\nLục Qu&acirc;n Hi:...Ha ha<br />\r\n<br />\r\ncp: Ngọc La S&aacute;t x Lục Qu&acirc;n Hi<br />\r\n<br />\r\nBi&ecirc;n tập vi&ecirc;n đ&aacute;nh gi&aacute;: Lục Qu&acirc;n Hi mang theo hệ thống Kiếm Tam biến dị xuy&ecirc;n kh&ocirc;ng, chỉ l&agrave; nghĩ đem Minh Gi&aacute;o ph&aacute;t triển th&agrave;nh Ma gi&aacute;o.<br />\r\nHo&agrave;n th&agrave;nh nhiệm vụ về nh&agrave;, lại tại ngay trạng th&aacute;i dịch dung cứu T&acirc;y Phương Ma Gi&aacute;o gi&aacute;o chủ tương lai Ngọc La S&aacute;t.<br />\r\nTừ đ&oacute; về sau bị Ngọc La S&aacute;t d&acirc;y dưa kh&ocirc;ng dứt, chỉ c&oacute; thể tại tinh ph&acirc;n tr&ecirc;n đường c&agrave;ng đi c&agrave;ng xa.<br />\r\nVăn n&agrave;y văn phong hoan tho&aacute;t thoải m&aacute;i, c&aacute;c v&otilde; hiệp thế giới kịch t&igrave;nh bị xảo diệu dung hợp vi một thể.<br />\r\nNhất t&acirc;m tưởng trở th&agrave;nh Ma Gi&aacute;o gi&aacute;o chủ Lục Qu&acirc;n Hi c&ugrave;ng T&acirc;y Phương Ma Gi&aacute;o gi&aacute;o chủ Ngọc La S&aacute;t chi gian tương s&aacute;t tương &aacute;i cảm t&igrave;nh tiến triển.<br />\r\nP/S: kh&ocirc;ng chắc chắn đ&uacute;ng 100%, c&oacute; sự gi&uacute;p đỡ của QT ca ca, google-sama v&agrave; bản gốc mama</p>', 5, 'storage/images/ZqOqR7CDIFdSzJszDteDmO8y2ohm8eqgThTX36oI.jpg', 'Thần Thần tiểu thiên', 1, 55, 1, '2022-05-11 10:39:21', '2022-05-19 02:31:35'),
(17, 'THẾ GIỚI XUYÊN QUA', 'the-gioi-xuyen-qua', 'Bạn đang đọc truyện Thế Giới Xuyên Qua của tác giả Tâm Hữu Kỳ Đãi.', '<p>Bạn đang đọc truyện&nbsp;<strong>Thế Giới Xuy&ecirc;n Qua</strong>&nbsp;của t&aacute;c giả&nbsp;<strong>T&acirc;m Hữu Kỳ Đ&atilde;i</strong>. Bốn lần xuy&ecirc;n qua đều th&agrave;nh thai nhi, Phuơng Lập An to&agrave;n phải bắt đầu từng bước một, đến thế giới thứ tư n&agrave;y, hệ thống mới đột nhi&ecirc;n xuất hiện.<br />\r\n<br />\r\nTới đ&acirc;y, tới đ&acirc;y! Vui kh&ocirc;ng n&agrave;o? Ch&uacute;ng ta c&ograve;n nhiều thời gian lắm nha ~~~</p>', 5, 'storage/images/N5QFkheSON3C0EYv5XwHPUDQr8xQmnnEDHGAx9cP.jpg', 'Tâm Hữu Kỳ Đãi', 1, 32, 1, '2022-05-11 10:39:03', '2022-05-19 02:33:35'),
(23, 'DỊ GIỚI KHÂM LIỆM SƯ', 'di-gioi-kham-liem-su', 'qwqwerw', '<p>ertert</p>', 4, 'storage/images/WbvGrxqFhciLgeWYKczDd3MBmLs89cqs2xAmKdsp.jpg', 'Bạch Hi Thần', 0, 9, 1, '2022-05-11 10:31:00', '2022-05-19 01:50:08');

