INSERT INTO `chapters` (`id`, `book_id`, `name`, `slug`, `description`, `content`, `active`, `created_at`, `updated_at`) VALUES
(1, 5, 'Chương 1: Đại Hoang', 'chuong-1-dai-hoang', '<p>gsgsgafafaf</p>', '<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&ecirc;m đ&atilde; về khuya, b&oacute;ng tối phủ k&iacute;n khắp nơi, bao tr&ugrave;m l&ecirc;n cảnh vật. Thế nhưng trong n&uacute;i lại chẳng hề y&ecirc;n tĩnh, tiếng m&atilde;nh th&uacute; r&iacute;t g&agrave;o rung động cả non s&ocirc;ng, c&acirc;y cối rung l&ecirc;n, l&aacute; bay t&aacute;n loạn.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chốn rừng n&uacute;i m&ecirc;nh mang l&agrave; nơi hoạt động của v&ocirc; số m&atilde;nh th&uacute; hồng hoang c&ugrave;ng những chủng tộc s&oacute;t lại từ thời Th&aacute;i Cổ. Tiếng k&ecirc;u đ&aacute;ng sợ của mu&ocirc;n lo&agrave;i rống l&ecirc;n trong b&oacute;ng tối, khiến mặt đất như muốn nứt to&aacute;c ra.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Từ trong d&atilde;y n&uacute;i tr&ocirc;ng xa thấy c&oacute; thấp tho&aacute;ng một quầng s&aacute;ng nhu h&ograve;a, tựa như một ngọn nến lập l&ograve;e dưới m&agrave;n đ&ecirc;m đen v&ocirc; tận, lẩn khuất giữa mu&ocirc;n tr&ugrave;ng n&uacute;i, &aacute;nh s&aacute;ng dường như c&oacute; thể vụt tắt bất cứ l&uacute;c n&agrave;o.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đến gần hơn, c&oacute; thể thấy r&otilde; ở đ&oacute; c&oacute; một nửa th&acirc;n c&acirc;y kh&ocirc; khổng lồ, đường k&iacute;nh th&acirc;n c&acirc;y ước chừng hơn chục m&eacute;t, to&agrave;n th&acirc;n ch&aacute;y đen, ngoại trừ một nửa th&acirc;n c&acirc;y n&agrave;y, chỉ c&ograve;n lại một c&agrave;nh c&acirc;y yếu ớt nhưng lại tỏa ra sức sống. L&aacute; c&acirc;y lung linh như được khắc từ lục ngọc, t&aacute;n ph&aacute;t từng đốm s&aacute;ng nhu h&ograve;a bao tr&ugrave;m lấy cả một th&ocirc;n l&agrave;ng.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">N&oacute;i một c&aacute;ch ch&iacute;nh x&aacute;c th&igrave; đ&acirc;y l&agrave; một th&acirc;n c&acirc;y s&eacute;t đ&aacute;nh, rất nhiều năm về trước n&oacute; từng gặp phải một trận s&eacute;t th&ocirc;ng thi&ecirc;n, v&ograve;m c&acirc;y um t&ugrave;m c&ugrave;ng sức sống tr&agrave;n trề của gốc liễu gi&agrave; đ&atilde; bị sấm s&eacute;t ph&aacute; hủy. Nay chỉ c&ograve;n lại một đoạn gốc cao chừng t&aacute;m ch&iacute;n m&eacute;t trồi l&ecirc;n mặt đất, đường k&iacute;nh rộng kinh người, c&agrave;nh liễu duy nhất s&oacute;t lại kia tr&ocirc;ng như sợi x&iacute;ch thần bằng m&acirc;y lục b&iacute;ch, h&agrave;o quang ngập tr&agrave;n bao tr&ugrave;m che chở cho cả th&ocirc;n l&agrave;ng, khiến mảnh đất n&agrave;y trở n&ecirc;n m&ocirc;ng lung như một v&ugrave;ng ti&ecirc;n thổ, trong chốn đại hoang cảm gi&aacute;c v&ocirc; c&ugrave;ng thần b&iacute;.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; nh&agrave; trong th&ocirc;n đều x&acirc;y bằng đ&aacute;. Đ&ecirc;m khuya thanh vắng, vẻ an l&agrave;nh y&ecirc;n ả của chốn n&agrave;y dường như t&aacute;ch biệt hẳn với b&oacute;ng tối c&ugrave;ng tiếng m&atilde;nh th&uacute; g&agrave;o th&eacute;t b&ecirc;n ngo&agrave;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;H&uacute; uuuuu&hellip;.&quot;</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một trận cuồng phong thổi qua, c&oacute; đ&aacute;m m&acirc;y đen khổng lồ vắt ngang trời, che k&iacute;n cả m&agrave;n đ&ecirc;m, chắn nốt ch&uacute;t x&iacute;u &aacute;nh sao yếu ớt khiến d&atilde;y n&uacute;i c&agrave;ng th&ecirc;m tăm tối.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một tiếng chim k&ecirc;u hung tợn từ tr&ecirc;n cao vọng xuống, tiếng k&ecirc;u sắc lẻm c&oacute; sức xuy&ecirc;n thấu mạnh, kh&ocirc;ng ngờ lại bắt nguồn từ đ&aacute;m m&acirc;y đen kia. Nh&igrave;n kỹ, th&igrave; ra đ&oacute; l&agrave; một con chim khổng lồ to đến kh&ocirc;ng tưởng, che lấp trăng sao, d&agrave;i kh&ocirc;ng biết bao nhi&ecirc;u dặm.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đi qua Thạch Th&ocirc;n, n&oacute; c&uacute;i đầu nh&igrave;n xuống, hai con mắt tựa như hai vầng trăng m&aacute;u, hung kh&iacute; ng&uacute;t trời. N&oacute; nh&igrave;n chằm chằm gốc liễu gi&agrave; trong chốc l&aacute;t, cuối c&ugrave;ng bay về ph&iacute;a s&acirc;u nhất trong d&atilde;y n&uacute;i.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Y&ecirc;n ắng một l&uacute;c l&acirc;u m&atilde;i cho đến tận nửa đ&ecirc;m, mặt đất bắt đầu rung chuyển. Một b&oacute;ng d&aacute;ng mơ hồ từ phương xa bước tới, cao ngang đỉnh n&uacute;i!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một hơi thở k&igrave; lạ lan tỏa, n&uacute;i rừng lặng ngắt một c&aacute;ch chết ch&oacute;c, hung cầm m&atilde;nh th&uacute; c&uacute;i rạp m&igrave;nh kh&ocirc;ng d&aacute;m ph&aacute;t ra một ch&uacute;t x&iacute;u &acirc;m thanh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lại gần, đ&acirc;y l&agrave; một sinh vật c&oacute; h&igrave;nh người, d&aacute;ng đứng thẳng, n&oacute; cao lớn v&ocirc; c&ugrave;ng, s&aacute;nh ngang đỉnh n&uacute;i, to&agrave;n th&acirc;n n&oacute; kh&ocirc;ng c&oacute; l&ocirc;ng t&oacute;c g&igrave; m&agrave; d&agrave;y đặc một lớp vảy v&agrave;ng kim lấp l&aacute;nh. Mặt n&oacute; phẳng l&igrave;, chỉ c&oacute; một con mắt thẳng, mỗi lần chớp mở như c&oacute; &aacute;nh s&eacute;t v&agrave;ng kim rạch qua, sắc b&eacute;n gh&ecirc; người. To&agrave;n th&acirc;n n&oacute; huyết kh&iacute; m&ecirc;nh m&ocirc;ng, tựa như một vị thần ma!</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">N&oacute; đi ngang qua chốn n&agrave;y, liếc nh&igrave;n gốc liễu, tho&aacute;ng dừng ch&acirc;n rồi dường như đang vội v&atilde; l&ecirc;n đường, cuối c&ugrave;ng nhanh ch&oacute;ng bỏ đi. V&ocirc; v&agrave;n ngọn n&uacute;i như đang r&ecirc;n rỉ dưới từng bước ch&acirc;n của n&oacute;, cả chốn n&uacute;i rừng cũng bị uy thế ấy l&agrave;m cho run rẩy.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">B&igrave;nh minh l&ecirc;n, một con rết d&agrave;i mười m&eacute;t, th&acirc;n to như th&ugrave;ng nước, tỏa &aacute;nh s&aacute;ng bạc lấp l&aacute;nh b&ograve; ngoằn ngo&egrave;o trong n&uacute;i. Con rết như được đ&uacute;c từ bạc trắng, mỗi một đốt đều s&aacute;ng b&oacute;ng dữ dằn, đập v&agrave;o đ&aacute; n&uacute;i rầm rầm, tia lửa bắn tung t&oacute;e. Nhưng cuối c&ugrave;ng n&oacute; lại tr&aacute;nh qua Thạch Th&ocirc;n m&agrave; kh&ocirc;ng hề x&acirc;m nhập, nơi n&oacute; đi qua sương đen mịt m&ugrave;, mu&ocirc;ng th&uacute; đều lẩn tr&aacute;nh.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một c&agrave;nh liễu mảnh mai tỏa &aacute;nh s&aacute;ng b&iacute;ch h&agrave; &oacute;ng &aacute;nh khẽ đung đưa trong gi&oacute;&hellip;.</span></span></p>', 1, '2022-03-08 03:18:07', '2022-03-15 02:11:15'),
(2, 15, 'Chương 1: Hoàng Quốc', 'chuong-1-hoang-quoc', '<p>M&ugrave;a xu&acirc;n, Hồ Ch&iacute; Minh.Gi&oacute; xu&acirc;n nh&egrave; nhẹ thổi qua những con phố.Đột nhi&ecirc;n từ trong hẻm c&oacute; một c&aacute;i đầu lấp l&oacute;.</p>', '<p style=\"text-align:justify\"><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">M&ugrave;a xu&acirc;n, Hồ Ch&iacute; Minh.Gi&oacute; xu&acirc;n nh&egrave; nhẹ thổi qua những con phố.Đột nhi&ecirc;n từ trong hẻm c&oacute; một c&aacute;i đầu lấp l&oacute;.N&oacute; chạy thật nhanh về cửa h&agrave;ng tạp h&oacute;a gần nhất v&agrave; ch&ocirc;m lấy một g&oacute;i sandwich.N&oacute; t&ecirc;n l&agrave; Ho&agrave;ng Quốc, năm nay 12 tuổi, mồ c&ocirc;i, kh&ocirc;ng biết chữ n&ecirc;n ở ch&ugrave;a theo đạo, tuy vậy vẫn thường xuống phố ăn cắp vặt, bị sư thầy mắng t&eacute; t&aacute;t mỗi lần xuống phố.N&oacute; đang chạy đi, mặc kệ tiếng b&agrave; chủ cửa h&agrave;ng qu&aacute;t th&aacute;o ph&iacute;a sau: - Thằng Quốc lại đ&acirc;y tao biểu coi! M&agrave;y kh&ocirc;ng nghe b&agrave; l&agrave; b&agrave; m&eacute;c với sư thầy &agrave;.Mặc kệ tiếng la h&eacute;t của b&agrave;, thằng Quốc vẫn chạy đi.Khi n&oacute; về ch&ugrave;a, đột nhi&ecirc;n n&oacute; nghe c&oacute; tiếng sư cụ gọi n&oacute;: - Quốc v&agrave;o đ&acirc;y đi con, sư thầy c&oacute; việc gọi con.Trong một gi&acirc;y, n&oacute; gần như đứng h&igrave;nh.<br />\r\n<br />\r\nVậy l&agrave; sư thầy đ&atilde; biết việc l&uacute;c n&atilde;y, hơn nữa b&agrave; chủ cửa h&agrave;ng đ&oacute; c&ograve;n l&agrave; mẹ thầy.<br />\r\n<br />\r\nN&oacute; thầm nghĩ, &quot;&Ocirc;i th&ocirc;i toang rồi&quot;.<br />\r\n<br />\r\nSau vụ n&agrave;y chắc n&oacute; phải cuốn g&oacute;i ra khỏi ch&ugrave;a th&ocirc;i.<br />\r\n<br />\r\n<br />\r\nTho&aacute;ng sau, n&oacute; đ&atilde; về ph&ograve;ng với cặp m&ocirc;ng đang rươm rướm m&aacute;u, hai b&agrave;n tay sưng bầm l&ecirc;n v&agrave; một khu&ocirc;n mặt đầy dấu tay.Đột nhi&ecirc;n, từ trong g&oacute;c ph&ograve;ng l&oacute;e ra một tia s&aacute;ng từ ph&iacute;a sau c&aacute;i nệm của n&oacute;.<br />\r\n<br />\r\nN&oacute; lại gần v&agrave; đụng ngay v&agrave;o một c&otilde;i hư v&ocirc;.Tr&ecirc;n đầu v&agrave; xung quanh n&oacute; ho&agrave;n to&agrave;n tối đen, qu&aacute; hoảng, n&oacute; h&eacute;t to l&ecirc;n: - Thầy ơi, sư cụ ơi, tụi sư tiểu ơi ới ời ơi.Kh&ocirc;ng c&oacute; ai đ&aacute;p lại, chỉ c&oacute; tiếng n&oacute; vọng đi thật xa.Đột nhi&ecirc;n b&ecirc;n tai n&oacute; ph&aacute;t ra một tiếng động, kh&ocirc;ng, l&agrave; một tiếng n&oacute;i: - H&agrave;i tử, con kh&ocirc;ng việc g&igrave; phải sợ, họ đều ở trong ch&ugrave;a, c&oacute; m&igrave;nh con th&ocirc;i.N&oacute; quay phắt lại.<br />\r\n<br />\r\nĐập v&agrave;o mặt n&oacute; l&agrave; một cụ gi&agrave; c&oacute; vẻ hiền l&agrave;nh, r&acirc;u t&oacute;c bạc phơ, d&agrave;i chấm đất.<br />\r\n<br />\r\nTr&ecirc;n người cụ gi&agrave; mặc một bộ đồ m&agrave;u trắng kiểu cổ trang, tr&ecirc;n tay cầm một cuốn s&aacute;ch, c&oacute; vẻ như l&agrave; một người thầy.<br />\r\n<br />\r\n&Ocirc;ng cụ lại n&oacute;i: - Đ&atilde; tới l&uacute;c con phải hiểu tất cả rồi.Tất cả? Tất cả thứ g&igrave;?&Ocirc;ng cụ lại cất tiếng n&oacute;i: - Chắc con ngạc nhi&ecirc;n lắm phải kh&ocirc;ng? Đừng lo v&agrave; cũng đừng nghĩ ta c&oacute; &aacute;c &yacute; với con, con sẽ biết.<br />\r\n<br />\r\n&Agrave;, cho con v&agrave;i thứ...Dứt lời, &ocirc;ng l&atilde;o đưa ra cho n&oacute; một v&agrave;i cuốn s&aacute;ch, một bộ gi&aacute;p v&agrave; hai mươi t&aacute;m c&acirc;y kiếm.B&acirc;y giờ th&igrave; n&oacute; mới để &yacute; h&igrave;nh như sau lưng &ocirc;ng l&atilde;o c&oacute; &aacute;nh s&aacute;ng giống như &aacute;nh s&aacute;ng l&uacute;c n&atilde;y trong ph&ograve;ng n&oacute; ở ch&ugrave;a.<br />\r\n<br />\r\n&Ocirc;ng l&atilde;o để &yacute;, n&oacute;i: - C&aacute;i n&agrave;y th&igrave; mai mốt con sẽ biết, b&acirc;y giờ th&igrave; đưa con tới nơi đ&atilde;.Đột nhi&ecirc;n n&oacute; thấy m&igrave;nh ở trước một t&ograve;a thủ phủ rất lớn, xung quanh người qua lại tấp nập.<br />\r\n<br />\r\n<br />\r\nT&ograve;a phủ n&agrave;y c&oacute; tường l&agrave;m bằng v&agrave;ng, m&aacute;i ng&oacute;i đỏ, tr&ecirc;n bốn g&oacute;c m&aacute;i c&oacute; 4 người đ&agrave;n &ocirc;ng c&ugrave;ng với 4 con vật b&ecirc;n cạnh họ.<br />\r\n<br />\r\nN&oacute; lại nghe tiếng &ocirc;ng cụ b&ecirc;n tai: - Ở g&oacute;c b&ecirc;n phải ph&iacute;a trước l&agrave; Quỷ chủ V&otilde; Hận Thi&ecirc;n c&ugrave;ng với Bắc Phương Huyền Vũ, b&ecirc;n tr&aacute;i ph&iacute;a trước l&agrave; Ti&ecirc;n chủ To&agrave;n &Acirc;n Thi&ecirc;n c&ugrave;ng với Nam Phương Chu Tước, b&ecirc;n tr&aacute;i ph&iacute;a sau l&agrave; Thần chủ Văn Hữu Thi&ecirc;n c&ugrave;ng với Đ&ocirc;ng Phương Thanh Long, cuối c&ugrave;ng l&agrave; Ma chủ Song V&ocirc; Thi&ecirc;n c&ugrave;ng với T&acirc;y Phương Kỳ L&acirc;n.<br />\r\n<br />\r\nĐ&oacute; l&agrave; B&aacute;t Tộc sống quanh Nhị Thập Cửu T&uacute; ứng với Quỷ, Long, Ti&ecirc;n, Phụng, Thần, L&acirc;n, Ma, Quy ở t&aacute;m ph&iacute;a từ Bắc đến T&acirc;y Bắc.N&oacute; nghe th&igrave; hết hồn, tr&ecirc;n đời n&agrave;y c&ograve;n c&oacute; Ma Quỷ Thần Ti&ecirc;n, xem ra n&oacute; đ&atilde; đi ra khỏi Tr&aacute;i Đất rồi.<br />\r\n<br />\r\n&Agrave;, n&oacute;i đến rời khỏi Tr&aacute;i Đất, n&oacute; hiện đang ở Thi&ecirc;n Địa cầu, một h&agrave;nh tinh nằm ngo&agrave;i tầm quan s&aacute;t của thi&ecirc;n văn học Tr&aacute;i Đất.V&agrave; Nhị Thập B&aacute;t T&uacute; l&agrave; hai mươi t&aacute;m tinh cầu đang quay quanh Thi&ecirc;n Địa cầu n&agrave;y, trấn giữ bởi hai mươi t&aacute;m gia tộc si&ecirc;u đẳng kh&aacute;c nhau, mỗi tinh cầu đại diện cho một trong hai mươi t&aacute;m nguy&ecirc;n tố chủ yếu của Vũ Trụ n&agrave;y.<br />\r\n<br />\r\nC&ograve;n trung t&acirc;m Thi&ecirc;n Địa Cầu c&oacute; thể nh&igrave;n thấy bất cứ nguy&ecirc;n tố n&agrave;o ở bất cứ đ&acirc;u.Ho&agrave;ng Quốc b&acirc;y giờ l&agrave; thiếu gia của gia tộc trấn thủ to&agrave;n bộ Thi&ecirc;n Địa cầu, dưới trướng c&oacute; mười gia tộc si&ecirc;u đẳng, trăm gia tộc nhất đẳng, ngh&igrave;n gia tộc trung đẳng, vạn gia tộc hạ đẳng v&agrave; h&agrave;ng ức gia đ&igrave;nh nhỏ.Gia tộc n&agrave;y thường gọi l&agrave; Huỳnh gia, một gia tộc c&oacute; quyền thế lớn nhất trong Vũ Trụ, hạng ch&iacute;n trong cửu đại gia tộc Long Trần Mai Ho&agrave;ng Văn V&otilde; Song To&agrave;n Huỳnh đại diện cho Long L&acirc;n Quy Phụng Thần Ma Ti&ecirc;n Quỷ b&aacute;t tộc v&agrave; trung t&acirc;m Vũ Trụ, Huỳnh gia.Đột nhi&ecirc;n trong đầu Quốc hiện ra một giọng n&oacute;i đều đều: - Ch&agrave;o mừng thiếu gia tới Thi&ecirc;n Địa cầu, hiện giờ cứ gọi t&ocirc;i l&agrave; Bằng Hữu, t&ocirc;i sẽ dẫn dắt v&agrave; giao nhiệm vụ cho thiếu gia để thống nhất Vũ Trụ n&agrave;y.Thứ nhất, thiếu gia cần biết r&otilde; m&igrave;nh l&agrave; ai, b&acirc;y giờ Bằng Hữu t&ocirc;i đ&acirc;y sẽ hiện ra th&ocirc;ng số của cậu:...Chủ nh&acirc;n: Ho&agrave;ng QuốcTư chất: Đế VươngThể chất: Long L&acirc;n Quy Phụng thể.Huyết mạch: Thần Ti&ecirc;n Ma Quỷ huyết.Chức nghiệp: Kh&ocirc;ng.Hồn cảnh: Kh&ocirc;ng.Ti&ecirc;n cảnh: Kh&ocirc;ng.Thần cảnh: Kh&ocirc;ng.Ma cảnh: Kh&ocirc;ng.Quỷ cảnh: Kh&ocirc;ng.Kh&iacute; cảnh: Kh&ocirc;ng.Huyết cảnh: Kh&ocirc;ng.Thể cảnh: Kh&ocirc;ng.Ngũ H&agrave;nh cảnh: Kh&ocirc;ng.Nguy&ecirc;n Tố cảnh: Kh&ocirc;ng.Linh cảnh: Kh&ocirc;ng.B&aacute;t Qu&aacute;i cảnh: Kh&ocirc;ng.Đạo cảnh: Kh&ocirc;ng....Ho&agrave;ng Quốc nh&igrave;n xem m&agrave; hết cả hồn, nh&igrave;n th&igrave; đơn giản m&agrave; con người trong Thi&ecirc;n Địa cầu c&oacute; thể tu tới cả đống cảnh giới như vậy, đ&atilde; thế l&agrave; tu luyện hơn 1 th&igrave; chậm biết bao nhi&ecirc;u, th&agrave; ở ch&ugrave;a ngồi tụng kinh c&ograve;n nhanh hơn tu luyện ở đ&acirc;y.Ho&agrave;ng Quốc nghĩ thầm trong đầu, tự hỏi xem Bằng Hữu c&oacute; nghe được n&oacute; n&oacute;i kh&ocirc;ng: - Bằng Hữu, ta c&oacute; cần phải tu hết tất cả loại cảnh giới đ&oacute; kh&ocirc;ng?Bắt ngờ, Bằng Hữu trả lời bằng một c&acirc;u m&agrave; n&oacute; kh&ocirc;ng tin được: - Cần, v&agrave; phải tu l&ecirc;n cấp cao nhất mới coi như đủ điều kiện đầu ti&ecirc;n để thống nhất Vũ Trụ.Ho&agrave;ng Quốc đang đứng ở trước cửa nh&agrave; Huỳnh gia th&igrave; một người phụ nữ nh&igrave;n thấy hắn v&agrave; la l&ecirc;n mừng rỡ: - Mọi người ơi tiểu th&aacute;i tử về rồi, tiểu th&aacute;i tử về rồi!Từ trong phủ, một loạt người chạy ra, &ocirc;m chầm n&oacute; v&agrave; n&oacute;i: - &Ocirc;i thiếu gia đi đ&acirc;u m&agrave; 12 năm nay biệt t&iacute;ch, l&agrave;m t&ocirc;i lo qu&aacute;! - Thiếu gia c&oacute; bị sao kh&ocirc;ng? C&oacute; bị bắt nạt kh&ocirc;ng?Sau đ&oacute; l&agrave; một loạt c&acirc;u hỏi nữa trước khi một người đ&agrave;n &ocirc;ng từ trong phủ chạy ra, nhận ra Ho&agrave;ng Quốc, &ocirc;ng la to: - Quốc Nhi, con về rồi!Trong giọng n&oacute;i của &ocirc;ng h&agrave;m chứa t&igrave;nh cảm của người cha d&agrave;nh cho con c&aacute;i sau khi trở về sau một chuyến đi d&agrave;i.Quốc hỏi Bằng Hữu: - Bằng Hữu, người đ&agrave;n &ocirc;ng đ&oacute; l&agrave; ai? - Cậu chủ, đ&oacute; l&agrave; cha cậu, cũng l&agrave; người đ&atilde; gắn t&ocirc;i v&agrave;o trong c&acirc;u l&uacute;c cậu mới sinh ra.<br />\r\n<br />\r\n<br />\r\n- Thật sao? &Ocirc;ng ấy c&oacute; ở đ&acirc;y, vậy mẹ t&ocirc;i đ&acirc;u? - Mẹ của cậu đang bị bệnh, tr&uacute;ng độc của Hắc Ảnh Ma X&agrave;, phải d&ugrave;ng một c&acirc;y Cao Thảo Kinh Ni&ecirc;n xay ra bột rồi đun nước uống mới khỏi, hiện giờ loại c&acirc;y ấy đang được trồng trong vườn của Huỳnh gia, rất dễ lấy th&ocirc;i.L&uacute;c n&agrave;y Quốc đang cố gắng tho&aacute;t khỏi đ&aacute;m người đang v&acirc;y quanh n&oacute; v&agrave; tiến về ph&iacute;a cha.Cha n&oacute; mặc ho&agrave;ng y, đội một c&aacute;i n&oacute;n th&ecirc;u Tứ Vương Long L&acirc;n Quy Phụng, dưới ch&acirc;n mang đ&ocirc;i h&agrave;i th&ecirc;u Tứ Đế Thần Ma Ti&ecirc;n Quỷ.<br />\r\n<br />\r\n&Ocirc;ng nh&igrave;n xanh xao, mặt hơi h&oacute;p v&agrave;o, tr&ocirc;ng c&oacute; vẻ như đ&atilde; mất ngủ nhiều th&aacute;ng.Cha n&oacute; tiếp đ&oacute;n nồng nhiệt: - Quốc Nhi, bao nhi&ecirc;u năm nay con ở đ&acirc;u? Sao kh&ocirc;ng quay về Huỳnh gia? - Con đi chơi ở gần đ&acirc;y, nhưng m&agrave; đi lạc, v&agrave;o một nh&agrave; d&acirc;n, nh&agrave; đ&oacute; đ&oacute;n tiếp con th&acirc;n t&igrave;nh, con tưởng l&agrave; về nh&agrave; rồi n&ecirc;n ở lại, từ đ&oacute; tới giờ lu&ocirc;n, một h&ocirc;m con nghe được một cuộc đối thoại của hai vợ chồng đ&oacute;, n&oacute;i rằng con l&agrave; nh&agrave; Huỳnh gia, giờ đem n&oacute; về nộp th&igrave; thể n&agrave;o cũng ẵm được cả đống tiền, con nghe m&agrave; sốc, cho n&ecirc;n con mới đi về đ&acirc;y.<br />\r\n<br />\r\n- Giời &ocirc;i, may l&agrave; con về sớm chứ kh&ocirc;ng gia tộc mất cả đống tiền rồi.<br />\r\n<br />\r\nB&acirc;y giờ đi v&agrave;o phủ nha!.</span></span></p>', 1, '2022-03-14 06:48:29', '2022-03-15 02:11:05'),
(3, 15, 'Chương 2: Huỳnh Gia Thủ Phủ', 'chuong-2-huynh-gia-thu-phu', '<p>Cha, người c&oacute; thể đưa con tham quan Huỳnh phủ được kh&ocirc;ng, xa nh&agrave; l&acirc;u qu&aacute; con cũng nhớ.- Thằng nh&oacute;c n&agrave;y</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Cha, người c&oacute; thể đưa con tham quan Huỳnh phủ được kh&ocirc;ng, xa nh&agrave; l&acirc;u qu&aacute; con cũng nhớ.- Thằng nh&oacute;c n&agrave;y, kh&ocirc;ng phải l&agrave; con tưởng đang ở nh&agrave; trong suốt 12 năm trước sao?N&oacute;i l&agrave; n&oacute;i vậy nhưng &ocirc;ng vẫn đưa Quốc đi.Huỳnh gia thủ phủ cực kỳ rộng lớn, kh&ocirc;ng biết bao nhi&ecirc;u l&agrave; đ&igrave;nh viện, v&otilde; điện, thư viện, c&ocirc;ng viện, thậm ch&iacute; c&oacute; cả một khu rừng linh dược hơn ng&agrave;n năm rộng kh&ocirc;ng c&oacute; điểm dừng.<br />\r\n<br />\r\nĐ&igrave;nh viện th&igrave; rộng lớn, chứa to&agrave;n gia bảo, v&otilde; điện c&oacute; tất cả trang thiết bị, bao c&aacute;t; thư viện đầy những kệ s&aacute;ch rộng ng&uacute;t ng&agrave;n v&agrave; d&agrave;i ng&uacute;t trời; c&ocirc;ng viện th&igrave; đầy những người qua lại tấp nập; đủ thấy sự gi&agrave;u c&oacute; của Huỳnh gia.<br />\r\n<br />\r\nTrước cửa một t&ograve;a đ&igrave;nh viện gọi l&agrave; Huỳnh Th&aacute;i Tử Phủ, cha Quốc đưa cho n&oacute; một tờ giấy, n&oacute;i: - Đ&acirc;y l&agrave; bản đồ Huỳnh gia thủ phủ, khi n&agrave;o cần con h&atilde;y mở ra xem.Hấp tấp mở qu&ecirc;n cả cảm ơn người cha đ&oacute;, thằng Quốc vội đọc ngay.Ph&iacute;a ch&iacute;nh Đ&ocirc;ng l&agrave; 3 cung điện, ở giữa lớn nhất, gọi l&agrave; Thần Ma Cung, hai b&ecirc;n l&agrave; Thần Vũ Cung v&agrave; Ma Vũ Cung; ph&iacute;a ch&iacute;nh T&acirc;y l&agrave; Ti&ecirc;n Quỷ Quan, hai b&ecirc;n l&agrave; Ti&ecirc;n M&ocirc;n Quan v&agrave; Quỷ M&ocirc;n Quan; ch&iacute;nh Bắc l&agrave; Bắc Nam Điện, hai b&ecirc;n l&agrave; Huyền Vũ Điện v&agrave; Chu Tước Điện; ch&iacute;nh Nam l&agrave; Đ&ocirc;ng T&acirc;y Cung, hai b&ecirc;n l&agrave; Thanh Long Điện v&agrave; Kỳ L&acirc;n Điện.<br />\r\n<br />\r\nChỗ n&oacute; đang đứng l&agrave; Huỳnh Th&aacute;i Tử Phủ.<br />\r\n<br />\r\nTrung t&acirc;m của Huỳnh gia thủ phủ l&agrave; tập hợp 28 đại gia tộc nắm quyền h&agrave;nh tr&ecirc;n Nhị Thập B&aacute;t T&uacute; quanh Thi&ecirc;n Địa cầu.<br />\r\n<br />\r\nỞ giữa Huỳnh Th&aacute;i Tử Phủ l&agrave; một vườn c&acirc;y, một thư viện, một t&ograve;a đ&igrave;nh viện, một c&aacute;i kho lớn, v&agrave; một khu đất trống, d&ugrave;ng để cho th&aacute;i tử dựng nh&agrave;.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, tiếng n&oacute;i đều đều của Bằng Hữu ph&aacute;t ra trong đầu: - B&iacute;p! B&acirc;y giờ Bằng Hữu t&ocirc;i mở của B&aacute; Vương thương h&agrave;nh, liệu cậu chủ muốn mở? - Được th&ocirc;i.Hai tiếng cạch, tiếng n&oacute;i của Bằng Hữu vang l&ecirc;n: - Đ&atilde; mở, mới cậu chủ v&agrave;o mua sắm, mỗi lần v&agrave;o cậu sẽ c&oacute; 100 điểm thưởng.Khi Quốc nh&igrave;n v&agrave;o B&aacute; Vương thương h&agrave;nh, n&oacute; nh&igrave;n thấy điểm t&iacute;ch lũy của n&oacute; tới tận năm mươi ức 100 điểm, n&oacute; mừng thầm.<br />\r\n<br />\r\n<br />\r\nDạo quanh B&aacute; Vương thương h&agrave;nh, n&oacute; t&igrave;m một ng&ocirc;i nh&agrave; vừa đủ để n&oacute; ti&ecirc;u hết số tiền n&agrave;y trong một khắc.<br />\r\n<br />\r\nĐ&atilde; l&agrave; B&aacute; Vương th&igrave; phải chơi sang!Nghĩ được 2 gi&acirc;y, n&oacute; thẫn thờ.<br />\r\n<br />\r\nTrong khu mua nh&agrave;, n&oacute; thấy một đống những ng&ocirc;i nh&agrave; to v&agrave; đẹp, nội thất sang trọng, v&agrave; c&ograve;n kh&aacute; rẻ.<br />\r\n<br />\r\nCuối c&ugrave;ng, n&oacute; mua một căn B&aacute;t Qu&aacute;i Cung, gi&aacute; 2 tỉ điểm; Ngũ H&agrave;nh Cung 2 căn, tổng cộng 1 tỉ điểm; Nguy&ecirc;n Tố Cung 1 căn, gi&aacute; 1 tỉ điểm; Thập Nghệ Cung 1 căn, năm ức điểm; v&agrave; một t&ograve;a B&aacute; Vương Cung, gi&aacute; bốn ức điểm t&iacute;ch lũy.<br />\r\n<br />\r\nC&ograve;n 100 triệu, về tấn c&ocirc;ng, n&oacute; mua một c&acirc;y Ngạo Thế Th&aacute;nh Thương, 50 ngh&igrave;n; Nhị Thập B&aacute;t Nguy&ecirc;n Tố kiếm, 40 ngh&igrave;n; ; về ph&ograve;ng thủ, n&oacute; mua Hỗn Nguy&ecirc;n Gi&aacute;p gi&aacute; 20 ngh&igrave;n; một đ&ocirc;i găng Binh Ph&ograve;ng gi&aacute; 10 ngh&igrave;n; về tu luyện, n&oacute; mua 1 bộ c&ocirc;ng ph&aacute;p tu Kiếm Đạo, Ma Đạo, Thần Đạo, Quỷ Đạo, Ti&ecirc;n Đạo t&ecirc;n Ngũ Đạo Kinh gi&aacute; 30 ngh&igrave;n; một bộ c&ocirc;ng ph&aacute;p luyện thể cảnh t&ecirc;n Thể Đạo Kinh gi&aacute; 40 ngh&igrave;n; v&agrave; một bộ c&ocirc;ng ph&aacute;p tu Thập Nhị cảnh giới, t&ecirc;n l&agrave; Tu Cảnh Kinh, gi&aacute; 10 ngh&igrave;n.<br />\r\n<br />\r\nSau một hồi mua sắm, n&oacute; c&ograve;n lại 99 triệu v&agrave; 800 ngh&igrave;n điểm, mua th&ecirc;m một v&agrave;i cuốn s&aacute;ch nữa chẳng sao, n&oacute; mua tiếp một cuốn s&aacute;ch t&ecirc;n l&agrave; Thi&ecirc;n Thư ghi ch&eacute;p về mọi thứ trong Vũ Trụ; Địa Thư về lịch sử v&agrave; địa l&yacute; của Vũ Trụ; B&aacute;t Qu&aacute;i Thư, Nguy&ecirc;n Tố Thư, Thế Chất Thư, Huyết Mạch Thư, Linh Hồn Thư, Thần Ti&ecirc;n Thư, Ma Quỷ Thư, Ngũ H&agrave;nh Thư v&agrave; Luyện Kh&iacute; Thư, ghi ch&eacute;p về c&aacute;c cấp bậc, độ mạnh của c&aacute;c cảnh giới đ&oacute;, tất cả gi&aacute; 800 ngh&igrave;n.<br />\r\n<br />\r\nVậy l&agrave; c&ograve;n tổng cộng 99 triệu 100 điểm t&iacute;ch lũy.B&acirc;y giờ v&agrave;o trong khu đất trống, n&oacute; đặt t&ograve;a B&aacute; Vương Cung ở ch&iacute;nh giữa, hai t&ograve;a B&aacute;t Qu&aacute;i v&agrave; Thập Nghệ Cung ở hai b&ecirc;n, t&ograve;a Nguy&ecirc;n Tố Cung ph&iacute;a trước v&agrave; Ngũ H&agrave;nh Cung hai căn bọc hậu.Sắp xếp đ&acirc;u ra đấy, n&oacute; đi v&agrave;o trong B&aacute; Vương Cung.<br />\r\n<br />\r\nVừa v&agrave;o Quốc đ&atilde; bị l&oacute;a mắt bới những thứ b&ecirc;n trong.<br />\r\n<br />\r\nTường bằng v&agrave;ng, cột v&agrave;ng, hai b&ecirc;n kệ chất đầy đ&aacute; qu&yacute; c&aacute;c loại, ở giữa tiền sảnh c&ograve;n c&oacute; một bức tượng bằng đ&aacute; c&oacute; h&igrave;nh một con người được bao quanh bới Quỷ chủ, Thần chủ, Ma chủ v&agrave; Ti&ecirc;n chủ c&ugrave;ng với Thanh Long, Chu Tước, Kỳ L&acirc;n v&agrave; Huyền Vũ, tất cả nh&igrave;n l&ecirc;n người đ&oacute; bằng &aacute;nh mắt t&ocirc;n s&ugrave;ng.<br />\r\n<br />\r\nĐi qua một b&ecirc;n n&oacute; thấy ph&ograve;ng ngủ, ph&ograve;ng kh&aacute;ch, ph&ograve;ng tắm, ph&ograve;ng đọc s&aacute;ch, ph&ograve;ng thư gi&atilde;n, v&otilde; đường, v&agrave; kho; tất cả đều được d&aacute;t v&agrave;ng.<br />\r\n<br />\r\nĐ&uacute;ng l&agrave; B&aacute; Vương Cung!B&acirc;y giờ đi qua Thập Nghệ Cung, n&oacute; nh&igrave;n thấy tiền sảnh c&oacute; vẻ như l&uacute;c trước, ngoại trừ một điểm l&agrave; d&aacute;t bằng bạc v&agrave; kh&ocirc;ng c&oacute; tủ k&iacute;nh đầy đ&aacute; qu&yacute; như B&aacute; Vương Cung.<br />\r\n<br />\r\nThập Nghệ Cung được chia l&agrave;m mười đường đi theo mười nghệ thuật của những người tu Thần cảnh l&agrave; Đan Tr&ugrave; Ph&ugrave; Trận Thư Kinh Kh&iacute; Nguy&ecirc;n H&agrave;nh v&agrave; Chi&ecirc;m Tinh, mười con đường n&agrave;y th&ocirc;ng giữa Thập Nghệ Cung v&agrave; Thập Nghệ C&aacute;c, ch&iacute;nh l&agrave; Thập Nghệ đ&atilde; n&oacute;i tr&ecirc;n.Nguy&ecirc;n Tố Cung th&igrave; chia l&agrave;m 28 ngả, tường d&aacute;t hồng ngọc, thảm đỏ dưới ch&acirc;n.<br />\r\n<br />\r\n<br />\r\nHai mươi t&aacute;m nguy&ecirc;n tố n&agrave;y ứng với 28 gia tộc nhất đẳng trấn giữ Nhị Thập B&aacute;t T&uacute;, như thế n&agrave;o th&igrave; em xin n&oacute;i ở phần sau.Ngũ H&agrave;nh Cung được bao bọc bởi những khu vườn, đầy c&acirc;y rậm rạp th&igrave; gọi l&agrave; Mộc Vi&ecirc;n, c&acirc;y bốc ch&aacute;y khắp nơi gọi l&agrave; Hỏa Vi&ecirc;n, bởi những m&ocirc; đất nhỏ th&igrave; gọi l&agrave; Thổ Vi&ecirc;n, những mỏ quặng lộ thi&ecirc;n th&igrave; gọi l&agrave; Kim Vi&ecirc;n, v&agrave; những con suối bao bọc th&igrave; gọi l&agrave; Thủy Vi&ecirc;n.<br />\r\n<br />\r\nTrong Ngũ H&agrave;nh Cung, tiền sảnh c&oacute; tường d&aacute;t T&iacute;a ngọc, chia năm đường, ứng với Ngũ H&agrave;nh Mộc Hỏa Thổ Kim Thủy.Ngo&agrave;i ra, B&aacute; Vương Cung c&ograve;n c&oacute; một nơi để vũ kh&iacute;, Quốc đ&agrave;nh bỏ những thứ m&agrave; n&oacute; vừa mua ở B&aacute; Vương thương h&agrave;nh v&agrave;o, rồi v&agrave;o thư viện t&igrave;m chỗ để s&aacute;ch.<br />\r\n<br />\r\nNhưng những kệ s&aacute;ch n&agrave;y c&oacute; vẻ như đ&atilde; đầy kh&ocirc;ng du ra một hạt bụi n&agrave;o n&ecirc;n n&oacute; đ&agrave;nh hỏi Bằng Hữu xem c&oacute; nơi n&agrave;o trong B&aacute; Vương Cung hay trong Huỳnh Th&aacute;i Tử Phủ c&oacute; dư chỗ đế s&aacute;ch kh&ocirc;ng, v&agrave; c&acirc;u trả lời l&agrave;: - C&oacute; đấy, nh&agrave; kho.<br />\r\n<br />\r\n- Đệt mẹ m&agrave;y, s&aacute;ch qu&yacute; để nh&agrave; kho! - Đ&ugrave;a đấy! Dưới B&aacute; Vương Cung c&oacute; một c&aacute;i hầm, chỉ cần t&igrave;m một c&aacute;i n&uacute;t m&agrave;u đen dưới gầm giường trong ph&ograve;ng ngủ của cậu chủ l&agrave; được th&ocirc;i.Cuối c&ugrave;ng n&oacute; cũng v&agrave;o được trong hầm.Cho&aacute;ng ngợp!Đ&oacute; l&agrave; cảm x&uacute;c của n&oacute; khi nh&igrave;n thấy căn hầm, tường v&agrave; trần khảm đầy đ&aacute; qu&yacute;, kệ s&aacute;ch c&aacute;c loại th&igrave; d&agrave;i hun h&uacute;t, c&ograve;n c&oacute; một c&aacute;i rương trữ đồ, một c&aacute;i giường v&agrave; một c&aacute;i bếp tự động, chỉ cần ra lệnh cho n&oacute; nấu c&aacute;i g&igrave; l&agrave; c&oacute; ngay lập tức.Quốc thầm nghĩ: - Sau n&agrave;y lỡ nổi tiếng qu&aacute; th&igrave; trốn v&ocirc; hầm n&agrave;y, đỡ ch&aacute;n!...H&ocirc;m sau - T&iacute;ch t&iacute;ch t&iacute;ch t&iacute;ch t&iacute;ch t&iacute;ch ...!- Đệt mẹ m&agrave;y im coi Bằng Hữu, tao đang ngủ m&agrave;.<br />\r\n<br />\r\n- Cậu đang n&oacute;i chuyện với t&ocirc;i đấy th&ocirc;i, đang ngủ g&igrave; nữa.<br />\r\n<br />\r\n- Tại c&aacute;i đồng hồ của m&agrave;y đấy chứ.<br />\r\n<br />\r\n- &Agrave;i.<br />\r\n<br />\r\nH&ocirc;m nay l&agrave; ng&agrave;y Bằng Hữu đưa hệ thống v&agrave;o hoạt động đấy, cố gắng m&agrave; thức sớm kh&ocirc;ng th&igrave; ăn đồng hồ.<br />\r\n<br />\r\n- Đệt.H&ocirc;m nay l&agrave; ng&agrave;y đầu ti&ecirc;n của Quốc ở Huỳnh gia thủ phủ, n&oacute; cần đi kh&aacute;m ph&aacute; một phen.<br />\r\n<br />\r\n<br />\r\nĐang nghĩ tới đ&acirc;y th&igrave;...!- Quốc Nhi, con ra Ngọc Điện ở Ch&iacute;nh Cung lấy cho cha cuốn s&aacute;ch.<br />\r\n<br />\r\n- Cha ơi con c&ograve;n chưa biết đường tới Ch&iacute;nh Cung lấy đ&acirc;u ra cuốn s&aacute;ch cho cha đọc??? - &Agrave; qu&ecirc;n, con lại đ&acirc;y.<br />\r\n<br />\r\n- Dạ con tới liền.N&oacute; đi thẳng tới chỗ cha, &ocirc;ng n&oacute;i: - Đưa tấm bản đồ đ&acirc;y, bản đồ Huỳnh gia thủ phủ ấy.<br />\r\n<br />\r\n- Đ&acirc;y n&egrave; cha.<br />\r\n<br />\r\n- Đ&acirc;y, tấm bản đồ n&agrave;y c&oacute; nhiều chức năng, hữu dụng hạng nhất nh&igrave; chắc phải n&oacute;i đến chức năng t&igrave;m đường v&agrave; vẽ đường.<br />\r\n<br />\r\nCon chỉ cần n&oacute;i nơi con muốn đến, miễn sao nằm trong thủ phủ l&agrave; được, n&oacute; sẽ tự động vẽ đường đến thẳng nơi đ&oacute; cho con..</span></span><br />\r\n&nbsp;</p>', 1, '2022-03-15 07:12:28', '2022-03-15 07:12:28'),
(4, 15, 'Chương 3: Nhị Thập Bát Tú - 28 Gia Tộc Siêu Đẳng  Và Tinh Cầu Thứ 30 - Truyền Thuyết Không Tên', 'chuong-3-nhi-thap-bat-tu-28-gia-toc-sieu-dang-va-tinh-cau-thu-30-truyen-thuyet-khong-ten', '<p>afafaf</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Cha ơi cho con hỏi l&agrave; tại sao bao quanh Ch&iacute;nh Cung lại được bao quanh bởi khu vực Nhị Thập B&aacute;t T&uacute; vậy cha? - &Agrave;, cha sẽ n&oacute;i sau, b&acirc;y giờ lấy s&aacute;ch cho cha trước đi.<br />\r\n<br />\r\n- Ạch.N&oacute; đi từ đ&oacute; đến Ch&iacute;nh Cung, tới Nhị Thập B&aacute;t T&uacute;, n&oacute; thấy c&aacute;c biển hiệu như l&agrave; L&ecirc; gia, Nguyễn gia, L&yacute; gia...!tr&ecirc;n c&aacute;c đ&igrave;nh viện gần đ&oacute;, n&oacute; đo&aacute;n l&agrave; Nhị Thập B&aacute;t T&uacute; l&agrave; 28 tinh cầu xung quanh Thi&ecirc;n Địa cầu v&agrave; L&ecirc; gia L&yacute; Gia Nguyễn gia g&igrave; g&igrave; đ&oacute; l&agrave; những gia tộc trấn thủ 28 tinh cầu đ&oacute;.Về đến nơi, cha n&oacute; n&oacute;i: - N&agrave;o, Nhị Thập B&aacute;t T&uacute;, đ&acirc;y, con đọc đi.N&oacute; nhận quyển s&aacute;ch từ tay cha, đọc sơ qua v&agrave; hiểu ngay.C&oacute; tổng cộng 28 tinh cầu xoay quanh Thi&ecirc;n Địa cầu, về điểm n&agrave;y n&oacute; đ&uacute;ng, nhưng những gia tộc n&oacute;i tr&ecirc;n kh&ocirc;ng trấn thủ m&agrave; cai trị những tinh cầu đ&oacute;, danh s&aacute;ch những tinh cầu c&ugrave;ng với gia tộc tương ứng l&agrave;:L&yacute; gia trấn Liệt Hỏa cầuTrần gia trấn Hồng Thủy cầuL&ecirc; gia trấn Cuồng Kh&iacute; cầuNguyễn gia trấn Thổ Chấn cầu.Hạ gia giữ Thảo Mộc cầuTrung gia giữ Thiết Kim cầuThượng gia giữ Cơ Năng cầuSi&ecirc;u gia giữ Hư Kh&ocirc;ng cầu.Luận gia tr&igrave; Nhật Quang cầuB&agrave;n gia tr&igrave; Nguyệt &Aacute;m cầuTh&agrave;nh gia tr&igrave; Mệnh Sinh cầuB&agrave;nh gia tr&igrave; Vong Tử cầu.Dương gia trị Thời Gian cầuTần gia trị Kh&ocirc;ng Gian cầuTề gia trị Dược Thảo cầuH&aacute;n gia trị Cầm Th&uacute; cầu.Triệu gia cai Bạo L&ocirc;i cầuĐường gia cai Sắc Phong cầuChu gia cai Kiến Thức cầuKim gia cai Sức Mạnh cầu.Trịnh gia tr&igrave; Thi&ecirc;n Diễn cầuPhạm gia tr&igrave; Hỗn Độn cầuNg&ocirc; gia tr&igrave; Đại Đạo cầuĐ&agrave;o gia tr&igrave; Hồng M&ocirc;ng cầu.&Acirc;n gia giữ B&aacute;t Qu&aacute;i cầuH&agrave;n gia giữ Ngũ H&agrave;nh cầuLương gia giữ Nguy&ecirc;n Tố cầuTr&igrave;nh gia giữ Đạo Nguy&ecirc;n cầu.Đọc tới đ&acirc;y, Quốc to&agrave;n th&acirc;n run l&ecirc;n, h&oacute;a ra c&oacute; cả đống gia tộc h&ugrave;ng mạnh trấn giữ 28 h&agrave;nh tinh lớn kh&ocirc;ng thua g&igrave; Thi&ecirc;n Địa cầu, xem ra sứ mạng thống nhất Vũ Trụ của n&oacute; kh&oacute; th&agrave;nh c&ocirc;ng.<br />\r\n<br />\r\n- Bất ngờ kh&ocirc;ng?Tiếng k&ecirc;u của cha n&oacute; đưa n&oacute; về thực tại: - Dạ c&oacute;.<br />\r\n<br />\r\n- H&agrave; h&agrave;, cha c&ograve;n biết một chuyện kh&aacute;c c&ograve;n l&agrave;m con bất ngờ hơn đấy.<br />\r\n<br />\r\n- L&agrave; g&igrave; thế cha? - Thi&ecirc;n Địa cầu v&agrave; Nhị Thập B&aacute;t T&uacute; cộng lại l&agrave; 29 tinh cầu, 29 tinh cầu ở giữa Vũ Trụ, tuy nhi&ecirc;n c&ograve;n c&oacute; một truyền thuyết truyền đời qua miệng của gia tộc ta - Tinh Cầu thứ 30! - Tinh cầu thứ 30? Cha n&oacute;i r&otilde; hơn được kh&ocirc;ng? - Tương truyền rằng c&oacute; một người ở Huỳnh gia đạt tới Thượng Thừa kỳ, B&aacute;t Qu&aacute;i cảnh đỉnh phong đ&atilde; thử d&ugrave;ng sức b&igrave;nh sinh kết hợp Nhị Thập B&aacute;t T&uacute;, người đ&atilde; th&agrave;nh c&ocirc;ng v&agrave; đặt t&ecirc;n n&oacute; l&agrave; Nguy&ecirc;n Tố cầu, sau đ&oacute; &ocirc;ng định thử kết hợp Nguy&ecirc;n Tố cầu với Thi&ecirc;n Địa cầu...!- Vậy &ocirc;ng ấy c&oacute; th&agrave;nh c&ocirc;ng kh&ocirc;ng? - Kh&ocirc;ng, v&agrave; &ocirc;ng ấy thậm ch&iacute; c&ograve;n mất mạng, sau đ&oacute; Nguy&ecirc;n Tố cầu v&igrave; đang kết hợp m&agrave; kh&ocirc;ng th&agrave;nh n&ecirc;n cũng trở lại như cũ.<br />\r\n<br />\r\n- Vậy nếu &ocirc;ng ấy th&agrave;nh c&ocirc;ng &ocirc;ng ấy sẽ đặt t&ecirc;n cho tinh cầu đ&oacute; l&agrave; g&igrave;? - Cha kh&ocirc;ng biết, nhưng trước khi chết &ocirc;ng ấy c&oacute; n&oacute;i c&acirc;u g&igrave; đ&oacute; như l&agrave; &quot;Ta vẫn sẽ sống để ho&agrave;n th&agrave;nh Thi&ecirc;n Địa cầu&quot;.<br />\r\n<br />\r\nKh&ocirc;ng biết n&oacute; c&oacute; &yacute; nghĩa g&igrave; nhưng cha d&aacute;m &ocirc;ng định đặt t&ecirc;n cho n&oacute; l&agrave; Thi&ecirc;n Địa cầu đấy.<br />\r\n<br />\r\n<br />\r\n- Hả? Vậy chứ l&uacute;c đ&oacute; Thi&ecirc;n Địa cầu c&oacute; t&ecirc;n kh&aacute;c sao? - Ừ, sau vụ đ&oacute; người d&acirc;n Huỳnh gia mới gọi tinh cầu n&agrave;y l&agrave; Thi&ecirc;n Địa cầu, chắc để tưởng nhớ vị kia.L&uacute;c n&agrave;y, tiếng của Bằng Hữu dội v&agrave;o đầu Quốc: - B&iacute;p! Cậu chủ c&oacute; nhiệm vụ.<br />\r\n<br />\r\nNhiệm vụ &quot;Tu Thập Nhị đạo&quot;, y&ecirc;u cầu cậu tu tới cảnh giới thấp nhất của Thập Nhị đạo, phần thưởng l&agrave; G&oacute;i qu&agrave; bắt đầu, 1 t&ograve;a Linh bảo Linh Thần Cung, 1 bộ s&aacute;ch &quot;Ch&acirc;n Thư&quot; v&agrave; 30000 điểm t&iacute;ch lũy.<br />\r\n<br />\r\nPhải chăng cậu muốn tiếp nhận? - Tiếp nhận....L&uacute;c n&agrave;y c&oacute; một c&ocirc; b&eacute; cỡ 12 tuổi đi ngang qua Quốc, nh&igrave;n thấy cậu, c&ocirc; b&eacute; đỏ mặt v&agrave; quay đi.<br />\r\n<br />\r\nQuốc c&ograve;n chưa hiểu m&ocirc; t&ecirc; g&igrave; đ&atilde; ăn một chưởng v&agrave;o mặt.<br />\r\n<br />\r\nMột l&atilde;o b&agrave; n&oacute;i: - Chưa g&igrave; m&agrave; đ&atilde; t&aacute;n đệ tử ta &agrave;? Muốn chết sao?Cha n&oacute; liền n&oacute;i giải v&acirc;y: - L&agrave; Kiều c&ocirc; nương đ&acirc;y nh&igrave;n thấy tiểu th&aacute;i tử rồi quay mặt đi chứ con t&ocirc;i đ&atilde; l&agrave;m g&igrave; c&ocirc; nương đ&acirc;u? - Hừ! Lần sau th&igrave; tiểu th&aacute;i tử n&agrave;y...!Hả? Tiểu th&aacute;i tử? &Agrave; nếu vậy th&igrave; kh&ocirc;ng sao, ta cũng tha lỗi cho.<br />\r\n<br />\r\n- Ạch!C&acirc;u n&agrave;y l&agrave; do cả hai cha con Quốc c&ugrave;ng cảm th&aacute;n b&agrave;....Tuần tiếp theo - B&iacute;p! Ch&uacute;c mừng cậu chủ ho&agrave;n th&agrave;nh nhiệm vụ &quot;Tu Thập Nhị đạo&quot;, nhận được phần thưởng l&agrave; một g&oacute;i qu&agrave; bắt đầu, 1 t&ograve;a Linh Thần Cung, 1 bộ s&aacute;ch &quot;Ch&acirc;n Thư v&agrave; 30000 điểm t&iacute;ch lũy.<br />\r\n<br />\r\n- B&iacute;p! Hiện giờ cậu chủ c&oacute; một bộ s&aacute;ch &quot;Ch&acirc;n Thư&quot;, liệu cậu chủ muốn lĩnh ngộ trong đầu hay muốn in ra th&agrave;nh s&aacute;ch đọc? - Lĩnh ngộ trong đầu.<br />\r\n<br />\r\n- B&iacute;p! Đ&atilde; lĩnh ngộ xong.<br />\r\n<br />\r\n- B&iacute;p! Cậu chủ c&oacute; một G&oacute;i qu&agrave; bắt đầu, phải chăng muốn nhận? - Nhận.<br />\r\n<br />\r\n- Đang mở.<br />\r\n<br />\r\n- B&iacute;p! Đ&atilde; mở xong, G&oacute;i qu&agrave; bắt đầu của cậu chủ gồm Dẫn Hỏa Thuật, Dẫn Thủy Thuật, G&oacute;i đan bắt đầu, bộ dụng cụ Chi&ecirc;m Tinh Đế cấp, bộ dụng cụ Thập Nghệ Vương cấp, Gi&aacute;m Định Thuật, Nguy&ecirc;n Tố Thuật, Ngũ H&agrave;nh Thuật, B&aacute;t Qu&aacute;i Thuật, Thi&ecirc;n Diễn Kinh, Hồng M&ocirc;ng Kinh, Tinh Huyết ph&ugrave; văn, Hồn ph&ugrave; văn, Hỗn Độn Kinh, Hỗn Độn ph&ugrave; văn, Hồng M&ocirc;ng ph&ugrave; văn, Thi&ecirc;n Diễn ph&ugrave; văn, Đại Đạo ph&ugrave; văn, Đạo Nguy&ecirc;n ph&ugrave; văn, B&aacute;t Qu&aacute;i ph&ugrave; văn, bộ s&aacute;ch &quot;Vật&quot;, bộ s&aacute;ch &quot;Nghệ&quot;, Tứ Kh&iacute; Quyết, Đạo Nguy&ecirc;n Kinh, v&agrave; kho lịch sử của Nhị Thập B&aacute;t T&uacute;.<br />\r\n<br />\r\n<br />\r\n- Đ&ugrave;, nhiều v&atilde;i! - B&iacute;p! B&acirc;y giờ cậu chủ c&oacute; G&oacute;i đan bắt đầu, phải chăng muốn nhận? - Nhận.<br />\r\n<br />\r\n- B&iacute;p! Đ&atilde; mở xong, G&oacute;i đan bắt đầu của cậu gồm 50 vi&ecirc;n Thăng Thần Đan, 100 vi&ecirc;n Thăng Đạo Đan, 1000 vi&ecirc;n Thăng Nguy&ecirc;n Tố Đan, 10000 vi&ecirc;n Thăng B&aacute;t Qu&aacute;i Đan v&agrave; 40000 vi&ecirc;n Thăng Ngũ H&agrave;nh Đan.<br />\r\n<br />\r\n- B&iacute;p! Hiện giờ cậu c&oacute; Thi&ecirc;n Diễn Kinh, Hồng M&ocirc;ng Kinh, Hỗn Độn Kinh, Đạo Nguy&ecirc;n Kinh, phải chăng muốn lĩnh ngộ hay in s&aacute;ch? - Lĩnh ngộ.<br />\r\n<br />\r\n- B&iacute;p! Hiện giờ cậu c&oacute; Tinh Huyết ph&ugrave; văn, Hồn ph&ugrave; văn, Hỗn Độn ph&ugrave; văn, Hồng M&ocirc;ng ph&ugrave; văn, Thi&ecirc;n Diễn ph&ugrave; văn, Đại Đạo ph&ugrave; văn, Đạo Nguy&ecirc;n ph&ugrave; văn, B&aacute;t Qu&aacute;i ph&ugrave; văn, phải chăng muốn dung nạp v&agrave;o cơ thể? - Dung nạp.<br />\r\n<br />\r\n- B&iacute;p! Hi&ecirc;n giờ cậu c&oacute; bộ dụng cụ Thập Nghệ Vương cấp, bộ dụng cụ Chi&ecirc;m Tinh Đế cấp, phải chăng muốn cất v&agrave;o nơi đ&acirc;u? - V&agrave;o Thập Nghệ C&aacute;c đi.<br />\r\n<br />\r\n- B&iacute;p! B&acirc;y giờ cậu chủ c&oacute; 1 t&ograve;a Linh Thần Cung, liệu muốn đem cất kho hay x&agrave;i ngay b&acirc;y giờ? - Đặt ở b&ecirc;n cạnh t&ograve;a Nguy&ecirc;n Tố Cung đi.<br />\r\n<br />\r\n- Đang đặt...B&ugrave;m! Một tiếng lớn kinh thi&ecirc;n động địa ở ph&iacute;a trước ph&ograve;ng ngủ của n&oacute;, Quốc hiểu l&agrave; t&ograve;a Linh Thần Cung đ&atilde; được đặt.<br />\r\n<br />\r\nN&oacute; đi ra ngo&agrave;i th&igrave; thấy cho&aacute;ng ngợp, t&ograve;a Linh Thần Cung n&agrave;y c&oacute; rất nhiều t&ograve;a nh&agrave;, y như l&agrave; Huỳnh gia thủ phủ thu nhỏ vậy, hai b&ecirc;n đường đi th&igrave; trồng tre xanh l&agrave;m b&oacute;ng m&aacute;t, những ai đang cơn n&oacute;ng giận m&agrave; đi v&agrave;o trong đ&acirc;y cũng phải vui vẻ l&agrave;m l&agrave;nh với mọi người.V&agrave;o trong ch&iacute;nh điện, Quốc mới biết đ&acirc;y mới l&agrave; đỉnh cao của gi&agrave;u.<br />\r\n<br />\r\nS&agrave;n l&aacute;t bằng kim cương, khảm v&acirc;n ngọc v&agrave; ho&agrave;ng ngọc hai b&ecirc;n viền; tường khảm lam ngọc, m&agrave;u hồng ngọc; trần mang m&agrave;u giống như ngọc lục bảo, khảm t&iacute;a ngọc rải r&aacute;c; cửa v&agrave;o th&igrave; l&agrave;m từ một trong những loại gỗ đắt tiền nhất Thi&ecirc;n Địa cầu, gọi l&agrave; Cao Ni&ecirc;n Mộc; những loại đ&aacute; qu&yacute; n&oacute;i tr&ecirc;n nếu ở Tr&aacute;i Đất m&agrave; biết c&aacute;ch sử dụng th&igrave; c&oacute; thể chữa bệnh n&agrave;o cũng được v&agrave; c&oacute; thể trở th&agrave;nh vũ kh&iacute; giết người bằng c&aacute;ch l&agrave;m ph&acirc;n r&atilde; nguy&ecirc;n tử người.<br />\r\n<br />\r\n<br />\r\n- B&iacute;p! Ch&uacute;c mừng cậu chủ đ&atilde; mở kh&oacute;a Uy Danh thương h&agrave;nh, tiền tệ l&agrave; điểm Uy Danh, l&agrave;m người kh&aacute;c sợ +2, k&iacute;nh phục +10, bất tu&acirc;n +3, khinh thường +20.<br />\r\n<br />\r\n- B&iacute;p! Ch&uacute;c mừng cậu chủ mở kh&oacute;a Đế Vương thương h&agrave;nh, tiền tệ l&agrave; Đế Vương điểm.<br />\r\n<br />\r\n- B&iacute;p! B&aacute; Vương thương h&agrave;nh vừa năng cấp, c&oacute; th&ecirc;m t&iacute;nh năng mới &quot;C&oacute; Qua C&oacute; Lại&quot;, cậu chủ cũng c&oacute; thể b&aacute;n đồ cho B&aacute; Vương thương h&agrave;nh nhận điểm t&iacute;ch lũy.<br />\r\n<br />\r\n- B&iacute;p! Ch&uacute;c mừng cậu chủ đạt được danh hiệu &quot;Tiểu Thương Bu&ocirc;n, Tam Thương H&agrave;nh&quot;, nhận được 30000 điểm t&iacute;ch lũy v&agrave; 50000 Đế Vương điểm.<br />\r\n<br />\r\n- B&iacute;p! Ph&aacute;t động nhiệm vụ &quot;Tu l&ecirc;n tầng 2&quot;, y&ecirc;u cầu trong 1 năm phải tu l&ecirc;n 1 tiểu cảnh giới của Thập Nhị đạo, phần thưởng l&agrave; G&oacute;i qu&agrave; Sơ đẳng, bộ c&ocirc;ng ph&aacute;p Thi&ecirc;n Địa tu, một bộ s&aacute;ch &quot;Ma Thư&quot;, một bộ s&aacute;ch &quot;Thần Thư&quot;, một bộ s&aacute;ch &quot;Ti&ecirc;n Thư&quot;, một bộ s&aacute;ch &quot;Quỷ Thư&quot;, v&agrave; 30000000 điểm t&iacute;ch lũy.<br />\r\n<br />\r\nPhải chăng muốn tiếp nhận? - Ngươi n&oacute;i đủ chưa, tiếp nhận!.</span></span></p>', 1, '2022-03-15 07:14:03', '2022-03-15 07:14:03');
INSERT INTO `chapters` (`id`, `book_id`, `name`, `slug`, `description`, `content`, `active`, `created_at`, `updated_at`) VALUES
(6, 15, 'Chương 4: Linh Thần Cung', 'chuong-4-linh-than-cung', '<p>fsgsgsg</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">...9 th&aacute;ng sau tại Linh Thần CungTrong ph&ograve;ng kh&aacute;ch - B&iacute;p! Ch&uacute;c mừng cậu chủ ho&agrave;n th&agrave;nh nhiệm vụ &quot;Tu l&ecirc;n tầng 2&quot;, phần thưởng l&agrave; G&oacute;i qu&agrave; Sơ đẳng, bộ c&ocirc;ng ph&aacute;p Thi&ecirc;n Địa tu, một bộ s&aacute;ch &quot;Ma Thư&quot;, một bộ s&aacute;ch &quot;Thần Thư&quot;, một bộ s&aacute;ch &quot;Ti&ecirc;n Thư&quot;, một bộ s&aacute;ch &quot;Quỷ Thư&quot;, v&agrave; 30000000 điểm t&iacute;ch lũy.<br />\r\n<br />\r\n- B&iacute;p! Ph&aacute;t hiện cậu chủ c&oacute; bộ s&aacute;ch &quot;Ma Thư&quot;, bộ s&aacute;ch &quot;Thần Thư&quot;, bộ s&aacute;ch &quot;Ti&ecirc;n Thư&quot;, v&agrave; bộ s&aacute;ch &quot;Quỷ Thư&quot;, lĩnh ngộ hay in? - Lĩnh ngộ.<br />\r\n<br />\r\n- B&iacute;p! Ph&aacute;t hiện cậu c&oacute; G&oacute;i qu&agrave; Sơ đẳng, phải chăng muốn mở? - Mở! - B&iacute;p! G&oacute;i qu&agrave; Sơ đẳng của cậu gồm 20 Nguy&ecirc;n Tố Đan, 20 Ngũ H&agrave;nh Đan, Nguy&ecirc;n Tố Thư, Ngũ H&agrave;nh Thư, B&aacute;t Qu&aacute;i Thư, Sơn H&agrave; Quan Tưởng Trận, Sơn H&agrave; Quan Tưởng C&aacute;c, Sơn H&agrave; Quan Tưởng Đồ, Sơn H&agrave; Quan Tưởng Ph&aacute;p, 1 t&ograve;a Long Cung, 1 t&ograve;a L&acirc;n Cung, 1 t&ograve;a Quy Cung, 1 t&ograve;a Phụng Cung, Phụng Thư, Long Thư, L&acirc;n Thư, Quy Thư, 1 t&ograve;a Thần Cung, 1 t&ograve;a Ti&ecirc;n Cung, 1 t&ograve;a Ma Cung, 1 t&ograve;a Quỷ Cung, 1 t&ograve;a Đế Vương Cung, 1 t&ograve;a Linh Cung, 2000000000 điểm t&iacute;ch lũy, 3000000000000 điểm Đế Vương, 1 G&oacute;i qu&agrave; bắt đầu, v&agrave; kiến thức về Nhị Thập B&aacute;t T&uacute; c&ugrave;ng với 28 đại gia tộc.<br />\r\n<br />\r\n- Đ&ugrave;! Kh&ocirc;ng nhiều bằng lần trước nhưng chắc to&agrave;n đồ xịn, phải kh&ocirc;ng mậy? - Xịn hết.<br />\r\n<br />\r\nCậu chủ đừng lo.<br />\r\n<br />\r\n<br />\r\n- B&iacute;p! Ph&aacute;t hiện cậu c&oacute; 1 G&oacute;i qu&agrave; bắt đầu, phải chăng muốn mở? - Mở! - B&iacute;p! G&oacute;i qu&agrave; bắt đầu của cậu c&oacute; 1 gậy chữa thương bằng đ&aacute; qu&yacute; c&ugrave;ng với c&aacute;ch sử dụng, 1 t&ograve;a Th&aacute;nh bảo Linh Th&aacute;nh Ma Cung, 1 t&ograve;a Th&aacute;nh bảo Linh Th&aacute;nh Thần Cung, 1 t&ograve;a Th&aacute;nh bảo Linh Th&aacute;nh Quỷ Cung, 1 t&ograve;a Th&aacute;nh bảo Linh Th&aacute;nh Ti&ecirc;n Cung, Bồng Lai Cảnh, 1 bộ sưu tập s&aacute;ch về c&aacute;c chủng tộc tr&ecirc;n to&agrave;n Vũ Trụ, v&agrave; 50,000,000,000,000,000,000,000,000,000,000,000 điểm t&iacute;ch lũy.<br />\r\n<br />\r\n- B&iacute;p! Cậu chủ hiện c&oacute; 50,000,000,000,000,000,000,000,003,129,000,100 điểm t&iacute;ch lũy, liệu muốn mua sắm một phen? - Mở B&aacute; Vương thương h&agrave;nh đi.Cuối c&ugrave;ng, n&oacute; mua tổng cộng 2000 t&ograve;a Th&aacute;nh bảo, tổng cộng 30,000,000,000,000,000,000,000,003,129,000,100, c&ograve;n c&oacute; 20,000,000,000,000,000,000,000,000,000,000,000, n&oacute; mua 100 t&ograve;a Thần bảo, tổng cộng 10,000,000,000,000,000,000,000,000,000,000,000, 200000000 kiện vũ kh&iacute; đủ loại, tổng cộng 5,000,000,000,000,000,000,000,000,000,000,000, 40000000000 bộ gi&aacute;p cao cấp nhứt c&oacute; thể t&igrave;m thấy trong B&aacute; Vương thương h&agrave;nh, gi&aacute; 4,999,999,999,999,999,999,999,999,000,000,000 điểm, c&ograve;n lại 1,000,000,000 điểm, n&oacute; mua 1 bộ c&ocirc;ng ph&aacute;p luyện Thể cảnh l&agrave; Lực Đỉnh C&ocirc;ng, gi&aacute; 100,000,000, một bộ đồ d&ugrave;ng n&acirc;ng cấp vũ kh&iacute; gi&aacute; 400,000,000; v&agrave; một bộ đồ d&ugrave;ng bảo tr&igrave; &aacute;o gi&aacute;p gi&aacute; 400,000,000; c&ograve;n lại 100,000,000 điểm để d&agrave;nh.<br />\r\n<br />\r\n- B&iacute;p! Hiện giờ cậu chủ c&oacute; 2000 t&ograve;a Th&aacute;nh bảo, 100 t&ograve;a Thần bảo 1 t&ograve;a Th&aacute;nh bảo Linh Th&aacute;nh Ma Cung, 1 t&ograve;a Th&aacute;nh bảo Linh Th&aacute;nh Thần Cung, 1 t&ograve;a Th&aacute;nh bảo Linh Th&aacute;nh Quỷ Cung, 1 t&ograve;a Th&aacute;nh bảo Linh Th&aacute;nh Ti&ecirc;n Cung, 1 t&ograve;a Long Cung, 1 t&ograve;a L&acirc;n Cung, 1 t&ograve;a Quy Cung, 1 t&ograve;a Phụng Cung, 1 t&ograve;a Thần Cung, 1 t&ograve;a Ti&ecirc;n Cung, 1 t&ograve;a Ma Cung, 1 t&ograve;a Quỷ Cung, 1 t&ograve;a Đế Vương Cung, 1 t&ograve;a Linh Cung, liệu cậu chủ muốn đặt? - 2000 t&ograve;a Th&aacute;nh cung v&agrave; 100 t&ograve;a Thần cung th&igrave; đặt ở khắp nơi trong Huỳnh gia thủ phủ đi, c&ograve;n 4 t&ograve;a Linh Th&aacute;nh Cung đặt ở hai b&ecirc;n Ngũ H&agrave;nh Cung, 4 t&ograve;a Tứ Vương Cung đặt b&ecirc;n tr&aacute;i Thập Nghệ Cung, 4 t&ograve;a Tứ Đế Cung đặt ở b&ecirc;n phải B&aacute;t Qu&aacute;i Cung, Đế Vương Cung đặt ở ph&iacute;a sau Ngũ H&agrave;nh Cung, v&agrave; Linh Cung đặt ở hướng ch&iacute;nh Đ&ocirc;ng Huỳnh gia Th&aacute;i Tử Phủ.<br />\r\n<br />\r\n- B&iacute;p b&iacute;p b&iacute;p! Đ&atilde; đặt xong.<br />\r\n<br />\r\n- Bằng Hữu, mở ra th&ocirc;ng số....Chủ nh&acirc;n: Ho&agrave;ng QuốcTư chất: Đế VươngThể chất: Long L&acirc;n Quy Phụng thể.Huyết mạch: Thần Ti&ecirc;n Ma Quỷ huyết.Chức nghiệp: Kh&ocirc;ng.Hồn cảnh: Quỷ ảnh tầng 2Ti&ecirc;n cảnh: B&aacute;n Ti&ecirc;n tầng 2Thần cảnh: Hoang Cung nhị tinhMa cảnh: Hư Ma tầng 2Quỷ cảnh: Quỷ Ảnh tầng 2Kh&iacute; cảnh: Luyện Kh&iacute; tầng 3Huyết cảnh: Linh Huyết tầng 4Thể cảnh: Linh Thể tầng 5Ngũ H&agrave;nh cảnh: Ngũ H&agrave;nh tầng 3Nguy&ecirc;n Tố cảnh: Nguy&ecirc;n Tố tầng 6Linh cảnh: Linh Sĩ cấp 10B&aacute;t Qu&aacute;i cảnh: Cấn Đo&agrave;i trung kỳĐạo cảnh: Khai Đạo...Bỗng nhi&ecirc;n từ b&ecirc;n phải Quốc c&oacute; một tiếng bịch, n&oacute; nh&ograve;m qua xem th&igrave; thấy một người mặc &aacute;o trắng đang dựa v&agrave;o bức tường b&ecirc;n phải Long Cung, kh&ocirc;ng r&otilde; sống chết.<br />\r\n<br />\r\nLại gần th&igrave; mới biết người n&agrave;y l&agrave; một nữ sĩ, đeo một c&aacute;i mạng che mặt.<br />\r\n<br />\r\nN&oacute; giở c&aacute;i mạng che mặt th&igrave; thấy một khu&ocirc;n mặt ho&agrave;n mỹ.<br />\r\n<br />\r\nMột nữ tử thật xinh đẹp! Mặt tr&aacute;i xoan, s&oacute;ng mũi cao, đ&ocirc;i mắt kh&eacute;p hờ, mi cong như vẽ, bồ m&ocirc;i mềm mại.<br />\r\n<br />\r\n<br />\r\nThấy khắp th&acirc;n thể của nữ sĩ đều bị thương, n&oacute; bấm bụng ti&ecirc;u lu&ocirc;n thảo dược cao cấp n&oacute; định cất kho x&agrave;i.<br />\r\n<br />\r\nChữa xong cho khu&ocirc;n mặt, n&oacute; nh&igrave;n thấy những vết thương kh&aacute;c tr&ecirc;n bụng nữ sĩ, lật &aacute;o ra chữa thương th&igrave; thấy tr&ecirc;n ngực c&ograve;n một vệt m&aacute;u chảy d&agrave;i, n&oacute; thầm nghĩ: - N&ecirc;n kh&ocirc;ng ta? B&acirc;y giờ m&igrave;nh chữa th&igrave; người n&agrave;y bảo m&igrave;nh d&ecirc; xồm, kh&ocirc;ng chữ th&igrave; cầm chắc c&aacute;i chết?Thế l&agrave; n&oacute; bắt đầu chơi tr&ograve; đếm ng&oacute;n tay huyền thoại.<br />\r\n<br />\r\n- Kh&ocirc;ng chữa, chữa, kh&ocirc;ng chữa, Chữa, kh&ocirc;ng chữa, chữa, kh&ocirc;ng chữa, chữa, kh&ocirc;ng chữa, chữa.<br />\r\n<br />\r\nVậy th&igrave; m&igrave;nh chữa th&ocirc;i vậy.Sau một hồi, n&oacute; chữa xong, đặt lại tư thế ngồi cho nữ sĩ rồi ra gần đ&oacute; tu luyện (đ&uacute;ng hơn l&agrave; ăn đan) tiếp.<br />\r\n<br />\r\nNữ sĩ kia thức dậy, thấy tr&ecirc;n người m&igrave;nh khắp nơi đều được băng b&oacute;, cơn đau tr&ecirc;n ngực cũng kh&ocirc;ng c&ograve;n, m&agrave; lại thấy mạng che mặt của m&igrave;nh đ&atilde; mất, th&igrave; hốt hoảng nh&igrave;n xung quanh, th&igrave; thấy Quốc đang ăn đan ở b&ecirc;n kia liền xấu hổ.<br />\r\n<br />\r\nV&igrave; sao? V&igrave; sư phụ của n&agrave;ng từng n&oacute;i: - Nam nh&acirc;n n&agrave;o th&aacute;o mạng che mặt của con đầu ti&ecirc;n th&igrave; con phải nghe theo lời người đ&oacute; đến hết đời.<br />\r\n<br />\r\n<br />\r\n- N&agrave;ng tỉnh lại rồi &agrave;?Nữ sĩ kia giật m&igrave;nh, kh&ocirc;ng biết ai n&oacute;i.<br />\r\n<br />\r\n- Đừng lo, ta kh&ocirc;ng hại n&agrave;ng đ&acirc;u.Quốc l&uacute;c n&agrave;y n&oacute;i với Bằng Hữu: - Bằng Hữu, mở ra th&ocirc;ng số của người kia....T&ecirc;n: Hồ Như MộngTư chất: Gi&aacute;m mụcChức nghiệp: Cốc chủ Vạn Hoa cốc ki&ecirc;m L&acirc;u chủ Vạn Hoa L&acirc;uThể chất: Kh&ocirc;ng.Huyết mạch: Kh&ocirc;ng.Kh&iacute; cảnh: Luyện Đạo trung kỳĐạo cảnh: Khai ĐạoHảo cảm: 80 (Hơi rung động)...Đ&oacute; l&agrave; một chức năng mới m&agrave; Quốc t&igrave;m thấy của Bằng Hữu, kiểm tra th&ocirc;ng số của người kh&aacute;c.B&acirc;y giờ th&igrave; n&oacute; hỏi Bằng hữu: - Bằng Hữu n&agrave;y, cho ta hỏi c&oacute; mấy loại tư chất thế? - C&oacute; tổng cộng 12 tư chất, cậu chủ đừng hỏi th&ecirc;m, khi mạnh tới mức n&agrave;o đ&oacute; th&igrave; cậu chủ sẽ biết hết th&ocirc;i.<br />\r\n<br />\r\n- B&iacute;p! Ph&aacute;t động nhiệm vụ &quot;C&agrave;n qu&eacute;t Nhị Thập B&aacute;t T&uacute;&quot;, v&ocirc; thời hạn, y&ecirc;u cầu cậu chủ thống nhất Nhị Thập B&aacute;t T&uacute;, phần thưởng l&agrave; G&oacute;i qu&agrave; Thưởng đẳng, 1 khả năng bất kỳ m&agrave; cậu chủ muốn, 3,000,000,000,000,000 điểm t&iacute;ch lũy, 2,000,000,000,000,000,000,000,000 điểm Đế Vương, v&agrave; mở kh&oacute;a Chiến T&iacute;ch thương h&agrave;nh.<br />\r\n<br />\r\nPhải chăng cậu chủ muốn tiếp nhận? - Tiếp nhận..</span></span><br />\r\n&nbsp;</p>', 1, '2022-03-16 02:52:54', '2022-03-16 02:52:54'),
(7, 15, 'Chương 5: Tình Hình Nhị Thập Bát Gia', 'chuong-5-tinh-hinh-nhi-thap-bat-gia', '<p>afadf</p>', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">H&ocirc;m sau, tại Ch&iacute;nh Cung- Gia chủ, người đi đ&acirc;u đấy?- Ta đi qua Th&aacute;i Tử Phủ một ch&uacute;t, Quốc th&aacute;i tử của ch&uacute;ng ta muốn uống tr&agrave;.Người n&oacute;i ch&iacute;nh l&agrave; Huỳnh Th&uacute;c Kh&aacute;ng, gia chủ đương nhiệm của Huỳnh gia.Đến Th&aacute;i Tử Phủ, Huỳnh gia chủ gọi với Quốc:- Quốc Nhi, ta tới rồi đ&acirc;y.- A, gia gia v&agrave;o đ&acirc;y.- &Aacute;i ch&agrave;, lấy đ&acirc;u ra mấy t&ograve;a cung điện n&agrave;y thế, c&ograve;n c&oacute; tượng Tứ Vương nữa.- Dạ kh&ocirc;ng, l&agrave; cung điện đấy ạ.- Rồi, kh&ocirc;ng n&oacute;i nhảm nhiều, con gọi ta l&agrave;m g&igrave;?- &Agrave;, con muốn hỏi gia gia về quan hệ của Nhị Thập B&aacute;t Gia th&ocirc;i, người n&oacute;i cơ bản được kh&ocirc;ng?- Th&igrave; ra vậy.<br />\r\n<br />\r\nXem n&agrave;o, L&yacute; gia v&agrave; Trần gia đang x&iacute;ch m&iacute;ch, Luận gia v&agrave; B&agrave;n gia đang c&oacute; đấu tranh ch&iacute;nh trị kh&aacute; gay gắt, Th&agrave;nh gia v&agrave; B&agrave;nh gia đang hợp sức lại để ph&aacute;t triển một loại c&ocirc;ng nghệ n&agrave;o đ&oacute; của họ đến ta cũng kh&ocirc;ng biết, Dương gia v&agrave; Tần gia cũng vậy, Tề gia v&agrave; H&aacute;n gia, thậm ch&iacute; l&agrave; Chu gia v&agrave; Kim gia c&ugrave;ng với Trịnh gia v&agrave; Phạm gia đều đang c&oacute; xu hướng hợp t&aacute;c, Thượng gia v&agrave; Si&ecirc;u gia đang chống đối nhau, v&agrave; cả hai c&oacute; vẻ đang phục vụ cho một thế lực n&agrave;o đ&oacute;, ta d&aacute;m l&agrave; Ti&ecirc;n giới v&agrave; Quỷ giới lắm...- Vậy c&ograve;n những người kh&aacute;c?- Triệu gia v&agrave; Đường gia th&igrave; đang x&iacute;ch m&iacute;ch ch&iacute;nh trị rất lớn, c&oacute; vẻ như sắp chiến tranh tới nơi.<br />\r\n<br />\r\nL&ecirc; Nguyễn Hạ Trung tứ gia th&igrave; đang hợp t&aacute;c tạo loại vũ kh&iacute; mới để b&aacute;n cho Triệu Đường, ta nghĩ bốn t&ecirc;n cẩu tặc đ&oacute; nằm v&ugrave;ng trung lập.<br />\r\n<br />\r\nTrong Nhị Thập B&aacute;t Gia th&igrave; Tr&igrave;nh Ng&ocirc; nhị gia c&oacute; vẻ như l&agrave; mạnh nhất, v&agrave; đang đấu v&otilde; mồm với nhau.<br />\r\n<br />\r\n&Acirc;n Lương đang hợp t&aacute;c, v&agrave; cặp cuối l&agrave; Đ&agrave;o H&agrave;n c&oacute; vẻ hơi gh&eacute;t nhau.<br />\r\n<br />\r\n<br />\r\nTrong 28 gia tộc kia th&igrave; c&oacute; vẻ như Tr&igrave;nh Ng&ocirc; kh&ocirc;ng ai d&aacute;m động v&agrave;o, c&ograve;n yếu nhất l&agrave; L&yacute; gia v&agrave; Trần gia, tất cả 28 gia tộc đều c&oacute; x&iacute;ch m&iacute;ch s&acirc;u hằn v&agrave;o lịch sử, tuy nhi&ecirc;n b&acirc;y giờ c&oacute; lẽ đ&atilde; ch&igrave;m v&agrave;o qu&aacute; khứ rồi.<br />\r\n<br />\r\nL&yacute; Trần L&ecirc; Nguyễn tứ gia th&igrave; c&oacute; qu&aacute; nhiều x&iacute;ch m&iacute;ch với c&aacute;c gia tộc c&ograve;n lại, cuối c&ugrave;ng giờ thuộc loại yếu nhất, Hạ Trung Thượng Si&ecirc;u th&igrave; ổn hơn, mạnh nhất l&agrave; tứ đại gia tộc &Acirc;n H&agrave;n Lương Tr&igrave;nh, nhưng m&agrave; cho d&ugrave; vậy th&igrave; vẫn chung một nh&agrave;.<br />\r\n<br />\r\n- &Yacute; gia gia l&agrave; sao? - &Agrave;, &yacute; ta l&agrave; gia chủ họ đều l&agrave; trưởng l&atilde;o tiền bối g&igrave; g&igrave; đ&oacute; ở đ&acirc;y cả.<br />\r\n<br />\r\nTr&igrave;nh Trung gia chủ th&igrave; quản l&yacute; thư viện, ta d&aacute;m n&oacute;i l&agrave; hai &ocirc;ng l&atilde;o coi việc đọc hơn cả mạng sống của m&igrave;nh; Ng&ocirc; L&yacute; L&ecirc; Trần Hạ Triệu Đường Tần Tề H&aacute;n Chu Kim &Acirc;n H&agrave;n Lương Trịnh Nguyễn gia chủ đều l&agrave; những bậc v&otilde; học cao cường, họ quản l&yacute; qu&acirc;n đội, x&acirc;y dựng luật lệ v&agrave; chịu tr&aacute;ch nhiệm về t&agrave;i sản qu&acirc;n sự của Huỳnh gia ta; Đ&agrave;o gia v&agrave; Phạm gia quản l&yacute; c&aacute;c đ&igrave;nh viện; Đ&agrave;o Dương hai người l&agrave;m ở Nội vụ đường, chuy&ecirc;n t&igrave;m ra gi&aacute;n điệp; Luận B&agrave;n nhị gia th&igrave; khỏi n&oacute;i rồi, chuy&ecirc;n gia l&agrave;m cho mấy cuộc họp Trưởng l&atilde;o hội d&agrave;i l&ecirc; th&ecirc; ch&aacute;n ngắt, chưởng quản Ngoại vụ đường, chuy&ecirc;n thu thập tin tức; Th&agrave;nh B&agrave;nh l&agrave; hai chuy&ecirc;n gia l&agrave;m hỏng việc, nhưng được c&aacute;i l&agrave; thiết kế cực đẹp v&agrave; giỏi tổ chức; c&ograve;n Thượng Si&ecirc;u hai người quản l&yacute; C&ocirc;ng bộ, chuy&ecirc;n xử l&yacute; việc trong v&agrave; ngo&agrave;i phủ, n&oacute;i chung chung ra th&igrave; ngo&agrave;i qu&acirc;n sự ra th&igrave; bọn họ c&oacute; quyền lực cao nhất trong thủ phủ n&agrave;y.<br />\r\n<br />\r\n- Cho con hỏi gần đ&acirc;y việc kinh tế Huỳnh gia ch&uacute;ng ta sao ạ? - &Agrave;, gần đ&acirc;y th&igrave; cũng b&igrave;nh thường, l&acirc;u l&acirc;u b&aacute;n chạy, mặt h&agrave;ng chủ yếu l&agrave; đan dược v&agrave; vũ kh&iacute;, gi&aacute; tầm tầm 30000 tới 40000000 linh thạch, cửa h&agrave;ng nơi n&agrave;o cũng c&oacute;, mỗi th&agrave;nh phố tr&ecirc;n khắp Thi&ecirc;n Địa cầu đều c&oacute; Huỳnh gia phố.<br />\r\n<br />\r\nN&oacute;i chung l&agrave; kh&aacute; thịnh vượng.<br />\r\n<br />\r\n- Con c&oacute; &yacute; n&agrave;y, ch&uacute;ng ta đồng loạt đ&oacute;ng cửa c&aacute;c chi nh&aacute;nh, rồi b&aacute;o tin về khắp Nhị Thập B&aacute;t T&uacute; v&agrave; Thi&ecirc;n Địa cầu rằng ch&uacute;ng ta đang x&acirc;y một Huỳnh Gia Thương Hội C&aacute;c, rồi sau đ&oacute; x&acirc;y một c&aacute;i như vậy, nhờ Th&agrave;nh C&aacute;t H&atilde;n Th&agrave;nh gia chủ thiết kế, B&agrave;nh Ch&acirc;n B&agrave;nh gia chủ chủ tr&igrave; việc x&acirc;y dựng, việc n&agrave;y n&ecirc;n đừng để lọt v&agrave;o tai Tr&igrave;nh Trung Luận B&agrave;n tứ gia chủ biết, v&igrave; như vậy th&igrave; kiểu n&agrave;o cũng k&ecirc;u ca cho coi, con đề nghị lấy một khu Th&aacute;nh bảo con vừa thả ở trong thủ phủ l&agrave;m một con phố đi bộ cho người ta, v&agrave; cũng tung tin như đối với Huỳnh Gia Thương Hội C&aacute;c lu&ocirc;n, v&agrave; mở từ 6 giờ 30 đến 9 giờ tối.<br />\r\n<br />\r\nNhư vậy th&igrave; việc kinh doanh dễ điều khiển, v&agrave; ch&uacute;ng ta cũng c&oacute; thể thu lợi tận tay, kh&ocirc;ng cần chờ chuyển từ chi nh&aacute;nh kia đến Thủ phủ nữa.<br />\r\n<br />\r\n<br />\r\nNhờ Đ&agrave;o Cam Mộc Đ&agrave;o gia chủ v&agrave; Phạm Thi&ecirc;n Ho&agrave;ng Phạm gia chủ r&uacute;t tiền ng&acirc;n khố ra cho Th&agrave;nh C&aacute;t H&atilde;n v&agrave; B&agrave;nh Ch&acirc;n d&ugrave;ng, v&agrave; gia gia để mắt đến họ th&igrave; hơn.<br />\r\n<br />\r\nX&acirc;y lu&ocirc;n một xưởng đ&oacute;ng t&agrave;u v&agrave; đ&agrave;o k&ecirc;nh th&ocirc;ng ra biển, thu thập nh&acirc;n t&agrave;i rồi x&acirc;y một c&aacute;i Quốc Học đường, bồi dưỡng nh&acirc;n t&agrave;i, con c&oacute; Gi&aacute;m Định Thuật đ&acirc;y, gia gia đừng lo c&oacute; kẻ gian tr&agrave; trộn v&agrave;o trong.Gia gia thấy thằng ch&aacute;u nội nh&agrave; m&igrave;nh nhỏ vậy m&agrave; sắp xếp việc kinh doanh nh&agrave; m&igrave;nh đ&acirc;u ra đấy th&igrave; kinh ngạc, cũng phải th&ocirc;i, Quốc từng cầm đầu một băng nh&oacute;m c&oacute; tổ chức chuy&ecirc;n &quot;ch&ocirc;m&quot; đồ ăn ở mấy h&agrave;ng qu&aacute;n dưới phố thời c&ograve;n ở Tr&aacute;i Đất m&agrave;.Ng&agrave;y h&ocirc;m sau, to&agrave;n bộ trung t&acirc;m Vũ Trụ r&uacute;ng động v&igrave; tin tức của Huỳnh gia, một thương hội cực lớn v&agrave; một th&agrave;nh phố đi bộ sẽ được x&acirc;y trong Huỳnh gia thủ phủ, đồng thời c&aacute;c chi nh&aacute;nh kinh doanh v&agrave; c&aacute;c phố đi bộ do Huỳnh gia quản l&yacute; sẽ đồng loạt đ&oacute;ng cửa, thu về thương hội v&agrave; th&agrave;nh phố đi bộ mới.Đột nhi&ecirc;n...!- B&iacute;p! Ph&aacute;t hiện c&acirc;u chủ c&oacute; 1 G&oacute;i qu&agrave; bắt đầu h&ocirc;m qua, phải chăng muốn nhận? - Mẹ! Nhiều thế.<br />\r\n<br />\r\n- B&iacute;p! G&oacute;i qu&agrave; bắt đầu của cậu gồm bản đồ thi&ecirc;n văn tinh vi nhất của mọi thời đại, 1 t&ograve;a Địa Quỷ Cung, 1 t&ograve;a Địa Ma Cung, 1 t&ograve;a Thi&ecirc;n Thần Cung, 1 t&ograve;a Thi&ecirc;n Ti&ecirc;n Cung, 1 t&ograve;a C&agrave;n Kh&ocirc;n Cung, 1 t&ograve;a Ly Khảm Cung, 1 t&ograve;a Chấn Tốn Cung, 1 t&ograve;a Cấn Đo&agrave;i Cung, 2 t&ograve;a Đ&ocirc;ng Cung, 2 t&ograve;a T&acirc;y Cung, 2 t&ograve;a Nam Cung, 2 t&ograve;a Bắc Cung, 4 t&ograve;a Trung M&ocirc;n, v&agrave; 4000000000 điểm t&iacute;ch lũy.<br />\r\n<br />\r\n- B&iacute;p! Hiện giờ cậu c&oacute; 300,000,000,000,000 điểm Đế Vương, 4,100,000,200 điểm t&iacute;ch lũy v&agrave; 549859475824758624750 điểm Uy Danh, liệu cậu muốn mua sắm kh&ocirc;ng? - B&iacute;p! Khu vực bu&ocirc;n b&aacute;n giờ đ&atilde; c&oacute; th&ecirc;m Quầy đổi điểm, cậu chủ c&oacute; thể đổi điểm t&iacute;ch lũy ra c&aacute;c loại tiền tệ kh&aacute;c, thậm ch&iacute; c&oacute; thể đổi ra linh thạch với tỉ lệ Điểm 500:Linh thạch 1.<br />\r\n<br />\r\n- V&ocirc; mua lu&ocirc;n.V&agrave;o trong B&aacute; Vương thương h&agrave;nh, n&oacute; thấy điểm t&iacute;ch lũy của m&igrave;nh đ&atilde; tăng 100, mua được một vi&ecirc;n Trường Sinh Dược mới ra mắt.<br />\r\n<br />\r\nVậy l&agrave; n&oacute; c&oacute; thể mua 3 vi&ecirc;n để ti&ecirc;u hết tiền lẻ.<br />\r\n<br />\r\n<br />\r\nXong, n&oacute; mua tiếp một v&agrave;i thứ cần cho Huỳnh Gia Thương Hội C&aacute;c v&agrave; Huỳnh Gia H&agrave;nh Bộ Phố, rồi quay trở ra.<br />\r\n<br />\r\nNhưng lần n&agrave;y n&oacute; kh&ocirc;ng trở ra ngay m&agrave; gh&eacute; Đế Vương thương h&agrave;nh mua một v&agrave;i bức tượng lưu niệm, n&oacute; định để cho gia gia d&ugrave;ng b&ugrave;a Sinh đ&ocirc;i để tạo quầy lưu niệm Đế Vương.Xong xu&ocirc;i, n&oacute; đưa thư gửi mấy bức tượng cho Gia gia Huỳnh Th&uacute;c Kh&aacute;ng, gửi sơ đồ x&acirc;y dựng cho Huỳnh Th&uacute;c Chiến cha n&oacute; v&agrave; Th&agrave;nh C&aacute;t H&atilde;n để hỏi &yacute; kiến họ.<br />\r\n<br />\r\nĐồng thời, n&oacute; cũng hỏi thăm &yacute; kiến Cung Th&uacute;y H&agrave; Cung trưởng l&atilde;o quản l&yacute; ng&acirc;n khố về việc cho B&agrave;nh Ch&acirc;n v&agrave; Th&agrave;nh C&aacute;t H&atilde;n mượn tiền x&acirc;y dựng Huỳnh Gia Thương Hội C&aacute;c v&agrave; Huỳnh Gia H&agrave;nh Bộ Phố.<br />\r\n<br />\r\nXong việc, n&oacute; đi về tu luyện th&igrave; tiếng của Bằng Hữu vang l&ecirc;n: - B&iacute;p! Cậu chủ c&oacute; nhiệm vụ, &quot;V&agrave;o Vạn Hoa Cốc&quot;, y&ecirc;u cầu cậu gia nhập Vạn Hoa Cốc, phần thưởng l&agrave; mở cửa Chiến T&iacute;ch thương h&agrave;nh v&agrave; bắt đầu tu tập Nguy&ecirc;n Tố cảnh của ri&ecirc;ng tư chất Đế Vương!.</span></span></p>', 1, '2022-03-16 02:53:29', '2022-03-16 02:53:29'),
(8, 15, 'Chương 6: Cao Thảo Kinh Niên', 'chuong-6-cao-thao-kinh-nien', '<p>asdfgsgqrqr</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- C&ograve;n 5 th&aacute;ng nữa l&agrave; tổ chức buổi lễ nhập m&ocirc;n của tu sĩ dưới 18 tuổi rồi, con đi kh&ocirc;ng?Huỳnh Gia chủ Th&uacute;c Kh&aacute;ng hỏi Huỳnh th&aacute;i tử Huỳnh Kiến Quốc.- Con đi chứ.- &Agrave;, &ocirc;ng nội c&oacute; c&aacute;i n&agrave;y tặng sinh nhật con.- Tuần sau lận m&agrave; &ocirc;ng nội!- Kệ, kh&ocirc;ng th&igrave; &ocirc;ng nội kh&ocirc;ng n&oacute;i cho con biết về t&igrave;nh h&igrave;nh Huỳnh gia ta nữa.- Dạ rồi, con nhận n&egrave;.<br />\r\n<br />\r\nA, một tấm lệnh b&agrave;i...!của Vạn Hoa Cốc, sao &ocirc;ng nội biết con cần c&aacute;i n&agrave;y?- &Agrave;, h&ocirc;m qua c&oacute; cốc chủ Vạn Hoa Cốc đến thăm ta, đưa ta tấm lệnh b&agrave;i đ&oacute; rồi bảo đưa cho con.<br />\r\n<br />\r\n&Agrave;i, đ&uacute;ng rồi, mai l&agrave; ng&agrave;y khai trương Huỳnh Gia Thương Hội C&aacute;c, con đọc diễn văn ch&uacute;c mừng khai trương thay cho gia tộc.<br />\r\n<br />\r\n<br />\r\nTa c&oacute; đem tờ giấy n&agrave;y, đọc thuộc đi.Bản diễn văn khai trương n&agrave;y kh&aacute; d&agrave;i n&ecirc;n n&oacute; đ&atilde; viết ngắn gọn, học thuộc rồi n&oacute; ngủ, l&ograve;ng rối bời, về chuyện v&igrave; sao cốc chủ Vạn Hoa Cốc Hồ Như Mộng lại đưa cho n&oacute; tấm lệnh b&agrave;i, v&agrave; kh&ocirc;ng biết chắc rằng n&oacute; đ&atilde; thuộc r&agrave;nh r&agrave;nh b&agrave;i diễn văn chưa.Nhưng c&aacute;i g&igrave; đến cũng sẽ đến, ng&agrave;y mai, đứng trước đ&aacute;m đ&ocirc;ng, n&oacute; d&otilde;ng dạc h&ocirc; to: - K&iacute;nh ch&agrave;o c&aacute;c vị tu sĩ, c&aacute;c vị trưởng l&atilde;o! H&ocirc;m nay Huỳnh gia ta khai trương Huỳnh Gia Thương Hội C&aacute;c, đ&oacute;ng của c&aacute;c chi nh&aacute;nh, cảm ơn tất cả những người đứng đ&acirc;y v&igrave; đ&atilde; kh&ocirc;ng quản đường xa đến đ&acirc;y.<br />\r\n<br />\r\nHuỳnh Gia Thương Hội C&aacute;c n&agrave;y chia l&agrave;m 15 khu ch&iacute;nh, ch&iacute;nh Đ&ocirc;ng l&agrave; Đan C&aacute;c, hai b&ecirc;n l&agrave; Tr&ugrave; C&aacute;c v&agrave; Ph&ugrave; C&aacute;c; ch&iacute;nh T&acirc;y l&agrave; Trận C&aacute;c, hai b&ecirc;n l&agrave; Thư viện, Kinh viện; ch&iacute;nh Nam l&agrave; Kh&iacute; C&aacute;c, hai b&ecirc;n l&agrave; Nguy&ecirc;n Tố C&aacute;c, Ngũ H&agrave;nh C&aacute;c; ch&iacute;nh Bắc l&agrave; nơi bu&ocirc;n b&aacute;n tin tức, b&ecirc;n cạnh l&agrave; Chi&ecirc;m Tinh C&aacute;c; ở trung t&acirc;m l&agrave; hội đấu gi&aacute;, cứ c&aacute;ch 3 th&aacute;ng sẽ tổ chức một lần, bu&ocirc;n b&aacute;n c&aacute;c loại vũ kh&iacute;, đan dược, gi&aacute;p mũ, tượng, đ&aacute; qu&yacute;, x&aacute;c th&uacute;, v&agrave; nhiều loại mặt h&agrave;ng kh&aacute;c, b&ecirc;n cạnh l&agrave; nơi bu&ocirc;n qu&agrave; lưu niệm, nơi bu&ocirc;n b&aacute;n th&uacute;, v&agrave; 1 bảo t&agrave;ng.<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i nhiều nữa, mời mọi người v&agrave;o bu&ocirc;n b&aacute;n vui vẻ! Lưu &yacute;: Kh&ocirc;ng được ăn cướp đồ, nếu kh&ocirc;ng sẽ bị vứt v&agrave;o chuồng th&uacute; ăn thịt, ch&uacute;c mọi người!N&oacute; đi v&agrave;o trong c&ugrave;ng lớp lớp c&aacute;c tu sĩ để tham quan Huỳnh Gia Thương Hội C&aacute;c, cảnh vật đầu ti&ecirc;n n&oacute; thấy l&agrave;...!- Ch&agrave;o mừng qu&yacute; kh&aacute;ch tới Lam Ng&acirc;n Cung, nơi chứa đựng những t&agrave;i liệu qu&yacute; gi&aacute; nhất của Long tộc.<br />\r\n<br />\r\nĐ&acirc;y l&agrave; một trong 12 cung thuộc hệ thống Thập Nhị Nữ Thần Cung.<br />\r\n<br />\r\nT&igrave;m Bảo Vật Phong hệ, tới Thi&ecirc;n Hương Cung, Hỏa hệ tới Hồng Hỏa Cung,...Trong l&uacute;c c&ocirc; n&agrave;y n&oacute;i, Quốc đang đi tiếp qua Thập Nhị Nữ Thần Cung.Phải c&ocirc;ng nhận l&agrave; nơi n&agrave;y cực kỳ rộng lớn, đường đi lại m&aacute;t mẻ, cảm gi&aacute;c như đang đi qua chốn rừng tre Phật Cảnh.<br />\r\n<br />\r\nĐan C&aacute;c th&igrave; kh&aacute; nồng m&ugrave;i thuốc v&agrave; m&ugrave;i linh dược, Trận C&aacute;c th&igrave; c&oacute; cả đống người bỏ hết tiền ra mua một c&aacute;i trận m&agrave; m&igrave;nh chỉ x&agrave;i được một lần duy nhất.<br />\r\n<br />\r\nN&oacute; đến Đan C&aacute;c để mua Cao Ni&ecirc;n Linh Thảo, chữa bệnh cho mẹ.L&uacute;c n&agrave;y n&oacute; đang thấy m&igrave;nh đứng trong sảnh đường uy nghi&ecirc;m với đầy người mặc &aacute;o ch&ugrave;ng trắng đi đi lại lại quanh mấy quầy h&agrave;ng b&agrave;y b&aacute;n linh dược.<br />\r\n<br />\r\n<br />\r\nC&oacute; vẻ như n&oacute; đang đi sai đường.<br />\r\n<br />\r\nVừa quay lại th&igrave; c&oacute; một người gọi với theo n&oacute;: - Con trai, con đi đ&acirc;u thế, v&agrave;o đ&acirc;y đi.<br />\r\n<br />\r\n- Dạ kh&ocirc;ng sao đ&acirc;u, con bị lạc đường.<br />\r\n<br />\r\n- Kh&ocirc;ng sao đ&acirc;u, lại đ&acirc;y đi, h&igrave;nh như con đang t&igrave;m kiếm một loại linh dược &agrave;? May cho con l&agrave; đến đấy, Linh Dược Đường ch&uacute;ng ta đang b&aacute;n h&agrave;ng tồn kho.<br />\r\n<br />\r\n<br />\r\nCon lại đ&acirc;y m&agrave; mua.Linh Dược Đường, c&aacute;i t&ecirc;n quen quen, n&oacute; vội lấy bản đồ ra v&agrave; đập v&agrave;o mắt n&oacute; l&agrave; d&ograve;ng chữ Linh Dược Đường ngay g&oacute;c T&acirc;y Bắc của Cấm phủ, 1 trong Tứ Dược Đường.Như c&aacute;c kiến tr&uacute;c kh&aacute;c trong Cấm phủ, Tứ Dược Đường từng sản sinh ra những bậc thầy lỗi lạc v&agrave; tri thức uy&ecirc;n b&aacute;c, bao g&ograve;m cả Th&aacute;nh sư Thoth l&uacute;c ng&agrave;i mới bước chan v&agrave;o y học v&agrave; theo học một vị thầy, Thần sư Tos, một bậc thầy về tứ dược l&uacute;c bấy giờ.N&oacute;i về tứ dược, người ta thường li&ecirc;n tưởng tới những c&aacute;i vạc s&ocirc;i sục một loại Thần dược hay Độc dược, những khu vườn trồng Thảo dược v&agrave; Linh dược c&aacute;c loại, v&agrave; nơi thể hiện 4 thứ đ&oacute; r&otilde; nhất l&agrave; Tứ Dược Đường, tọa lạc tại đường Kiến Thức thuộc Cấm phủ, Huỳnh phủ.<br />\r\n<br />\r\nNghe n&oacute;i rằng ng&agrave;y xưa, khi m&agrave; đường Kiến Thức c&ograve;n chưa tồn tại th&igrave; đ&atilde; c&oacute; một Th&aacute;nh sư Thoth chuy&ecirc;n ph&aacute;t thuốc cho người ngh&egrave;o, chấn động tới cả Thi&ecirc;n Đ&igrave;nh tr&ecirc;n trời v&agrave; &Acirc;m Phủ dưới đất, l&agrave;m cho ngay cả Ngọc Ho&agrave;ng v&agrave; Minh Đế c&ograve;n phải đ&iacute;ch th&acirc;n đến xin thuốc, đặt cho c&aacute;i danh hiệu Th&aacute;nh sư khiến cho Tos được hưởng l&acirc;y, nhờ l&agrave; thầy, Tos trở th&agrave;nh Thần sư, c&ograve;n vị thầy của Tos v&agrave; một người sư đệ của Tos, một người th&agrave;nh Tối Thượng sư, Legenire, v&agrave; người c&ograve;n lại th&agrave;nh Thần sư Terar, c&ograve;n đệ tử th&igrave; trở th&agrave;nh Th&aacute;nh sư thứ hai, Aresin, &quot;một người l&agrave;m quan cả họ được nhờ&quot;.Đ&oacute; l&agrave; th&ocirc;ng tin của Bằng Hữu truyền v&agrave;o n&atilde;o của Quốc.<br />\r\n<br />\r\nTới b&acirc;y giờ, Quốc đ&atilde; biết được một v&agrave;i t&iacute;nh năng của Bằng Hữu, một l&agrave; thương h&agrave;nh, hai l&agrave; kho bảo vật, ba l&agrave; bản đồ, bốn l&agrave; truyền kiến thức, năm l&agrave; thang đo tu vi, s&aacute;u l&agrave; thần thức, v&agrave; bảy l&agrave; x&aacute;c định danh t&iacute;nh.Sau khi tiếp nhận xong, n&oacute; đi theo, &ocirc;ng ch&uacute; lạ mặt l&uacute;c n&agrave;y đ&atilde; đến gần n&oacute;, đến một quầy h&agrave;ng, v&agrave; l&agrave;m cho n&oacute; kinh ngạc l&agrave;...!- Cao Thảo Kinh Ni&ecirc;n!.</span></span><br />\r\n&nbsp;</p>', 1, '2022-03-16 02:55:18', '2022-03-22 15:26:33'),
(9, 15, 'Chương 7: Vô Hình Điện - Hữu Hình Điện - Lời Nguyền Của Thánh Sư Thoth - Đệ Tử Của Thoth', 'chuong-7-vo-hinh-dien-huu-hinh-dien-loi-nguyen-cua-thanh-su-thoth-de-tu-cua-thoth', '<p>afadsdgsg</p>', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">- Cao Thảo Kinh Ni&ecirc;n!Đ&uacute;ng, ch&iacute;nh l&agrave; loại cao ni&ecirc;n thảo m&agrave; Quốc cần để chữa bệnh cho mẹ.<br />\r\n<br />\r\nCao Thảo Kinh Ni&ecirc;n l&agrave; một lo&agrave;i thảo dược cực hiếm th&acirc;n cỏ, v&igrave; c&acirc;y th&acirc;n cỏ thường &quot;ngủm&quot; trước khi đ&oacute;n sinh nhật đầu, loại Cao Thảo Kinh Ni&ecirc;n n&agrave;y thường bị nhầm lẫn với lo&agrave;i C&acirc;y Bựa ở v&ugrave;ng bi*n đ*ng bắc ch&acirc;u Vegas nằm ở đ&ocirc;ng bắc tinh cầu.<br />\r\n<br />\r\n- Cảm ơn ch&uacute;, ch&uacute; Tranh!Đ&uacute;ng vậy, người đ&agrave;n &ocirc;ng dẫn Quốc đi mua linh dược l&agrave; Huỳnh Th&uacute;c Tranh, em trai của cha n&oacute;, Huỳnh Th&uacute;c Chiến, con trai thứ hai của gia chủ Huỳnh Th&uacute;c Kh&aacute;ng.N&oacute; lại đi bơ vơ t&igrave;m lối về Thương Hội C&aacute;c th&igrave; chợt một giọng n&oacute;i th&acirc;m trầm, lạnh lẽo ph&aacute;t ra sau lưng n&oacute;: - Ch&agrave;o nh&oacute;c, muốn du lịch ở V&ocirc; H&igrave;nh Điện &agrave;?Người n&oacute;i l&agrave; một người đ&agrave;n &ocirc;ng trạc tuổi cha n&oacute;, th&acirc;n vận một chiếc &aacute;o ch&ugrave;ng m&agrave;u đen, xung quanh &ocirc;ng dường như c&oacute; một l&agrave;n sương kh&ocirc;ng m&agrave;u.<br />\r\n<br />\r\nThấy Quốc ch&uacute; &yacute; đến n&oacute;, &ocirc;ng giải đ&aacute;p, khi m&agrave; Quốc su&yacute;t nữa th&igrave; hỏi Bằng Hữu: - Đ&oacute; l&agrave; V&ocirc; H&igrave;nh Sương, đặc trưng của những v&ocirc; h&igrave;nh giả đ&atilde; đặt tới Kh&iacute; cảnh Tối Thượng hậu kỳ như ta, chỉ cần c&oacute; đạo t&acirc;m vững ch&atilde;i c&ugrave;ng với đủ lượng linh kh&iacute;, con c&oacute; thể gọi n&oacute; ra bất kỳ l&uacute;c n&agrave;o con muốn.<br />\r\n<br />\r\nN&oacute; c&oacute; thể xem như một tấm l&aacute; chắn bọc hậu, v&agrave; c&ograve;n to&aacute;t ra uy lực ch&acirc;n ch&iacute;nh nếu con cần.<br />\r\n<br />\r\n<br />\r\nNếu như muốn c&oacute; V&ocirc; H&igrave;nh Sương, h&atilde;y theo ta đi theo con đường của Th&aacute;nh sư Aresin,...!th&aacute;i tử, Huỳnh Kiến Quốc!Thấy &ocirc;ng gọi đ&uacute;ng t&ecirc;n m&igrave;nh, l&uacute;c n&agrave;y đang đeo mặt nạ đễ dễ đi qua đi lại Thương Hội C&aacute;c m&agrave; kh&ocirc;ng bị ph&aacute;t hiện, Quốc hơi hơi đứng tim.<br />\r\n<br />\r\nĐ&acirc;y chắc chắn l&agrave; cường giả Tối Thượng hậu kỳ rồi, n&ecirc;n Quốc lật đật chạy theo.N&oacute; đang ở b&ecirc;n cạnh người đ&agrave;n &ocirc;ng b&iacute; ẩn, m&agrave; theo như n&oacute; điều tra từ Bằng Hữu, th&igrave; &ocirc;ng ch&iacute;nh l&agrave; Hồng Cao Tấn, điện chủ V&ocirc; H&igrave;nh Điện.<br />\r\n<br />\r\nN&oacute; đang ở b&ecirc;n trong một gi&aacute;o đường khổng lồ với những ngọn đuốc v&agrave; hoa văn tr&ecirc;n khắp c&aacute;c bức tường l&agrave;m từ một loại kim loại v&ocirc; h&igrave;nh...!- V&ocirc; H&igrave;nh Tinh Kim! Loại kim loại d&ugrave;ng để quan s&aacute;t địch tốt từ b&ecirc;n trong những ở b&ecirc;n ngo&agrave;i địch chỉ thấy một bức tường, thường được sử dụng để x&acirc;y những bức tường đ&aacute;nh lừa địch.<br />\r\n<br />\r\nLoại kim tinh n&agrave;y được giới v&ocirc; h&igrave;nh giả cực kỳ y&ecirc;u th&iacute;ch, v&agrave; hiện giờ gần như tất cả mọi người đều kh&ocirc;ng biết cấu tạo của n&oacute;!Giọng n&oacute;i của Bằng Hữu vừa vang l&ecirc;n từ cuối c&ugrave;ng, Hồng Cao Tấn cất l&ecirc;n c&aacute;i giọng trầm trầm của &ocirc;ng: - V&ocirc; h&igrave;nh học, một m&ocirc;n khoa học huyền b&iacute; nhưng th&uacute; vị, chuy&ecirc;n nghi&ecirc;n cứu những sinh vật v&ocirc; h&igrave;nh, những c&otilde;i giới v&ocirc; h&igrave;nh nằm ngo&agrave;i tầm hiểu biết, đ&agrave;o tạo những chuy&ecirc;n vi&ecirc;n c&oacute; thể li&ecirc;n lạc với linh hồn, v&agrave; t&igrave;m hiểu chuyến đi của linh hồn sau khi chết.<br />\r\n<br />\r\nCon c&oacute; tiềm năng trở th&agrave;nh một v&ocirc; h&igrave;nh cường giả, ta c&oacute; một c&acirc;u hỏi nhập m&ocirc;n cho con, ta l&agrave; ai? - Ta l&agrave; ai? Một c&acirc;u hỏi tưởng chừng như đơn giản nhưng chứa đựng nhiều b&agrave;i học s&acirc;u sắc, c&aacute;ch tốt nhất để trả lời l&agrave; khi thể x&aacute;c n&agrave;y chết đi, ta c&oacute; chết kh&ocirc;ng, nếu như ta kh&ocirc;ng phải thể x&aacute;c n&agrave;y, ta sẽ kh&ocirc;ng chết, nhưng l&agrave;m sao chứng minh được điều đ&oacute;?V&agrave;o đ&uacute;ng l&uacute;c Quốc n&oacute;i xong, Hồng Cao Tấn rưng rưng lệ, &ocirc;ng n&oacute;i trong cơn nghẹn ng&agrave;o: - Ng&agrave;y đ&oacute;, sư phụ của ta...!người cũng đ&atilde; từng tự hỏi lại ch&iacute;nh m&igrave;nh c&acirc;u hỏi đ&oacute;, c&acirc;u hỏi m&agrave;...!m&agrave;...!con vừa tự hỏi.<br />\r\n<br />\r\nNgười đ&atilde; l&agrave;m thử, một ng&agrave;y người th&agrave;nh c&ocirc;ng, nhưng...!nhưng người đ&atilde; tự r&uacute;t linh hồn m&igrave;nh ra khỏi thể x&aacute;c, người c&oacute; c&acirc;u trả lời ch&iacute;nh x&aacute;c, đồng thời...!người...!người cũng...!biến mất m&atilde;i m&atilde;i.<br />\r\n<br />\r\nTa l&uacute;c đ&oacute; đang đi chơi ở trong rừng, về hang thấy sư phụ như người mất hồn, ta lay m&atilde;i th&acirc;n x&aacute;c người.<br />\r\n<br />\r\nTa kh&oacute;c, kh&oacute;c, sau đ&oacute;, ta đ&agrave;nh chấp nhận sự thật l&agrave; người đ&atilde; chết m&agrave; ch&ocirc;n cất người.<br />\r\n<br />\r\n<br />\r\n- Cho ch&aacute;u hỏi, sư phụ của &ocirc;ng l&agrave; ai? Con kh&ocirc;ng c&oacute; &yacute; định khơi lại nỗi đau của &ocirc;ng, m&agrave; con hơi cảm thấy nghi ngờ, c&aacute;ch chết của người giống hệt c&aacute;ch chết của Th&aacute;nh sư Thoth! - Cụ l&agrave; một v&ocirc; h&igrave;nh giả xuất sắc, ta phải n&oacute;i như vậy.<br />\r\n<br />\r\n&Agrave;, con nghi ngờ sao, cụ ch&iacute;nh l&agrave; Th&aacute;nh sư Thoth.<br />\r\n<br />\r\nCụ bị một lời nguyền, một lời nguyền cay độc ảnh hưởng đến thế hệ học tr&ograve;, mỗi người chỉ được nhận một đệ tử, v&agrave; sẽ chết sau khi truyền dạy mọt nửa kiến thức của m&igrave;nh, ch&iacute;nh Th&aacute;nh sư Aresin l&agrave; một trường hợp, người đ&atilde; ếm lời nguyền n&agrave;y ch&iacute;nh l&agrave; Tr&ugrave;m Lớn, một người kh&ocirc;ng r&otilde; họ t&ecirc;n, qu&ecirc; qu&aacute;n.<br />\r\n<br />\r\nHắn l&agrave; một người tinh th&ocirc;ng Nghệ thuật Hắc &aacute;m, loại ma thuật &aacute;c độc nhất thế gian.<br />\r\n<br />\r\nẾm l&ecirc;n Th&aacute;nh sư Thoth chưa đủ, hắn c&ograve;n ếm l&ecirc;n cả hai Thần sư v&agrave; Th&aacute;nh sư c&ograve;n lại, ri&ecirc;ng với Thượng sư Legenire th&igrave; hai người bất ph&acirc;n thắng bại.N&oacute;i tới đ&acirc;y, Cao Tấn c&ugrave;ng Quốc khẽ r&ugrave;ng m&igrave;nh, một t&ecirc;n Ch&uacute;a tể Hắc &aacute;m nguyền được 2 Th&aacute;nh sư v&agrave; 2 Thần sư, c&ograve;n đ&aacute;nh ngang với Thượng sư th&igrave; hẳn l&agrave; một...!- Thượng sư B&oacute;ng Tối!C&oacute; hai loại Thượng sư tr&ecirc;n đời n&agrave;y, l&agrave; Thượng sư &Aacute;nh S&aacute;ng, ch&iacute;nh l&agrave; Legenire, v&agrave; Thượng sư B&oacute;ng Tối, theo như n&oacute; tra kiến thức từ Bằng Hữu th&igrave; hắn t&ecirc;n l&agrave;...!- Demoniris, Thương sư B&oacute;ng Tối, được ch&iacute;nh Ch&uacute;a Quỷ Satan c&ocirc;ng nhận! C&ograve;n Thượng sư &Aacute;nh S&aacute;ng Legenire được c&ocirc;ng nhận bởi Đấng S&aacute;ng Tạo Zeus.<br />\r\n<br />\r\n&Agrave; m&agrave; trời sắp tối rồi, con ở đ&acirc;y ăn lu&ocirc;n đi.<br />\r\n<br />\r\n<br />\r\nCon c&oacute; c&acirc;u trả lời cho ta chưa? - Dạ v&acirc;ng, ta l&agrave; linh hồn của ch&iacute;nh ta! - Đ&uacute;ng vậy, ta l&agrave; linh hồn của ta, kh&ocirc;ng phải thể x&aacute;c, nhiều người lầm tưởng ở điều n&agrave;y, họ mua rất nhiều quần &aacute;o đẹp, mặc cho sang trọng, quyền lực cho cao (&ocirc;ng đột ngột dừng lại v&igrave; thấy Quốc đang nh&igrave;n m&igrave;nh chăm ch&uacute;, &yacute; muốn hỏi &quot;Vậy &ocirc;ng c&oacute; quyền lực hơi bị cao đấy.&quot;), ăn cho nhiều, m&agrave; kh&ocirc;ng biết rằng họ lại đang chăm s&oacute;c cho ch&iacute;nh con th&uacute; cưỡi tạm thời của m&igrave;nh, một con th&uacute; chẳng biết g&igrave; ngo&agrave;i ăn, mặc ngủ, v&agrave; c&aacute;c thứ vật chất.<br />\r\n<br />\r\nHọ chẳng chăm lo cho linh hồn, chẳng biết đ&oacute;i thiếu l&agrave; g&igrave;.<br />\r\n<br />\r\nHọ chỉ chăm lo cho n&oacute; m&agrave; kh&ocirc;ng nghĩ rằng m&igrave;nh kh&ocirc;ng thể n&agrave;o đem n&oacute; đi sau khi m&igrave;nh chết cả.L&uacute;c n&agrave;y, đột nhi&ecirc;n c&oacute; một người đ&agrave;n &ocirc;ng sỗ s&agrave;ng từ cửa x&ocirc;ng v&agrave;o: - Ụ mẹ, thằng đi&ecirc;n Cao Tấn sao lại dẫn đại th&aacute;i tử sang c&aacute;i cung điện dở hơi n&agrave;y.<br />\r\n<br />\r\nỤ mẹ, m&agrave;y ăn n&oacute;i g&igrave; với n&oacute; hả Tấn? Mụ nội m&agrave;y, gia chủ m&agrave; biết l&agrave; m&agrave;y chết chắc nha con!N&oacute;i c&acirc;u n&agrave;o l&agrave; chửi thề c&acirc;u đ&oacute;, Hồng Cao Thắng, anh trai Hồng Cao Tấn, điện chủ Hữu H&igrave;nh Điện, trước những h&agrave;nh động sỗ s&agrave;ng của Thắng, Quốc định n&oacute;i b&agrave;o chữa cho Tấn th&igrave; Thắng đ&atilde; cất tiếng, quay mặt về ph&iacute;a Quốc v&agrave; n&oacute;i c&aacute;i giọng dịu d&agrave;ng kh&aacute;c hẳn với l&uacute;c chửi Tấn: - Ch&agrave;o con, hẳn con l&agrave; đại th&aacute;i tử Huỳnh Kiến Quốc (tới đ&acirc;y th&igrave; thằng Quốc chửi thầm &quot;G&igrave; vậy ba? Tự nhi&ecirc;n trước đ&oacute; n&oacute;i t&ocirc;i l&agrave; đại th&aacute;i tử giờ lại đi hỏi t&ocirc;i c&oacute; phải l&agrave; đại th&aacute;i tử hay kh&ocirc;ng? Ảo ma canada!&quot;).</span></span></p>', 1, '2022-03-16 02:56:39', '2022-03-16 02:56:39');
INSERT INTO `chapters` (`id`, `book_id`, `name`, `slug`, `description`, `content`, `active`, `created_at`, `updated_at`) VALUES
(10, 5, 'Chương 2: Triều khí bừng bừng', 'chuong-2-trieu-khi-bung-bung', '<p>dfhdhdhd</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thạch Th&ocirc;n nằm trong d&atilde;y n&uacute;i Thương M&atilde;ng, bốn bề n&uacute;i cao khe lớn, quần thể n&uacute;i non nguy nga kỳ vĩ trải ng&uacute;t tầm nh&igrave;n.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">S&aacute;ng sớm, từng l&agrave;n m&acirc;y sớm trải xuống lấp l&aacute;nh &aacute;nh v&agrave;ng, khiến người ta cảm thấy ấm &aacute;p khi đắm m&igrave;nh trong đ&oacute;/</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một đ&aacute;m mấy chục đứa trẻ lớn nhỏ kh&ocirc;ng đồng đều, khoảng từ bốn năm tuổi cho đến mười mấy tuổi đang đứng tr&ecirc;n khoảng đất trống trước th&ocirc;n, ngẩng mặt đ&oacute;n l&agrave;n m&acirc;y sớm, h&igrave; hục r&egrave;n luyện th&acirc;n thể. Từng khu&ocirc;n mặt non nớt lộ vẻ nghi&ecirc;m t&uacute;c, mấy đứa lớn hơn một ch&uacute;t th&igrave; h&ugrave;ng hổ lắm, tụi nhỏ hơn cũng đang khua khoắng kh&aacute; l&agrave; ra d&aacute;ng.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một người đ&agrave;n &ocirc;ng trung ni&ecirc;n tr&aacute;ng kiện như hổ b&aacute;o, mặc &aacute;o da th&uacute;, nước da m&agrave;u đồng, t&oacute;c đen bu&ocirc;ng x&otilde;a, &aacute;nh mắt lấp l&aacute;nh quan s&aacute;t từng đứa trẻ, cẩn thận chỉ điểm cho ch&uacute;ng.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;L&uacute;c mặt trời mới mọc l&agrave; l&uacute;c vạn vật khởi đầu, sinh kh&iacute; mạnh nhất, tuy kh&ocirc;ng thể nuốt m&acirc;y ngậm kh&iacute; như trong truyền thuyết, nhưng r&egrave;n luyện th&acirc;n thể dưới l&agrave;n m&acirc;y sớm cũng c&oacute; &iacute;ch lợi rất lớn. C&oacute; thể bồi đắp sinh cơ cho cơ thể. Trong một ng&agrave;y chỉ c&oacute; thời khắc s&aacute;ng sớm n&agrave;y th&ocirc;i, chịu kh&oacute; dậy sớm chăm chỉ r&egrave;n luyện, cường g&acirc;n tr&aacute;ng cốt, hoạt huyết luyện g&acirc;n, về sau mới c&oacute; vốn m&agrave; sinh tồn trong chốn m&atilde;ng sơn n&agrave;y.&quot; Người trung ni&ecirc;n đứng ph&iacute;a trước chỉ điểm đ&aacute;m trẻ nghi&ecirc;m mặt nhắc nhở, sau đ&oacute; qu&aacute;t: &quot;C&aacute;c ngươi c&oacute; hiểu kh&ocirc;ng?&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Hiểu!&quot; Đ&aacute;m trẻ hăng h&aacute;i cất giọng đ&aacute;p lời.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong n&uacute;i hay c&oacute; những sinh vật tiền sử xuất hiện. C&oacute; lo&agrave;i c&aacute;nh lớn che trời bay ngang in b&oacute;ng trải đầy mặt đất, cũng c&oacute; những con hoang th&uacute; đứng tr&ecirc;n đỉnh n&uacute;i tru trăng, c&agrave;ng kh&ocirc;ng thể thiếu những lo&agrave;i độc tr&ugrave;ng b&ograve; lổn ngổn, v&ocirc; c&ugrave;ng đ&aacute;ng sợ.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Hiểu ạ.&quot; Một nh&oacute;c t&igrave; r&otilde; r&agrave;ng đ&atilde; lơ đ&atilde;ng, chậm hẳn nửa nhịp thỏ thẻ l&ecirc;n tiếng.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; một ch&uacute; nh&oacute;c rất nhỏ, chỉ khoảng một hai tuổi, mới biết đi chưa được bao l&acirc;u, thế m&agrave; cũng đi theo mọi người r&egrave;n luyện th&acirc;n thể. Hiển nhi&ecirc;n ch&uacute; nh&oacute;c tự m&igrave;nh b&aacute;m theo tr&agrave; trộn trong đ&aacute;m trẻ th&ocirc;i chứ chưa đến tuổi xuất hiện trong đội ngũ n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;H&ocirc; h&ocirc; ha hi&quot; Ch&uacute; nh&oacute;c bi b&ocirc;, c&aacute;nh tay non nớt ra sức m&uacute;a may bắt chước động t&aacute;c của lũ trẻ lớn hơn, nhưng v&igrave; n&oacute; c&ograve;n qu&aacute; nhỏ n&ecirc;n cứ xi&ecirc;u xi&ecirc;u vẹo vẹo, bước ch&acirc;n tập tễnh lắc lư, cộng th&ecirc;m vệt sữa trăng trắng c&ograve;n vương b&ecirc;n m&eacute;p khiến mọi người phải bật cười.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&aacute;m trẻ lớn nh&igrave;n n&oacute;, ai nấy cau m&agrave;y liếc mắt khiến cho bầu khoog kh&iacute; r&egrave;n luyện nghi&ecirc;m t&uacute;c buổi sớm thoải m&aacute;i hơn kh&ocirc;ng &iacute;t.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute; nh&oacute;c trắng trẻo xinh xắn, đ&ocirc;i mắt đen l&aacute;y xoay tr&ograve;n tr&ocirc;ng như một con b&uacute;p b&ecirc; bằng sứ, đ&aacute;ng y&ecirc;u v&ocirc; c&ugrave;ng. Động t&aacute;c non nớt c&ugrave;ng những tiếng bi b&ocirc; trong miệng thật ng&acirc;y ng&ocirc; khờ khạo. Nh&igrave;n ch&uacute; nh&oacute;c, mấy người gi&agrave; đang khoanh ch&acirc;n ngồi h&iacute;t thở tinh kh&iacute; đất trời tr&ecirc;n những tảng đ&aacute; lớn ở một khoảng đất kh&aacute;c cũng phải nở nụ cười.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngay cả đ&aacute;m trai tr&aacute;ng trưởng th&agrave;nh th&acirc;n h&igrave;nh vạm vỡ, ở trần để lộ cơ bắp cuồn cuộn cũng nh&igrave;n sang, &yacute; cười lộ r&otilde;. Họ l&agrave; những người cường tr&aacute;ng nhất th&ocirc;n, l&agrave; lực lượng quan trọng nhất phụ tr&aacute;ch săn bắn v&agrave; bảo vệ th&ocirc;n l&agrave;ng. Họ cũng đang r&egrave;n luyện, c&oacute; người cầm c&acirc;y gậy lớn m&agrave;i từ xương cự th&uacute;, c&oacute; người lại mang kiếm to bản r&egrave;n từ kim loại m&agrave;u đen ra sức m&uacute;a, tiếng gi&oacute; nổi l&ecirc;n như sấm.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ho&agrave;n cảnh sinh tồn v&ocirc; v&ugrave;ng khốc liệt, qu&aacute; nhiều hồng hoang m&atilde;nh th&uacute; độc tr&ugrave;ng, để kiếm c&aacute;i ăn, để sống s&oacute;t, c&oacute; rất nhiều trai tr&aacute;ng chưa kịp trưởng th&agrave;nh đ&atilde; chết yểu trong chốn đại hoang. Muốn sống, chỉ c&oacute; kh&ocirc;ng ngừng l&agrave;m mạnh bản th&acirc;n. R&egrave;n luyện buổi sớm đ&atilde; trở th&agrave;nh th&oacute;i quen của mỗi người, từ người trưởng th&agrave;nh đến người gi&agrave; v&agrave; trẻ nhỏ.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Thu t&acirc;m!&quot; Người trung ni&ecirc;n phụ tr&aacute;ch k&egrave;m cặp chỉ dạy lũ trẻ lớn tiếng h&ocirc;. Đ&aacute;m trẻ vội v&agrave;ng tập trung ch&uacute; &yacute;, tiếp tục r&egrave;n luyện dưới l&agrave;n m&acirc;y sớm nhu h&ograve;a lấp l&aacute;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Ph&ugrave;&hellip; &ocirc;i cha&hellip; mệt qu&aacute;.&quot; Ch&uacute; nh&oacute;c thở ph&agrave;o một hơi ngồi phịch xuống đất nh&igrave;n đ&aacute;m trẻ lớn r&egrave;n luyện thể ph&aacute;ch, nhưng chỉ một chốc n&oacute; liền ph&acirc;n t&aacute;n sự ch&uacute; &yacute;, đứng dậy bập bễnh chạy về ph&iacute;a một con chim sẻ năm m&agrave;u đang nhảy nh&oacute;t c&aacute;ch đ&oacute; kh&ocirc;ng xa, cuối c&ugrave;ng lại li&ecirc;n tục vấp ng&atilde; phịch m&ocirc;ng xuống đất, nhưng n&oacute; kh&ocirc;ng kh&oacute;c m&agrave; tức tối lồm cồm b&ograve; dậy tiếp tục đuổi theo.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Được rồi, thu c&ocirc;ng!&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sau một tiếng h&ocirc; lớn, tất cả lũ trẻ đồng loạt reo h&ograve;, xoa nắn ch&acirc;n tay nhức mỏi rồi chạy &ugrave;a đi về ph&iacute;a nh&agrave; m&igrave;nh chuẩn bị ăn cơm s&aacute;ng.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Người gi&agrave; cười x&ograve;a, cũng đứng dậy khỏi phiến đ&aacute; lớn. C&ograve;n đ&aacute;m thanh ni&ecirc;n trai tr&aacute;ng như hổ b&aacute;o kia th&igrave; vừa cười vừa mắng, tr&ecirc;u chọc con m&igrave;nh, cầm cốt bổng c&ugrave;ng đại kiếm rảo bước về nh&agrave;.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thạch Th&ocirc;n kh&ocirc;ng lớn, gi&agrave; trẻ trai g&aacute;i cộng lại được khoảng hơn ba trăm người, nh&agrave; cửa đểu dựng từ đ&aacute; tảng, đơn sơ một c&aacute;ch tự nhi&ecirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đầu th&ocirc;n c&oacute; một gốc c&acirc;y s&eacute;t đ&aacute;nh bự chảng, đường k&iacute;nh hơn chục m&eacute;t, l&uacute;c n&agrave;y tr&ecirc;n c&agrave;nh liễu duy nhất của th&acirc;n c&acirc;y đ&atilde; thu hết &aacute;nh h&agrave;o quang trong l&agrave;n m&acirc;y sớm, trở n&ecirc;n rất đỗi b&igrave;nh thường.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Oa, c&oacute; cả thịt Thổ Long nữa, cho xin một miếng!&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&aacute;m trẻ đứa n&agrave;o cũng hoạt b&aacute;t hiếu động, cả l&uacute;c ăn cơm cũng kh&ocirc;ng chịu ngồi y&ecirc;n, nhiều đứa &ocirc;m b&aacute;t từ nh&agrave; m&igrave;nh ra x&uacute;m lại.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Xung quanh Thạch Th&ocirc;n c&acirc;y cỏ um t&ugrave;m v&agrave; c&oacute; nhiều th&uacute; dữ, c&oacute; điều tuy ở b&ecirc;n n&uacute;i nhưng thức ăn của d&acirc;n l&agrave;ng cũng kh&ocirc;ng qu&aacute; phong ph&uacute;, chỉ c&oacute; mấy m&oacute;n b&aacute;nh l&uacute;a mạch th&ocirc;, quả dại c&ugrave;ng ch&uacute;t thịt trong b&aacute;t lũ trẻ.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tr&ecirc;n thực tế, thiếu thốn đồ ăn lu&ocirc;n l&agrave; một vấn đề rất nghi&ecirc;m trọng của Thạch Th&ocirc;n. Trong n&uacute;i v&ocirc; c&ugrave;ng nguy hiểm, đ&aacute;m dị th&uacute; hung cầm qu&aacute; mạnh v&agrave; đ&aacute;ng sợ, mỗi lần đi săn đều c&oacute; khả năng thiệt hại về người.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nếu được chọn lựa, d&acirc;n l&agrave;ng kh&ocirc;ng ai muốn v&agrave;o trong n&uacute;i, v&igrave; v&agrave;o trong đ&oacute; c&oacute; nghĩa l&agrave; sẽ c&oacute; nguy cơ đổ m&aacute;u v&agrave; hi sinh.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thức hăn đối với họ v&ocirc; c&ugrave;ng qu&yacute; gi&aacute;, kh&ocirc;ng được ph&eacute;p l&atilde;ng ph&iacute;. Mỗi đứa trẻ từ nhỏ đ&atilde; hiểu điều n&agrave;y, đ&oacute;i, thức ăn, săn bắn, t&iacute;nh mạng, m&aacute;u tươi, tất cả đều li&ecirc;n kết với nhau.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&agrave; l&atilde;o tộc trưởng Thạch V&acirc;n Phong ở đầu th&ocirc;n được gh&eacute;p từ đ&aacute; tảng lớn, kề s&aacute;t gốc liễu gi&agrave; ch&aacute;y đen. Một thứ chất lỏng m&agrave;u trắng s&ocirc;i sung trục trong ang gốm tr&ecirc;n bếp l&ograve; trong s&acirc;n nh&agrave;, m&ugrave;i sữa thơm nức. L&atilde;o đang nấu sữa th&uacute;, thi thoảng lại bỏ v&agrave;o một ch&uacute;t dược thảo, d&ugrave;ng th&igrave;a gỗ khuấy nhẹ.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chẳng mấy chốc, &ocirc;ng l&atilde;o gọi: &quot;Nh&oacute;c con, mau lại ăn n&agrave;o.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ch&uacute; nh&oacute;c mất cha mẹ từ khi mới nửa tuổi, b&uacute; sữa b&aacute;ch th&uacute; lớn l&ecirc;n, nay đ&atilde; được một năm mấy th&aacute;ng. Nếu l&agrave; trẻ con b&igrave;nh thường lẽ ra đ&atilde; cai sữa từ l&acirc;u, nhưng n&oacute; vẫn th&iacute;ch ăn lắm, kh&ocirc;ng chịu cai, thường xuy&ecirc;n bị mấy đứa lớn ch&ecirc; cười.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;&Ocirc;i cha&hellip; ph&ugrave;&hellip;kh&ocirc;ng chạy nổi nữa.&quot; N&oacute; n&atilde;y giờ cứ mải miết đuổi theo con chim sẻ năm m&agrave;u, giờ đang thở hổn hển, ngồi phịch m&ocirc;ng xuống đất.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Nh&oacute;c con b&uacute; sữa n&agrave;o!&quot; Đ&aacute;m trẻ lớn h&ugrave;a tr&ecirc;u.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Đ&aacute;m khỉ con tụi b&acirc;y c&oacute; đứa n&agrave;o chưa từng qua độ tuổi n&agrave;y hả?&quot; L&atilde;o tộc trưởng cười mắng.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Bọn ch&aacute;u đ&acirc;u c&oacute; một tuổi rưỡi rồi m&agrave; vẫn b&uacute; sữa, hi hi.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bị đ&aacute;m trẻ lớn chọc qu&ecirc;, ch&uacute; nh&oacute;c cười ng&acirc;y ng&ocirc;, đ&ocirc;i mắt to đen l&aacute;y t&iacute;t lại th&agrave;nh h&igrave;nh trăng lưỡi liềm, ho&agrave;n to&agrave;n kh&ocirc;ng để bụng. N&oacute; ngồi xuống b&ecirc;n ang d&ugrave;ng muoi gỗ m&uacute;c sữa uống ngon l&agrave;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ăn s&aacute;ng xong, mấy người gi&agrave; lớn tuổi trong th&ocirc;n c&ugrave;ng đến nh&agrave; tộc trưởng Thạch V&acirc;n Phong, tuy r&acirc;u t&oacute;c đ&atilde; bạc phơ nhưng tinh kh&iacute; thần vẫn c&ograve;n sung t&uacute;c lắm.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Dạo n&agrave;y kh&ocirc;ng ổn lắm, nửa đ&ecirc;m hay c&oacute; bọn lớn x&aacute;c đi qua, động tĩnh qu&aacute; lớn, chắc chắn s&acirc;u trong n&uacute;i đ&atilde; xảy ra chuyện g&igrave; đ&oacute;.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Ừm, đ&ecirc;m qua t&ocirc;i bị đ&aacute;nh thức mấy lần, sởn hết xương cốt, nhất định l&agrave; c&oacute; hồng hoang hung th&uacute; đại tr&ugrave;ng đi ngang qua đ&acirc;y.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mấy người gi&agrave; lần lượt l&ecirc;n tiếng, người chau m&agrave;y kẻ trầm tư, thảo luận những dấu hiệu nguy hiểm thời gian gần đ&acirc;y, cảm thấy đ&atilde; ph&aacute;t sinh chuyện g&igrave; đ&oacute; kh&ocirc;ng tầm thường.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;T&ocirc;i thấy s&acirc;u trong Đại Hoang c&oacute; khả năng đ&atilde; xuất hiện thứ g&igrave; đ&oacute; kh&ocirc;ng tầm thường thu h&uacute;t sự ch&uacute; &yacute; của một v&agrave;i chủng tộc s&oacute;t lại từ Th&aacute;i Cổ ở quanh đ&acirc;y, khiến ch&uacute;ng lũ lượt k&eacute;o đến.&quot; L&atilde;o tộc trưởng Thạch V&acirc;n Phong trầm ng&acirc;m n&oacute;i.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;C&oacute; lẽ n&agrave;o đ&atilde; xuất hiện sơn bảo?&quot; Một &ocirc;ng l&atilde;o bỗng trợn mắt, rầu t&oacute;c dựng l&ecirc;n, mặt sửng sốt.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Những người c&ograve;n lại ai cũng lộ vẻ k&igrave; qu&aacute;i, &aacute;nh mắt hau h&aacute;u, nhưng nhanh ch&oacute;ng ngọn lửa trong mắt họ tắt vụt, đ&oacute; kh&ocirc;ng phải l&agrave; thứ m&agrave; họ c&oacute; thể đoạt được, ở s&acirc;u trong n&uacute;i, kh&ocirc;ng ai c&oacute; thể đi v&agrave;o.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bao nhi&ecirc;u năm nay chưa từng c&oacute; ai sống s&oacute;t đi ra, trong n&uacute;i c&oacute; v&ocirc; v&agrave;n lo&agrave;i đ&aacute;ng sợ, cho d&ugrave; to&agrave;n thể người d&acirc;n Thạch Th&ocirc;n c&ugrave;ng x&ocirc;ng v&agrave;o th&igrave; cũng kh&ocirc;ng ăn thua g&igrave;.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Tộc trưởng, đ&atilde; nhiều ng&agrave;y rồi ch&uacute;ng ta chưa v&agrave;o n&uacute;i.&quot; Đ&uacute;ng l&uacute;c n&agrave;y, một người đ&agrave;n &ocirc;ng vạm vỡ bước v&agrave;o nh&agrave;, anh ta l&agrave; thủ lĩnh đội săn, cũng l&agrave; tộc trưởng đời sau của Thạch Th&ocirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Dạo n&agrave;y kh&ocirc;ng được th&aacute;i b&igrave;nh cho lắm.&quot; L&atilde;o tộc trưởng Thạch V&acirc;n Phong chau m&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Nhưng thực sự sắp hết thức ăn rồi.&quot; Thạch L&acirc;m Hổ n&oacute;i, th&acirc;n h&igrave;nh y cực cao lớn, hơn hai m&eacute;t, lưng đeo một thanh đại kiếm nặng hơn ba trăm c&acirc;n, tr&ocirc;ng y giống như một người gấu, da m&agrave;u đồng, cơ bắp cuồn cuộn như từng con trăn đang di chuyển. truyện được lấy tại TruyenFull.vn</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Lũ trẻ cần ph&aacute;t triển, kh&ocirc;ng thể chịu đ&oacute;i. Phải nghĩ c&aacute;ch.&quot; C&oacute; &ocirc;ng l&atilde;o l&ecirc;n tiếng.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Tuy về đ&ecirc;m kh&ocirc;ng được y&ecirc;n ổn lắm, nhưng ban ng&agrave;y cũng kh&ocirc;ng c&oacute; điều kh&aacute;c thường n&agrave;o. T&ocirc;i dẫn th&ecirc;m v&agrave;i người, cẩn thận một ch&uacute;t chắc sẽ kh&ocirc;ng sao.&quot; Thạch L&acirc;m Hổ n&oacute;i.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cuối c&ugrave;ng, mấy chục g&atilde; thanh ni&ecirc;n trai tr&aacute;ng tụ tập lại ở đầu th&ocirc;n, do tộc trưởng Thạch V&acirc;n Phong dẫn đầu đến trước th&acirc;n c&acirc;y s&eacute;t đ&aacute;nh, nghi&ecirc;m trang cầu khấn trước gốc liễu gi&agrave;.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Tế Linh, xin h&atilde;y bảo hộ tộc nh&acirc;n, để lũ trẻ săn được thịt th&uacute; b&eacute;o tốt, b&igrave;nh an trở về. Ch&uacute;ng t&ocirc;i sẽ d&acirc;ng hi&ecirc;n tr&aacute;i tim th&agrave;nh k&iacute;nh, đời đời c&uacute;ng tế phụng dưỡng người.&quot;</span></span></p>\r\n\r\n<hr />', 1, '2022-03-16 03:10:53', '2022-03-16 03:11:17'),
(11, 5, 'Chương 3: Cốt văn', 'chuong-3-cot-van', '<p>gsdgdhdh</p>', '<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Trong khi tộc trưởng v&agrave; c&aacute;c vị b&ocirc; l&atilde;o cầu khấn, tất cả thanh ni&ecirc;n trai tr&aacute;ng ai cũng lộ vẻ trịnh trọng, tiến h&agrave;nh lễ b&aacute;i. Kh&ocirc;ng &iacute;t phụ nữ cũng đến lặng lẽ cầu nguyện, cầu cho người th&acirc;n của m&igrave;nh được trở về an to&agrave;n sau chuyến đi săn.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Trong n&uacute;i qu&aacute; nguy hiểm, rời khỏi th&ocirc;n l&agrave;ng được c&acirc;y liễu gi&agrave; bảo hộ, b&ecirc;n ngo&agrave;i sẽ l&agrave; một thế giới ho&agrave;n to&agrave;n kh&aacute;c, ở đ&oacute; đầy rẫy những con m&atilde;nh cầm v&agrave; cự th&uacute; v&ocirc; c&ugrave;ng đ&aacute;ng sợ.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">V&agrave; rồi cứ thế, một to&aacute;n người khỏe mạnh nhất th&ocirc;n v&aacute;c cung t&ecirc;n lớn tr&ecirc;n vai, mang theo đại kiếm l&ecirc;n đường tiến v&agrave;o trong chốn n&uacute;i s&acirc;u rừng rậm, một luồng hơi thở của đại hoang tức th&igrave; ập tới.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">D&otilde;i mắt tiễn đội săn ra đi, l&atilde;o tộc trưởng Thạch V&acirc;n Phong dẫn lũ trẻ về lại b&atilde;i cỏ đầu th&ocirc;n, khoanh ch&acirc;n ngồi xuống n&oacute;i: &quot;Được rồi, đ&aacute;m khỉ con c&aacute;c ch&aacute;u cũng cần chăm chỉ học tập đ&oacute;.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Đ&aacute;m trẻ lập tức xị mặt, đứa n&agrave;o đứa nấy kh&ocirc;ng c&ograve;n hứng th&uacute; g&igrave; hết, miễn cưỡng ngồi quanh l&atilde;o, ỉu x&igrave;u như một mớ l&aacute; c&acirc;y phơi h&eacute;o.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Tộc trưởng gia gia, mấy chữ k&igrave; cục đ&oacute; phức tạp như b&ugrave;a bắt ma ấy, kh&oacute; học lắm, phải dụng t&acirc;m ghi nhớ l&agrave;m g&igrave; ạ?&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Đ&uacute;ng thế, c&ograve;n chẳng hữu &iacute;ch bằng tiễn ph&aacute;p cha dạy ch&aacute;u!&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Đ&aacute;m trẻ to&agrave;n bộ đều xịu mặt, c&oacute; vẻ rất ch&aacute;n gh&eacute;t.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Lũ nh&oacute;c c&aacute;c ch&aacute;u thật kh&ocirc;ng hiểu chuyện, cốt văn l&agrave; những k&iacute; tự hiển h&oacute;a bẩm sinh tr&ecirc;n xương cốt của một chủng tộc mạnh mẽ thời Th&aacute;i Cổ, ẩn chứa sức mạnh thần b&iacute; kh&oacute; d&ograve;, bao nhi&ecirc;u người muốn học c&ograve;n kh&ocirc;ng c&oacute; đường học đ&oacute;. Chỉ cần học th&agrave;nh t&agrave;i, kh&ocirc;ng biết c&ograve;n mạnh hơn thế hệ cha ch&uacute; c&aacute;c ch&aacute;u bao nhi&ecirc;u lần đ&acirc;u.&quot; L&atilde;o tộc trưởng ng&aacute;n ngẩm ch&ecirc; tr&aacute;ch ch&uacute;ng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Tộc trưởng gia gia, &ocirc;ng h&atilde;y biểu diễn cho ch&uacute;ng ch&aacute;u xem thử sức mạnh của cốt văn đi.&quot; Một đứa nh&oacute;c choai choai l&ecirc;n tiếng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Nh&oacute;c t&igrave; lại đ&acirc;y.&quot; L&atilde;o tộc trưởng gọi với ra xa.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Ch&uacute; nh&oacute;c đ&atilde; đuổi con chim sẻ năm m&agrave;u xong, đang ra sức giựt đu&ocirc;i một con ch&oacute; v&agrave;ng lớn, nghe tiếng liền ngơ ng&aacute;c quay đầu, bu&ocirc;ng thay lon ton chạy tới, chớp chớp đ&ocirc;i mắt s&aacute;ng ngời: &quot;Ui cha ui cha, tộc trưởng gia gia c&oacute; chuyện g&igrave; ạ?&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;H&atilde;y đem cốt văn ta dạy cho ch&aacute;u sử dụng ra xem.&quot; Thạch V&acirc;n Phong n&oacute;i.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;V&acirc;ng ạ.&quot; Nh&oacute;c tỳ rất v&acirc;ng lời, n&oacute; giơ hai b&agrave;n tay b&eacute; x&iacute;u, ngậm miệng lại, to&agrave;n th&acirc;n kh&ocirc;ng ngừng d&ugrave;ng sức, khu&ocirc;n mặt nhỏ nhắn đỏ bừng l&ecirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;B&ugrave;m&quot; một tiếng, l&ograve;ng b&agrave;n tay n&oacute; xuất hiện một quầng s&aacute;ng, một k&iacute; tự k&igrave; lạ nổi l&ecirc;n, giống như được đ&uacute;c bằng kim loại, b&oacute;ng bẩy v&agrave; thực chất. Liền đ&oacute;, tr&ecirc;n b&agrave;n tay kia cũng xuất hiện.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Nh&oacute;c t&igrave; bước l&ecirc;n hai bước, nhấc bổng một khối đ&aacute; xanh c&ograve;n cao hơn cả m&igrave;nh l&ecirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Lợi hại qu&aacute;!&quot; Lũ trẻ reo l&ecirc;n kinh ngạc, một đứa b&eacute; mới hơn một tuổi, l&agrave;m thế n&agrave;o m&agrave; nhấc được một h&ograve;n đ&aacute; lớn ngần n&agrave;y?</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Nh&oacute;c tỳ, c&oacute; phải đệ đ&atilde; d&ugrave;ng hết cả sức b&uacute; sữa ra rồi kh&ocirc;ng?&quot; Mấy đứa lớn tr&ecirc;u.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Ui cha, v&acirc;ng ạ, d&ugrave;ng hết sạch cả rồi.&quot; Nh&oacute;c tỳ n&eacute;m h&ograve;n đ&aacute; xuống, ngồi phịch ra nền cười rất v&ocirc; tư thuần khiết, k&iacute; tự tr&ecirc;n tay n&oacute; cũng nhanh ch&oacute;ng mờ dần rồi biến mất.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Tộc trưởng gia gia, đ&acirc;y ch&iacute;nh l&agrave; sức mạnh của cốt văn thần b&iacute; m&agrave; &ocirc;ng nghi&ecirc;n cứu suốt mười mấy năm nay ư?&quot; Lũ trẻ s&aacute;ng mắt l&ecirc;n, ho&agrave;n to&agrave;n kh&aacute;c hẳn vẻ ủ &ecirc; l&uacute;c n&atilde;y.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Đừng hưng phấn vội, những thứ n&agrave;y chỉ c&oacute; thể đưa c&aacute;c ch&aacute;u l&ecirc;n đường m&agrave; th&ocirc;i, so với Thi&ecirc;n Cốt Văn xuất hiện trong truyền thuyết thời cổ đại th&igrave; c&ograve;n k&eacute;m xa nhiều lắm.&quot; &Ocirc;ng l&atilde;o gật đầu rồi lại lắc đầu.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Tộc trưởng gia gia, &ocirc;ng kể chuyện thế giới b&ecirc;n ngo&agrave;i cho ch&uacute;ng ch&aacute;u nghe đi.&quot; Đ&aacute;m trẻ tỏ vẻ h&aacute;o hức.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Người trong th&ocirc;n ai cũng biết l&atilde;o tộc trưởng hồi c&ograve;n trẻ từng c&ugrave;ng với mười mấy vị tộc nh&acirc;n cường đại trong th&ocirc;n đi đến tận c&ugrave;ng mặt đất xa x&ocirc;i, từng x&ocirc;ng pha ở thế giới b&ecirc;n ngo&agrave;i.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Thế nhưng mười mấy năm trước, chỉ c&oacute; hai người to&agrave;n th&acirc;n đẫm m&aacute;u trở về, một trong số đ&oacute; kh&ocirc;ng l&acirc;u sau th&igrave; chết, chỉ c&oacute; một m&igrave;nh Thạch V&acirc;n Phong sống s&oacute;t.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Mấy năm nay, l&atilde;o vẫn lu&ocirc;n nghi&ecirc;n cứu cốt văn thần b&iacute;, thi thoảng lại lấy mấy người c&oacute; thể chất khỏe mạnh trong th&ocirc;n ra l&agrave;m th&iacute; nghiệm. Lũ trẻ biết r&otilde;, những bậc cha ch&uacute; khỏe như rồng như hổ của ch&uacute;ng mỗi lần bị gọi đến căn nh&agrave; đ&aacute; kia đều ph&aacute;t ra những tiếng g&agrave;o th&ecirc; thảm đến r&ugrave;ng m&igrave;nh, khiến lũ trẻ n&agrave;y từ b&eacute; đ&atilde; nảy sinh l&ograve;ng sợ h&atilde;i c&ugrave;ng lảng tr&aacute;nh.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Đến tận dạo gần đ&acirc;y việc nghi&ecirc;n cứu của l&atilde;o tộc trưởng mới gi&atilde;n ra, nhờ đ&oacute; m&agrave; d&acirc;n l&agrave;ng c&ugrave;ng đỡ sợ. Th&ecirc;m nữa, nh&oacute;c tỳ b&uacute; sữa b&aacute;ch th&uacute; v&agrave; ăn chực trăm nh&agrave; được l&atilde;o nhận nu&ocirc;i, trở th&agrave;nh đối tượng nghi&ecirc;n cứu tốt nhất.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Thế giới b&ecirc;n ngo&agrave;i ư&hellip;&quot; L&atilde;o tộc trưởng lộ vẻ hồi ức, sau một tho&aacute;ng xuất thần mới n&oacute;i: &quot;Thế giới b&ecirc;n ngo&agrave;i qu&aacute; lớn, b&aacute;t ng&aacute;t v&ocirc; bờ, từ v&ugrave;ng n&agrave;y qua v&ugrave;ng kh&aacute;c l&agrave; h&agrave;ng trăm vạn dặm, kh&ocirc;ng ai biết ch&iacute;nh x&aacute;ch rộng bao nhi&ecirc;u, một người c&oacute; đi cả đời m&igrave;nh cũng kh&ocirc;ng ra khỏi một v&ugrave;ng. Đại hoang m&ecirc;nh m&ocirc;ng v&ocirc; tận, lo&agrave;i người ở những v&ugrave;ng kh&aacute;c nhau rất kh&oacute; c&oacute; thể li&ecirc;n lạc với nhau v&igrave; thực sự qu&aacute; nguy hiểm. Tr&ecirc;n mặt đất c&oacute; v&ocirc; v&agrave;n chủng tộc mạnh mẽ, đ&aacute;ng sợ v&agrave; thần b&iacute;, cho d&ugrave; l&agrave; bộ lạc mấy chục vạn người hoặc một cự th&agrave;nh h&ugrave;ng vỹ cũng c&oacute; khả năng bị mấy con th&uacute; thuộc chủng tộc s&oacute;t lại từ thời th&aacute;i cổ ph&aacute; hủy tan t&agrave;nh. Đương nhi&ecirc;n, cũng c&oacute; lo&agrave;i người mạnh đến mức kh&ocirc;ng thể n&agrave;o tưởng tượng, kh&ocirc;ng hề thua k&eacute;m chiến lực tuyệt đỉnh của những chủng tộc kh&aacute;c, thần uy v&ocirc; địch, xứng danh bậc thi&ecirc;n ki&ecirc;u của lo&agrave;i người.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Đ&aacute;m trẻ sinh l&ograve;ng k&iacute;nh sợ, đồng thời cũng v&ocirc; c&ugrave;ng khao kh&aacute;t, thấy t&ograve; m&ograve; trước thế giới m&igrave;nh kh&ocirc;ng biết. C&oacute; đứa hỏi: &quot;N&uacute;i s&ocirc;ng n&agrave;y c&oacute; địa bảo ti&ecirc;n dược g&igrave; c&oacute; thể khiến người ta tho&aacute;t thai ho&aacute;n cốt sau một đ&ecirc;m kh&ocirc;ng? Thi&ecirc;n ki&ecirc;u lợi hại nhất lo&agrave;i người uy thế lớn tới cỡ n&agrave;o ạ?&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&Ocirc;ng l&atilde;o cười n&oacute;i: &quot;Muốn biết th&igrave; h&atilde;y khiến bản th&acirc;n m&igrave;nh mạnh l&ecirc;n trước đ&atilde;.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Nếu ch&uacute;ng ch&aacute;u nắm vững sức mạnh thần b&iacute; của cốt văn th&igrave; sẽ c&oacute; thể đi x&ocirc;ng pha khắp thi&ecirc;n hạ được phải kh&ocirc;ng ạ?&quot; Mấy đứa nh&oacute;c lộ vẻ mơ m&agrave;ng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Thạch V&acirc;n Phong xoa đầu một đứa trẻ, đ&aacute;p: &quot;Chưa n&oacute;i đến c&aacute;c v&ugrave;ng kh&aacute;c, chỉ một v&ugrave;ng của ch&uacute;ng ta đ&acirc;y th&ocirc;i, nếu c&oacute; ai đi hết một nửa cương thổ đ&atilde; t&agrave;i giỏi lắm rồi!&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Lũ trẻ to&agrave;n bộ đều ngẩn người.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Điều ta c&oacute; thể l&agrave;m chỉ l&agrave; dẫn c&aacute;c ch&aacute;u l&ecirc;n đường, c&ograve;n sau n&agrave;y đi được đến bước n&agrave;o đều phải dựa v&agrave;o bản th&acirc;n c&aacute;c ch&aacute;u. Những thứ ta dạy c&aacute;c ch&aacute;u chắc cũng kh&ocirc;ng thua k&eacute;m g&igrave; lũ trẻ đồng trang lứa ở thế giới b&ecirc;n ngo&agrave;i.&quot; &Ocirc;ng l&atilde;o n&oacute;i hết c&acirc;u, &aacute;nh mắt lộ ra một thứ &aacute;nh s&aacute;ng kh&aacute;c thường, tay sờ miếng ngọc cốt k&igrave; dị cất trong ngực &aacute;o.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Lũ trẻ ngồi quanh l&atilde;o tộc trưởng, cuối c&ugrave;ng cũng trật tự chăm ch&uacute; nghe lời dạy bảo cho đến tận trưa mới giải t&aacute;n ra về.&nbsp;xem tại TruyenFull.vn</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Kh&oacute; qu&aacute; đi, tộc trưởng kh&ocirc;ng ngờ lại n&oacute;i phải mất mấy năm mới c&oacute; v&agrave;i người c&aacute; biệt h&oacute;a được cốt văn v&agrave;o trong cơ thể, c&ograve;n đại đa số sẽ m&atilde;i m&atilde;i kh&ocirc;ng thể th&agrave;nh c&ocirc;ng.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Nhưng nh&oacute;c tỳ mới nhỏ x&iacute;u như hạt đậu, đệ ấy l&agrave;m được kia m&agrave;.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Ch&uacute; nh&oacute;c chớp mắt ng&acirc;y thơ v&ocirc; tội, rồi tiếp tục đi giựt đu&ocirc;i con ch&oacute; v&agrave;ng kia, con ch&oacute; sủa u&ocirc;ng u&ocirc;ng c&ograve;n v&ocirc; tội hơn nữa.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Mặt trời ngả b&oacute;ng, trong &aacute;nh tịch dương, cả Thạch Th&ocirc;n như bị nhuộm trong một tầng &aacute;nh s&aacute;ng m&agrave;u v&agrave;ng nhạt. Xa xa vẳng đến tiếng hổ k&ecirc;u vượn h&uacute;, c&ograve;n những ng&ocirc;i nh&agrave; đ&aacute; ở đ&acirc;y lại y&ecirc;n b&igrave;nh tựa như những t&ograve;a thấn miếu từ thời viễn cổ.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Mấy chục người xuất hiện ở đường ch&acirc;n trời, &aacute;nh chiều t&agrave; k&eacute;o b&oacute;ng họ d&agrave;i l&ecirc; th&ecirc;, th&acirc;n h&igrave;nh họ bị r&aacute;ng chiều phủ l&ecirc;n từng đường viền lấp l&aacute;nh tr&ocirc;ng cao lớn v&agrave; h&ugrave;ng vĩ v&ocirc; c&ugrave;ng. Gần như ai cũng k&eacute;o theo một con m&atilde;nh th&uacute; khổng lồ, căng đầy thu hoạch.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Về rồi!&quot; Đ&aacute;m phụ nữ đứng đợi ở đầu th&ocirc;n từ rất l&acirc;u rồi, họ reo l&ecirc;n vui sướng, nỗi bất an sợ h&atilde;i trong l&ograve;ng biến mất ngay lập tức. Họ cất tiếng gọi vang.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Cha lũ trẻ b&igrave;nh an trở về rồi!&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Trời ơi, kh&ocirc;ng ngờ lại săn được nhiều đến thế, quả l&agrave; vụ thu hoạch lớn chưa từng c&oacute;!&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Chuyến đi săn n&agrave;y hết sức th&agrave;nh c&ocirc;ng, mấy chục người đ&agrave;n &ocirc;ng ai cũng c&oacute; thu hoạch, trong đống th&uacute; săn c&oacute; voi sừng rồng khổng lồ, c&oacute; con Quỳ Th&uacute; một ch&acirc;n h&igrave;nh dạng giống con tr&acirc;u, c&ograve;n c&oacute; cả con Phi M&atilde;ng mọc c&aacute;nh to như th&ugrave;ng nước&hellip;.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">B&ocirc; l&atilde;o trong th&ocirc;n ai cũng ngạc nhi&ecirc;n, những sinh vật n&agrave;y ng&agrave;y thường rất kh&oacute; đối ph&oacute;, c&oacute; những con được coi l&agrave; hung th&uacute;, vậy m&agrave; nay lại bị liệp s&aacute;t nhiều như vậy, m&aacute;u loang lốm đốm, thực sự nằm ngo&agrave;i &yacute; liệu của mọi người.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">V&iacute; dụ như con voi sừng rồng kia, th&acirc;n voi cứng như sắt, ngọn gi&aacute;o sắt kh&oacute; m&agrave; chọc thủng, cặp sừng rồng sắc nhọn như lưỡi dao kim cương, dễ d&agrave;ng đập vụn đ&aacute; tảng th&agrave;nh bột phấn. C&ograve;n con Quỳ Th&uacute; nọ tiếng vang như sấm, nếu ở gần c&oacute; thể chấn chết người ta. Con Phi M&atilde;ng mọc c&aacute;nh l&agrave; s&aacute;t thủ sơn l&acirc;m, c&oacute; thể bất th&igrave;nh l&igrave;nh nh&agrave;o xuống từ tr&ecirc;n đỉnh n&uacute;i, cực k&igrave; đ&aacute;ng sợ.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Trong mớ thu hoạch c&ograve;n c&oacute; cả những sinh vật lợi hại hơn nữa, v&iacute; như con Hỏa T&ecirc; hai đầu to&agrave;n th&acirc;n đỏ rực, con T&igrave; Hưu huyết mạch kh&ocirc;ng thuần chủng &hellip; ch&uacute;ng đều l&agrave; những lo&agrave;i hung th&uacute; trứ danh, ph&aacute;t hiện ra ch&uacute;ng đều cần tr&aacute;nh xa đi đường v&ograve;ng n&eacute; tr&aacute;nh, vậy m&agrave; nay đều bị liệp s&aacute;t rồi, điều n&agrave;y v&ocirc; c&ugrave;ng tr&aacute;i với lẽ thường!</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Chuyến n&agrave;y thực sự v&ocirc; c&ugrave;ng may mắn, cả đo&agrave;n ai cũng đầy t&uacute;i trở về m&agrave; kh&ocirc;ng ai bị thương.&quot; Thạch L&acirc;m Hổ thủ lĩnh đội săn cất tiếng cười sảng kho&aacute;i giải th&iacute;ch với tộc trưởng v&agrave; d&acirc;n l&agrave;ng. Mấy đ&ecirc;m vừa rồi, trong n&uacute;i c&oacute; cự th&uacute; si&ecirc;u cấp đi qua khiến đất trời rung chuyển, dẫm chết v&agrave; bị thương v&ocirc; số th&uacute; rừng. Ban ng&agrave;y họ lần theo giết chết kh&ocirc;ng &iacute;t hung th&uacute; trọng thương, ch&uacute;ng đều l&agrave; những sinh vật cường ho&agrave;nh m&agrave; ng&agrave;y thường d&acirc;n l&agrave;ng phải n&eacute; tr&aacute;nh.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Trong n&uacute;i c&oacute; mấy dấu ch&acirc;n lớn h&igrave;nh dạng giống ch&acirc;n người, nhưng thực sự qu&aacute; lớn, d&agrave;i đến gần trăm m&eacute;t!&quot;</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">&quot;Lớn vậy sao?&quot; D&acirc;n l&agrave;ng trầm trồ, đ&acirc;y đ&uacute;ng l&agrave; một tin đ&aacute;ng sợ.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Ngay cả c&aacute;c b&ocirc; l&atilde;o nghe n&oacute;i đều th&oacute;t dạ, c&agrave;ng th&ecirc;m chắc chắn s&acirc;u trong n&uacute;i đ&atilde; ph&aacute;t sinh chuyện bất thường thu h&uacute;t những chủng tộc s&oacute;t lại từ Th&aacute;i Cổ ở xung quanh Đại Hoang t&igrave;m đến.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Mặc kệ n&oacute;i thế n&agrave;o th&igrave; thu hoạch lần n&agrave;y cũng khiến to&agrave;n thể tộc nh&acirc;n mừng rỡ, Thạch Th&ocirc;n tr&agrave;n ngập tiếng cười đ&ugrave;a của trẻ con, bầu kh&ocirc;ng kh&iacute; khắp nơi đều vui sướng.</span></span></p>\r\n\r\n<p><span style=\"font-family:Arial,Helvetica,sans-serif\"><span style=\"font-size:26px\">Tộc trưởng Thạch V&acirc;n Phong dẫn mọi người đi về ph&iacute;a gốc liễu gi&agrave;, v&aacute;c theo x&aacute;c mấy chục con th&uacute;, đặt to&agrave;n bộ lũ hung th&uacute; d&iacute;nh đầy vết m&aacute;u l&ecirc;n tr&ecirc;n bục đ&aacute;, r&otilde; r&agrave;ng đ&acirc;y l&agrave; một đ&agrave;i tế cỡ lớn.</span></span></p>', 1, '2022-03-16 03:12:27', '2022-03-16 03:12:27'),
(14, 7, 'Chương 1: Thế Giới Mới', 'chuong-1-the-gioi-moi', '<p>afaeaef</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trong một bệnh viện c&oacute; hai người đang tại trong ph&ograve;ng bệnh, một nam một nữ, người nam nằm tr&ecirc;n giường bệnh băng b&oacute; khắp người nh&igrave;n c&oacute; vẻ sắp ra đi, người nữ ở b&ecirc;n cạnh kh&oacute;c.<br />\r\n<br />\r\nNữ nh&acirc;n vừa kh&oacute;c vừa n&oacute;i:&quot; tại sao ngươi lại phải l&agrave;m như thế r&otilde; r&agrave;ng ngươi c&oacute; thể kh&ocirc;ng cần đẩy ta ra? người cũng sẽ kh&ocirc;ng bị xe đ&acirc;m tr&uacute;ng&quot;<br />\r\nNam nh&acirc;n yếu ớt n&oacute;i: &quot; đ&acirc;y l&agrave; ta n&ecirc;n l&agrave;m phải kh&ocirc;ng? ngươi c&ugrave;ng ta đều sắp kết h&ocirc;n ai nghĩ tới sẽ xảy ra chuyện như thế n&agrave;y, xin lỗi rồi &oacute;c lẽ ta sắp kh&ocirc;ng được phải bỏ lại ngươi ở thế giới n&agrave;y ta cũng rất &aacute;y n&aacute;y, nếu kiếp sau c&oacute; thể gặp lại ch&uacute;ng ta sẽ lần nữa y&ecirc;u nh&atilde;, ch&uacute;ng ta sẽ kh&ocirc;ng bao giờ phải chia t&aacute;ch khỏi nhau nữa&quot;<br />\r\nN&oacute;i xong nam nh&acirc;n cũng kh&ocirc;ng c&ograve;n thở nằm bất động tr&ecirc;n giường bệnh m&aacute;y đo nhịp tim cũng về 0.<br />\r\n<br />\r\nNữ nh&acirc;n hoảng sợ kh&oacute;c rống l&ecirc;n:&quot; ngươi l&agrave;m sao kh&ocirc;ng n&oacute;i nữa, hu hu! &quot;<br />\r\nNam nh&acirc;n lần nữa mở mắt ra, hắn cảm gi&aacute;c m&igrave;nh kh&ocirc;ng c&ograve;n th&acirc;n thể nữa, chẳng lẽ hắn đ&atilde; tới địa ngục, xung quanh l&agrave; một v&ugrave;ng tối tam chỉ c&oacute; ph&iacute;a xa kia một tia s&aacute;ng đang c&agrave;ng ng&agrave;y c&agrave;ng s&aacute;ng tỏ lao nhanh về ph&iacute;a hắn tia s&aacute;ng c&ugrave;ng hắn dung hợp lại với nhau, hắn cũng lần nữa l&acirc;m v&agrave;o ngủ say.<br />\r\n<br />\r\nNam nh&acirc;n h&igrave;nh dạng cũng kh&ocirc;ng x&aacute;c định h&oacute;a th&agrave;nh tia s&aacute;ng lao nhanh đi.<br />\r\n<br />\r\nMa ph&aacute;p thế giới, B&aacute;c th&agrave;nh<br />\r\nNam nh&acirc;n kia đ&atilde; ở thế giới n&agrave;y được 16 năm hắn t&ecirc;n Mạc Thi&ecirc;n , gia đ&igrave;nh hắn c&ograve;n c&oacute; 3 người kh&aacute;c l&agrave; cha hắn Mạc Gia Hưng, một đệ đệ song b&agrave;o thai l&agrave; Mạc Ph&agrave;m, c&ugrave;ng một người em g&aacute;i nu&ocirc;i t&ecirc;n Diệp T&acirc;m Hạ.<br />\r\n<br />\r\n<br />\r\nL&uacute;c mới sinh ra, Mạc Thi&ecirc;n cũng rất bất ngờ kh&ocirc;ng nghĩ tới hắn vậy m&agrave; cũng kh&ocirc;ng c&oacute; chết c&ograve;n xuy&ecirc;n qua tiểu thuyết thế giới m&agrave; trước đ&acirc;y hắn đ&atilde; từng đọc &quot;To&agrave;n chức ph&aacute;p sư&quot;, l&uacute;c mới ho&agrave;n to&agrave;n x&aacute;c định r&otilde; r&agrave;ng thế giới n&agrave;y hắn cũng chỉ mới 4 tuổi Mạc Thi&ecirc;n cũng rất hoảng sợ<br />\r\nMẹ n&oacute; thế giới n&agrave;y qu&aacute; nguy hiểm, y&ecirc;u ma ho&agrave;nh h&agrave;nh khắp nơi, mặc d&ugrave; nh&acirc;n loại c&oacute; kết giới bảo vệ c&ugrave;ng x&acirc;y dựng n&ecirc;n th&agrave;nh thị nhưng hắn l&agrave; người xuy&ecirc;n việt thế giới n&agrave;y kh&ocirc;ng n&oacute;i hiểu r&otilde; to&agrave;n bộ nhưng m&agrave; những g&igrave; hắn biết cũng đủ để cho hắn cảm thấy c&oacute; ch&uacute;t tuyệt vọng, mặc d&ugrave; biết l&atilde;o đệ tương lai l&agrave; ph&aacute;p thần cảnh, nhưng hắn cũng l&agrave; một người v&ocirc; c&ugrave;ng v&ocirc; c&ugrave;ng v&ocirc; c&ugrave;ng (chuyện quan trọng n&oacute;i 3 lần) biết t&igrave;m đường chết.<br />\r\n<br />\r\nL&agrave;m th&acirc;n nh&acirc;n của hắn cho d&ugrave; l&agrave; người b&igrave;nh thường như Mạc Gia Hưng cũng mấy lần bị li&ecirc;n lụy v&agrave;o.<br />\r\n<br />\r\nHơn nữa mặc d&ugrave; hắn c&oacute; thể th&agrave;nh ph&aacute;p thần nhưng đ&oacute; l&agrave; chuyện của hơn 20 năm sau, c&oacute; như vậy l&atilde;o đệ thất khiến hắn nhức cả trứng.<br />\r\n<br />\r\nCũng may năm 4 tuổi l&uacute;c hắn cũng c&oacute; một ph&uacute;c lợi khi xuy&ecirc;n qua thế giới n&agrave;y l&agrave; một c&aacute;i mặt ngo&agrave;i số liệu.<br />\r\n<br />\r\nMặt Ngo&agrave;i số liệu<br />\r\nMạc Thi&ecirc;n<br />\r\nTuổi 16<br />\r\nTốc độ tu luyện: 5 điểm mỗi giời<br />\r\nTu vi: kh&ocirc;ng<br />\r\ntinh thần cảnh giới: 0/500 [1 cấp] [ tốc độ 5 điểm mỗi giờ]<br />\r\nMa kh&iacute;: kh&ocirc;ng<br />\r\nLinh chủng: kh&ocirc;ng<br />\r\nHồn chủng: kh&ocirc;ng<br />\r\nThi&ecirc;n chủng : kh&ocirc;ng<br />\r\nTrời sinh thi&ecirc;n ph&uacute;: Trời sinh song hệ[ chờ thức tỉnh]<br />\r\nĐiểm tu luyện : 43830 điểm<br />\r\n[ tu luyện kh&ocirc;ng c&oacute; b&igrave;nh cảnh chỉ cần c&oacute; đủ điểm tu luyện c&oacute; thể l&ecirc;n bất cứ cấp bậc n&agrave;o, điểm tu vi c&agrave;ng cao sẽ quyết định cấp bậc ma ph&aacute;p đạt đến, chửng khống chấm nhỏ phụ thuộc v&agrave;o phần trăm điểm tu vi, kh&ocirc;ng thể cường h&oacute;a ng&ocirc;i sao bằng tinh ph&aacute;ch nhưng c&oacute; thể chuyển th&agrave;nh điểm tu luyện, kh&ocirc;ng thể l&ecirc;n cấp bậc ph&aacute;p sư khi tất cả c&aacute;c hệ chưa m&atilde;n tu]<br />\r\n[Tốc độ tu luyện phụ thuộc v&agrave;o ma kh&iacute;, tinh thần cảnh giới]<br />\r\n[muốn th&ecirc;m điểm tinh thần cảnh giới cần phải l&agrave;m ph&aacute;p sư đủ cấp bậc Sơ giai tối đa cấp 3 Trung giai tối đa cấp 6 Cao giai tối đa cấp 9 Si&ecirc;u giai tối đa cấp 12 ( sau cấp 12 l&agrave; thần cấp)]<br />\r\n[ Sơ gia trở xuống c&oacute; thể minh tu 2 giờ/ng&agrave;y, trung giai 3 giờ/ng&agrave;y , cao giai tự động tu luyện 4 giờ / ng&agrave;y, si&ecirc;u giai tư động 5 giờ /ng&agrave;y, cấm ch&uacute; tư động 10iờ /ng&agrave;y ph&aacute;p thần tư động cả ng&agrave;y]<br />\r\n12 năm n&agrave;y hắn mỗi ng&agrave;y đều minh tu 2 giờ bất kể ra sao tối v&agrave;o l&uacute;c cả nh&agrave; đi ngủ hắn đều tại ph&ograve;ng minh tu mới c&oacute; được số liệu xa hoa như vậy điểm tu luyện.<br />\r\n<br />\r\n<br />\r\nV&igrave; vậy hắn c&oacute; l&ograve;ng tin rất lớn c&oacute; thể trở th&agrave;nh thế giới n&agrave;y đỉnh hơn nữa c&ograve;n chưa hẳn kh&ocirc;ng thể so với l&atilde;o đệ hắn, vừa vặn h&ocirc;m nay cũng vừa l&uacute;c l&agrave; ng&agrave;y thức tỉnh ma ph&aacute;p tại cao trung, Mạc phầm lần n&agrave;y cũng kh&ocirc;ng cần phải đi cửa sau, d&ugrave; sao c&oacute; hắn ở đ&acirc;y l&agrave;m sao lại để cho l&atilde;o ba hắn đi quỳ gối cầu xin gi&uacute;p đỡ của t&ecirc;n Mục Hạ kia.<br />\r\n<br />\r\nMặc d&ugrave; l&agrave; thế hắn vẫn l&agrave; đứng ở vị tr&iacute; thứ nhất từ dưới l&ecirc;n, mẹ n&oacute; điểm v&agrave;o cao trung l&agrave; 50,Mạc Ph&agrave;m t&ecirc;n kia d&ugrave; c&oacute; học b&aacute; m&igrave;nh đ&acirc;y k&egrave;m cặp đốc th&uacute;c từ b&eacute; vậy m&agrave; vẫn c&oacute; thể su&yacute;t ch&uacute;t nữa xe tuột x&iacute;ch kh&ocirc;ng thi đậu cao trung.<br />\r\n<br />\r\nThi&ecirc;n Lan ma ph&aacute;p cao trung<br />\r\nL&atilde;o hiệu trưởng tiếng n&oacute;i vừa ra, tr&ecirc;n b&atilde;i tập c&aacute;c học sinh đ&atilde; k&igrave;m n&eacute;n kh&ocirc;ng được nội t&acirc;m vui sướng.<br />\r\n<br />\r\nD&ugrave; sao, kh&ocirc;ng c&oacute; ai kh&ocirc;ng muốn điều khiển liệt diễm tr&agrave;n ngập ph&aacute; huỷ chi lực, kh&ocirc;ng c&oacute; ai kh&ocirc;ng muốn chưởng khống h&agrave;n băng đ&ocirc;ng cứng hết thảy gian &aacute;c, kh&ocirc;ng c&oacute; ai kh&ocirc;ng muốn khống chế đại kh&iacute; rong ruổi thi&ecirc;n địa!.<br />\r\n<br />\r\n.<br />\r\n<br />\r\nĐương nhi&ecirc;n, vui sướng đồng thời, cũng kh&ocirc;ng &iacute;t người đang lo lắng!.<br />\r\n<br />\r\nLo nghĩ ch&iacute;nh m&igrave;nh thức tỉnh c&aacute;i quang hệ, Thủy hệ, hai c&aacute;i n&agrave;y phế hệ đ&atilde; thức tỉnh sau đ&oacute;, đ&oacute; nhất định ch&iacute;nh l&agrave; muốn mạng a!<br />\r\nChung quy l&agrave; địa phương nhỏ, theo bọn hắn nghĩ, chỉ c&oacute; đ&atilde; thức tỉnh Hỏa hệ mới l&agrave; ma ph&aacute;p sư.<br />\r\n<br />\r\nD&ugrave; sao, bọn hắn chỉ l&agrave; thấy được Hỏa hệ tiền kỳ cường h&atilde;n, lại l&agrave; kh&ocirc;ng biết, bất luận l&agrave; quang hệ vẫn l&agrave; Thủy hệ, chỉ cần c&oacute; thể ki&ecirc;n tr&igrave; khổ tu đến cao giai, si&ecirc;u giai sau đ&oacute;, đều l&agrave; giống nhau cường đại!<br />\r\n<br />\r\nĐương nhi&ecirc;n, sơ giai thức tỉnh, số đ&ocirc;ng cũng l&agrave; nguy&ecirc;n tố ma ph&aacute;p.<br />\r\n<br />\r\nM&agrave; nguy&ecirc;n tố trong ma ph&aacute;p, so Hỏa hệ c&agrave;ng th&ecirc;m hung m&atilde;nh, liền ch&iacute;nh l&agrave; L&ocirc;i hệ.<br />\r\n<br />\r\nThế nhưng l&agrave; L&ocirc;i hệ tại sơ giai, vậy th&igrave; thật l&agrave; ng&agrave;n dặm thậm ch&iacute; l&agrave;!.<br />\r\n<br />\r\nNg&agrave;n dặm mới t&igrave;m được một.<br />\r\n<br />\r\nThức tỉnh l&agrave; 20 lớp đồng thời tiến h&agrave;nh.<br />\r\n<br />\r\nMạc Ph&agrave;m l&agrave; 48 h&agrave;o, hắn ch&iacute;nh l&agrave; to&agrave;n lớp cái cu&ocirc;́i cùng đi tiếp thu thức tỉnh ban cho.</span></span></p>', 1, '2022-03-16 03:16:49', '2022-03-16 03:16:49');
INSERT INTO `chapters` (`id`, `book_id`, `name`, `slug`, `description`, `content`, `active`, `created_at`, `updated_at`) VALUES
(15, 7, 'Chương 2: Thức Tỉnh', 'chuong-2-thuc-tinh', '<p>qadfasdfsa</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đứng đầu l&agrave; Mạc Thi&ecirc;n, hắn d&ugrave; sao kiếp trước vẫn l&agrave; một c&aacute;i học b&aacute;, b&acirc;y giờ học một ch&uacute;t kiến thức mới so với những người kh&aacute;c tới n&oacute;i vẫn c&oacute; một ch&uacute;t ưu thế, v&igrave; vậy vinh quang gi&agrave;nh được vị tr&iacute; thứ nhất của Mục tr&agrave; xanh.<br />\r\nChủ nhiệm lớp, cũng ch&iacute;nh l&agrave; đại gia Ma Ph&aacute;p Đạo Sư - Tiết Mộc Sinh đ&atilde; đứng tại lớp học h&agrave;ng trước nhất, thức tỉnh sẽ ở trước mặt mọi người, c&aacute;c học sinh cần phải l&agrave;m l&agrave; tại bị gọi v&agrave;o t&ecirc;n thời điểm đi l&ecirc;n trước kỳ, đem hai tay đặt ở Ma Ph&aacute;p Đạo Sư trước mặt c&aacute;i kia thức tỉnh thạch, lẳng lặng cảm ứng.<br />\r\nMạc Thi&ecirc;n l&agrave; người đầu ti&ecirc;n thức tỉnh tiến v&agrave;o thế giới kia hắn cũng lập tức ph&aacute;t hiện một mảnh tinh kh&ocirc;ng bao la sau khi lần t&igrave;m một hồi hắn ph&aacute;t hiện một bụi sao m&agrave;u lục sắc c&ugrave;ng m&agrave;u t&iacute;m, kh&ocirc;ng bao l&acirc;u thức tỉnh thạch s&aacute;ng l&ecirc;n, nh&igrave;n m&agrave;u sắc sau đ&oacute; Tiết Mộc Sinh n&oacute;i:&quot;Phong hệ rất tốt hệ, Mạc Thi&ecirc;n đồng học ch&uacute;c mừng ngươi&quot;<br />\r\nbởi v&igrave; hắn cũng kh&ocirc;ng thế n&agrave;o nổi danh, b&igrave;nhthường chỉ học xong lại về nh&agrave; tập trung chủ y&ecirc;u v&agrave;o việc minh tu d&ugrave; mỗi ng&agrave;y 2 giờ nhưng hắn vẫn rất cẩn thận, cũng kh&ocirc;ng c&oacute; qu&aacute; nhiều người bất ngờ về việc hắn thức tỉnh tốt hệ.<br />\r\nChỉ l&agrave; trong tinh thần của hắn c&ograve;n c&oacute; một bụi sao m&agrave;u t&iacute;m kh&ocirc;ng biết l&agrave; nguy&ecirc;n nh&acirc;n g&igrave; m&agrave; n&oacute; kh&ocirc;ng hiện ra.<br />\r\n&quot; như vậy cũng tốt d&ugrave; sao bại lộ thi&ecirc;n ph&uacute; cũng kh&ocirc;ng tốt ai biết T&aacute;t L&atilde;ng vẫn c&ograve;n đang gi&aacute;m thị T&acirc;m Hạ nếu bị ph&aacute;t hiện, được mời tham gia hắc gi&aacute;o đ&igrave;nh vậy th&igrave; cũng qu&aacute; c&oacute; khả năng&quot;<br />\r\nMạc thi&ecirc;n Thầm nghĩ<br />\r\n&Iacute;t nhất, ch&iacute;nh m&igrave;nh sẽ kh&ocirc;ng qu&aacute; mức bị ch&uacute; &yacute; .<br />\r\nGiống hắn loại n&agrave;y kh&ocirc;ng c&oacute; quyền hạn kh&ocirc;ng c&oacute; bối cảnh người, nếu l&agrave; thật bị c&aacute;i g&igrave; thế lực nhỏ vừa &yacute; l&agrave;m gia thần m&agrave; n&oacute;i, hắn thật đ&uacute;ng l&agrave; chưa hẳn cự tuyệt .<br />\r\nD&ugrave; sao, cự tuyệt lời n&oacute;i....!Con đường ph&iacute;a trước chậm r&atilde;i, nguy cơ tr&ugrave;ng tr&ugrave;ng cảm gi&aacute;c a!<br />\r\nChưa trưởng th&agrave;nh thi&ecirc;n t&agrave;i, đ&oacute; ch&iacute;nh l&agrave; một cặn b&atilde;....<br />\r\n<br />\r\nCũng ch&iacute;nh l&agrave; Mạc Ph&agrave;m c&aacute;i n&agrave;y hổ đồ chơi, tại mới vừa rồi c&oacute; chút th&agrave;nh tựu sau đ&oacute;, ch&iacute;nh l&agrave; kh&ocirc;ng biết m&igrave;nh họ g&igrave;, cứ thế đi khi&ecirc;u kh&iacute;ch mục Tr&aacute;c V&acirc;n c&aacute;i n&agrave;y th&agrave;nh rộng thổ ho&agrave;ng đế...<br />\r\nTiếp theo l&agrave; Mục Bạch thức tỉnh kh&ocirc;ng bao l&acirc;u<br />\r\n&ldquo;Băng hệ, thực sự l&agrave; Băng hệ a!!!&rdquo;<br />\r\nĐột nhi&ecirc;n, c&oacute; người sau lưng k&ecirc;u lớn l&ecirc;n, trong đ&oacute; c&ograve;n k&egrave;m theo một v&agrave;i nữ sinh tiếng th&eacute;t ch&oacute;i tai.<br />\r\nMục Bạch đ&atilde; nghe được &acirc;m thanh , ở trong mừng rỡ mở mắt!<br />\r\nQuả nhi&ecirc;n l&agrave; h&agrave;n băng hệ, c&oacute; Mục gia bản nguy&ecirc;n hệ, Mục Bạch cũng đ&atilde; nh&igrave;n thấy bị Mục gia tiếp nhận sau đ&oacute;, c&agrave;ng l&agrave; cảm thấy l&ecirc;n như diều gặp gi&oacute; ng&agrave;y kh&ocirc;ng xa....<br />\r\nC&ugrave;ng một thời gian, nh&igrave;n về ph&iacute;a Mạc Ph&agrave;m bộ d&aacute;ng, c&agrave;ng l&agrave; tr&agrave;n đầy khinh thường....<br />\r\n&ldquo;Nằm th&aacute;o....!C&aacute;i n&agrave;y chết tr&agrave; xanh đắc &yacute; c&aacute;i g&igrave;, kh&ocirc;ng phải liền l&agrave; một c&aacute;i Băng hệ đi!&rdquo; Mạc Ph&agrave;m cảm gi&aacute;c c&oacute; bị khi&ecirc;u kh&iacute;ch n&oacute;i.<br />\r\n&ldquo;&Acirc;n, nỗ lực a tao năm, chỉ cần ngươi kh&ocirc;ng thức tỉnh kh&ocirc;ng kh&iacute;, hắn ở trước mặt ngươi ch&iacute;nh l&agrave; một c&aacute;i đệ đệ.&rdquo; Mạc Thi&ecirc;n cười đ&aacute;p lại n&oacute;i.<br />\r\nMạc Ph&agrave;m lập tức kh&ocirc;ng n&oacute;i....<br />\r\nD&ugrave; sao, hắn c&ograve;n thật sự sợ phế vật bắt đầu....<br />\r\nMấu chốt l&agrave;, thế giới n&agrave;y thế nhưng l&agrave; rất thực tế, thật sự g&igrave; cũng kh&ocirc;ng c&oacute;, liền k&eacute;o xe lời n&oacute;i? Đời n&agrave;y sợ l&agrave; lạnh....<br />\r\n....<br />\r\n&ldquo;Rất tốt, l&agrave; rất tinh khiết Băng hệ, ta nghĩ ngươi trong thế giới tinh thần cũng đ&atilde; xuất hiện một mảnh Băng hệ bụi sao, nhiều hơn tu luyện, để n&oacute; c&agrave;ng th&ecirc;m mở rộng a!&rdquo; Chủ nhiệm lớp trong mắt Tiết Mộc Sinh tr&agrave;n đầy khen ngợi.<br />\r\nMục Bạch điểm một ch&uacute;t đầu, rời đi về sau, chủ nhiệm lớp ch&iacute;nh l&agrave; lại điểm tới người kế tiếp t&ecirc;n.....<br />\r\nTối đa cũng ch&iacute;nh l&agrave; mấy c&aacute;i Phong hệ...<br />\r\nNhưng c&agrave;ng nhiều vẫn l&agrave; quang hệ, Thủy hệ....<br />\r\nĐ&atilde; thức tỉnh tiền kỳ yếu g&agrave; Ma Ph&aacute;p Hệ ch&iacute;nh bọn họ, cơ hồ cũng l&agrave; muốn đi cảm thụ một ch&uacute;t s&acirc;n thượng gi&oacute; !<br />\r\nC&aacute;i n&agrave;y c&ograve;n c&oacute; để cho người sống hay kh&ocirc;ng a!<br />\r\n&ldquo;C&aacute;i tiếp theo, Trương Tiểu Hầu.&rdquo;<br />\r\nNgồi ở Mạc Ph&agrave;m trước mặt Trương Tiểu Hầu nghe được m&igrave;nh bị h&ocirc; sau đ&oacute;, c&ugrave;ng Mạc Ph&agrave;m hồi b&aacute;o một l&uacute;c sau, ch&iacute;nh l&agrave; đứng dậy đi tới.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; thay đổi, hắn sơ giai ma ph&aacute;p Hệ l&agrave; Phong hệ.<br />\r\nTiếp lấy đến một c&aacute;i t&ecirc;n l&agrave; Chu Mẫn tướng mạo tương đối ngọt ng&agrave;o muội tử.<br />\r\nTại n&agrave;ng bắt đầu sau khi gi&aacute;c tỉnh, thức tỉnh tr&ecirc;n đ&aacute;, một đạo m&agrave;u cam ngọn lửa đột nhi&ecirc;n luồn l&ecirc;n.<br />\r\nMột m&agrave;n n&agrave;y để cho to&agrave;n lớp người lập tức mở to hai mắt nh&igrave;n.<br />\r\n&ldquo;Hỏa....!Hỏa hệ!!&rdquo;<br />\r\n&ldquo;Dựa v&agrave;o, thế m&agrave; c&ograve;n l&agrave; muội tử, nữ sinh n&agrave;y k&ecirc;u c&aacute;i g&igrave; a?&rdquo;<br />\r\nTrong l&uacute;c nhất thời, thức tỉnh nữ sinh kia trở th&agrave;nh ti&ecirc;u điểm của cả lớp, Hỏa hệ xem như Nguy&ecirc;n Tố hệ b&ecirc;n trong xuất sắc nhất , l&agrave; tất cả Ma đồ cấp ma ph&aacute;p sư thức tỉnh chọn lựa đầu ti&ecirc;n, nếu bọn hắn c&oacute; thể lựa chọn....<br />\r\nĐ&aacute;ng tiếc Hỏa hệ tương đối ngạo kiều, x&aacute;c suất t&iacute;nh to&aacute;n thấp, trong một lớp học tối đa cũng liền 3 c&aacute;i, một phần mười x&aacute;c suất cũng chưa tới.<br />\r\nĐương nhi&ecirc;n, kỳ thực c&aacute;i n&agrave;y c&ugrave;ng loại n&agrave;y thức tỉnh thạch cũng l&agrave; c&oacute; quan hệ rất lớn.<br />\r\n<br />\r\nD&ugrave; sao, trường học d&ugrave;ng, c&ugrave;ng trong loại trong thế lực lớn kia cất giữ kh&aacute;c biệt....<br />\r\n&ldquo;Xem như nữ theo mẹ nghiệp, văn hoa l&atilde;o sư biết hẳn l&agrave; sẽ rất vui mừng.&rdquo; Chủ nhiệm lớp Tiết Mộc Sinh nh&igrave;n xem Chu Mẫn h&agrave;i l&ograve;ng n&oacute;i.<br />\r\n&ldquo;Cảm ơn l&atilde;o sư.&rdquo; Chu Mẫn r&otilde; r&agrave;ng cũng thật vui vẻ.<br />\r\n&ldquo;C&aacute;i tiếp theo...!Quang hệ!&rdquo;<br />\r\n&ldquo;C&aacute;i tiếp theo....!Thủy hệ!&rdquo;<br />\r\n<br />\r\n&ldquo;Vị cuối c&ugrave;ng, Mạc Ph&agrave;m!&rdquo;<br />\r\nMạc Ph&agrave;m đặt tay l&ecirc;n thức tỉnh tỉnh thạch một hồi l&acirc;u.<br />\r\nC&ugrave;ng l&uacute;c đ&oacute;, ngay tại Mạc Ph&agrave;m muốn thức tỉnh đi ra Ma Ph&aacute;p Hệ thời điểm, b&ecirc;n ngo&agrave;i truyền ra L&ocirc;i hệ thức tỉnh tiếng h&ocirc;....<br />\r\nNếu như n&oacute;i Mạc Ph&agrave;m thuộc về k&egrave;m theo h&agrave;o quang m&agrave; n&oacute;i, c&aacute;i kia Hứa Chi&ecirc;u Đ&igrave;nh l&agrave; thuộc về c&oacute; ch&uacute;t thi&ecirc;n ph&uacute;.<br />\r\n<br />\r\nKh&ocirc;ng nhưng cảm gi&aacute;c tỉnh L&ocirc;i hệ, hơn nữa c&ograve;n dựa v&agrave;o trường học điểm n&agrave;y t&agrave;i nguy&ecirc;n, kh&ocirc;ng c&oacute; bất kỳ c&aacute;i g&igrave; ngoại lực t&igrave;nh huống ph&iacute;a dưới, đột ph&aacute; đến trung giai, c&aacute;i n&agrave;y đ&atilde; rất l&agrave; giỏi.<br />\r\nD&ugrave; sao, rất rất nhiều người, gi&aacute;c tỉnh ma ph&aacute;p sau đ&oacute;, c&oacute; thể cũng ch&iacute;nh l&agrave; một sơ giai ph&aacute;p sư .<br />\r\nĐến nỗi trung giai c&ugrave;ng cao giai, kh&ocirc;ng c&oacute; thế lực khổng lồ cung cấp t&agrave;i nguy&ecirc;n, muốn l&ecirc;n như diều gặp gi&oacute; ch&iacute;n vạn dặm, vậy cơ hồ l&agrave; kh&ocirc;ng c&oacute; g&igrave; cơ hội....<br />\r\nNgay sau đ&oacute;, Mạc Thi&ecirc;n th&igrave; thấy Mạc Ph&agrave;m đầu ti&ecirc;n l&agrave; hơi c&oacute; m&ecirc; mang, nhưng sau đ&oacute; triệt để l&acirc;m v&agrave;o đi&ecirc;n cuồng đồng dạng mừng rỡ đ&atilde; tr&uacute;ng!<br />\r\nNh&igrave;n xem c&aacute;i n&agrave;y mất mặt bộ dạng n&agrave;y c&uacute;i c&uacute;i bộ d&aacute;ng, thật l&agrave; để cho người ta muốn l&ecirc;n đi cho hắn một c&aacute;i t&aacute;t!</span></span><br />\r\n&nbsp;</p>', 1, '2022-03-16 03:17:20', '2022-03-16 03:17:20'),
(16, 7, 'Chương 3: Thực Lực Tăng Lên', 'chuong-3-thuc-luc-tang-len', '<p>qrqwrqwrad</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Chủ nhiệm sau khi trao đổi 1 ch&uacute;t với c&aacute;c học sinh , Mạc Thi&ecirc;n cũng theo Mạc Ph&agrave;m Trương Tiểu Hầu đi ra ngo&agrave;i dạo chơi một ch&uacute;t cũng về tới nh&agrave;.<br />\r\nMạc Thi&ecirc;n lập tức v&agrave;o ph&ograve;ng đắm ch&igrave;m v&agrave;o bảng số liệu.<br />\r\nMặt Ngo&agrave;i số liệu<br />\r\nMạc Thi&ecirc;n<br />\r\nTuổi 16<br />\r\nTốc độ tu luyện: 5 điểm mỗi giời<br />\r\nTu vi: Phong hệ sơ giai [0/1000], L&ocirc;i hệ [0/1000] [tu vi vượt qua 3 đại cảnh giới liền c&oacute; thể ph&aacute;t hiện tu vi của ngươi ngươi c&oacute; thể t&ugrave;y &yacute; &aacute;p chế cảnh giới của m&igrave;nh tới mức ngươi muốn cho người kh&aacute;c thấy]<br />\r\ntinh thần cảnh giới: 0/500 [1 cấp] [tốc độ 5 điểm mỗi giờ]<br />\r\n<br />\r\nMa kh&iacute;: kh&ocirc;ng<br />\r\nLinh chủng: kh&ocirc;ng<br />\r\nHồn chủng: kh&ocirc;ng<br />\r\nThi&ecirc;n chủng : kh&ocirc;ng<br />\r\nTrời sinh thi&ecirc;n ph&uacute;: Trời sinh song hệ<br />\r\nĐiểm tu luyện : 43830 điểm<br />\r\n[ tu luyện kh&ocirc;ng c&oacute; b&igrave;nh cảnh chỉ cần c&oacute; đủ điểm tu luyện c&oacute; thể l&ecirc;n bất cứ cấp bậc n&agrave;o, điểm tu vi c&agrave;ng cao sẽ quyết định cấp bậc ma ph&aacute;p đạt đến, chửng khống chấm nhỏ phụ thuộc v&agrave;o phần trăm điểm tu vi, kh&ocirc;ng thể cường h&oacute;a ng&ocirc;i sao bằng tinh ph&aacute;ch nhưng c&oacute; thể chuyển th&agrave;nh điểm tu luyện, kh&ocirc;ng thể l&ecirc;n cấp bậc ph&aacute;p sư khi tất cả c&aacute;c hệ chưa m&atilde;n tu]<br />\r\n[Tốc độ tu luyện phụ thuộc v&agrave;o ma kh&iacute;, tinh thần cảnh giới]<br />\r\n[muốn th&ecirc;m điểm tinh thần cảnh giới cần phải l&agrave;m ph&aacute;p sư đủ cấp bậc Sơ giai tối đa cấp 3 Trung giai tối đa cấp 6 Cao giai tối đa cấp 9 Si&ecirc;u giai tối đa cấp 12 ( sau cấp 12 l&agrave; thần cấp)]<br />\r\n[ Sơ gia trở xuống c&oacute; thể minh tu 2 giờ/ng&agrave;y, trung giai 3 giờ/ng&agrave;y , cao giai tự động tu luyện 4 giờ / ng&agrave;y, si&ecirc;u giai tư động 5 giờ /ng&agrave;y, cấm ch&uacute; tư động 10iờ /ng&agrave;y ph&aacute;p thần tư động cả ng&agrave;y]<br />\r\nHắn cũng kh&ocirc;ng vội tăng tu vi m&agrave; l&agrave; gia tăng tinh thần cảnh giới d&ugrave; sao tinh thần cảnh giới kh&ocirc;ng chỉ gi&uacute;p tăng khả năng chửng khống ma ph&aacute;p m&agrave; c&ograve;n c&oacute; thể gia tăng tốc độ tu luyện<br />\r\n500/500 trở th&agrave;nh cấp 2<br />\r\n1000/1000 trở th&agrave;nh cấp 3<br />\r\n2500/2500 chưa thể l&ecirc;n cấp 4 chờ v&agrave;o Trung giai<br />\r\ntinh thần cảnh giới: 2500/2500 (3 cấp đỉnh phong)(tốc độ 25 điểm mỗi giờ)<br />\r\nsau đ&oacute; hắn tiếp tục th&ecirc;m điểm v&agrave;o tu vi của m&igrave;nh<br />\r\n<br />\r\nTu vi: Phong hệ sơ giai tam cấp (1000/1000) chờ l&ecirc;n cấp, L&ocirc;i hệ sơ giai tam cấp (1000/1000) chờ l&ecirc;n cấp<br />\r\nĐiểm tu luyện : 37830 điểm<br />\r\nHắn cũng kh&ocirc;ng ngờ m&igrave;nh đơn giản như vậy liền c&oacute; thể bước v&agrave;o Trung giai chỉ l&agrave; nếu như b&acirc;y giờ hắn đột ph&aacute; trung giai nếu v&agrave;o l&uacute;c n&agrave;y gặp người kh&aacute;c c&oacute; tu vi si&ecirc;u giai mới c&oacute; thể nh&igrave;n ra được tu vi của hắn nhưng hắn cũng kh&ocirc;ng biết T&aacute;t L&atilde;ng c&oacute; tại đang ở nơi n&agrave;o đ&oacute; quan s&aacute;t nếu như bị ph&aacute;t hiện th&igrave; ...!hắn cũng kh&ocirc;ng gi&aacute;m nghĩ, v&igrave; qua hưng phấn đem chuyện n&agrave;y cho qu&ecirc;n mất.<br />\r\n<br />\r\ncũng may chỉ cần kh&ocirc;ng vượt qua 3 đại cảnh giới liền kh&ocirc;ng sao v&igrave; vậy hắn cũng lập tức đột ph&aacute; trung giai.<br />\r\nMuốn đột ph&aacute; cần điểm gấp 5 lần so với điểm tu h&agrave;nh đến m&atilde;n tu của cấp trước, khiến mạc thi&ecirc;n cảm thấy thật đủ hố<br />\r\nTu vi: Phong hệ trung giai tam cấp [10000/10000] chờ l&ecirc;n cấp, L&ocirc;i hệ trung giai tam cấp [10000/10000] chờ l&ecirc;n cấp<br />\r\nĐiểm tu luyện : 7830 điểm<br />\r\nHắn rốt cuộc cũng biết r&otilde; v&igrave; sao m&agrave; những ph&aacute;p sư kia l&ecirc;n cấp chậm như vậy, v&igrave; thi&ecirc;n ph&uacute; vấn đề nếu kh&ocirc;ng c&oacute; t&agrave;i nguy&ecirc;n chồng chất l&ecirc;n cả đời điều kh&oacute; m&agrave; đạt đến cao giai một số lớn thậm ch&iacute; liền trung giai cũng kh&ocirc;ng v&agrave;o được.<br />\r\nMẹ n&oacute; hắn tu luyện hết 12 năm mới c&oacute; thể tới trung giai m&atilde;n tu 2 hệ mặc d&ugrave; tu vi cao ph&aacute;p sư c&oacute; thể minh tu nhiều hơn nhưng bọn hắn l&ecirc;n cấp so với hắn t&ecirc;n n&agrave;y kh&ocirc;ng gặp b&igrave;nh cảnh c&agrave;ng trở n&ecirc;n kh&oacute; khăn hơn.<br />\r\n<br />\r\n<br />\r\nCho d&ugrave; hắn tu luyện suốt 12 năm qua cũng chỉ c&oacute; thể tới như vậy nếu hắn nhớ kh&ocirc;ng nhầm l&atilde;o đệ tu luyện c&ugrave;ng thời gian với m&igrave;nh như vậy cũng đ&atilde; trở th&agrave;nh ph&aacute;p thần.<br />\r\n<br />\r\nMạc d&ugrave; c&oacute; thanh long trợ gi&uacute;p nhưng vẫn l&agrave; v&ocirc; c&ugrave;ng kinh khủng, m&agrave; hắn kh&ocirc;ng thể d&ugrave;ng t&agrave;i nguy&ecirc;n để l&ecirc;n cấp chỉ c&oacute; thể sử dụng tinh ph&aacute;ch l&agrave;m ch&uacute;t điểm tu luyện, nghĩ tới đ&acirc;y hắn trong t&acirc;m nước mắt cũng ch&agrave;y d&agrave;i.<br />\r\nHắn b&acirc;y giờ muốn l&ecirc;n Cao giai cần phải thức tỉnh 2 hệ nữa phải tu luyện tới Trung giai m&atilde;n tu c&ograve;n cần phải c&oacute; 32000 điểm tu luyện c&ograve;n c&oacute; th&ecirc;m 50000 điểm để 1 hệ l&ecirc;n cao giai, c&aacute;i n&agrave;y mẹ n&oacute; 12 năm hắn mới c&oacute; hơn 4 vạn điểm b&acirc;y giờ cần hơn 8 vạn điểm để l&ecirc;n cao giai d&ugrave; l&agrave; tốc độ tu luyện tăng gấp 5 lần cũng cần tu luyện 5 năm mới c&oacute; thể đạt tới cao giai, nếu t&iacute;nh như vậy hắn tốc độ l&ecirc;n cao giai cũng l&agrave; bị Mạc Ph&agrave;m đuổi kịp.<br />\r\n<br />\r\nvề sau mẹ n&oacute; c&agrave;ng cần nhiều hơn điểm c&ograve;n c&oacute; th&ecirc;m hệ mới hắn suy nghĩ một ch&uacute;t hắn đều ho&agrave;i nghi ch&iacute;nh m&igrave;nh c&oacute; thể hay kh&ocirc;ng đạt tới si&ecirc;u giai, nếu t&iacute;nh sơ ra hấn muốn tới si&ecirc;u giai cần tới gần 150 vạn điểm, nghĩ một hồi nội t&acirc;m mới vừa b&agrave;nh trướng của hắn lập tức dừng lại.<br />\r\nTa qu&aacute; kh&oacute; khăn!</span></span><br />\r\n&nbsp;</p>', 1, '2022-03-16 03:17:54', '2022-03-16 03:17:54'),
(17, 7, 'Chương 4: Đường Nguyệt', 'chuong-4-duong-nguyet', '<p>qwqhfh</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một th&aacute;ng sau<br />\r\nD&ugrave; sao đều khai giảng một th&aacute;ng, đến b&acirc;y giờ c&ograve;n l&agrave; Tiết l&atilde;o sư đang dạy bọn hắn, đều cảm gi&aacute;c kh&ocirc;ng c&oacute; &yacute; g&igrave;.<br />\r\n<br />\r\nĐang l&uacute;c mọi người c&ograve;n tại l&uacute;c o&aacute;n tr&aacute;ch<br />\r\nNgo&agrave;i cửa truyền tới gi&agrave;y cao g&oacute;t giẫm m&agrave; &acirc;m thanh, ngay sau đ&oacute; l&agrave; tiếng mở cửa, c&aacute;c bạn học đều tại xem chừng cửa trước, theo cửa trước mở ra, một vị th&agrave;nh thục hơn nữa gợi cảm thục nữ đứng ở bọn hắn cửa ra v&agrave;o, tiếp đ&oacute; đi tới bục giảng b&ecirc;n cạnh, nữ l&atilde;o sư c&aacute;i kia như ma quỷ d&aacute;ng người trở th&agrave;nh trong lớp một phong cảnh tuyến, tr&ecirc;n cơ bản mỗi c&aacute;i nam sinh tiểu đệ đệ đều biến th&agrave;nh tiểu ca ca, Mạc Ph&agrave;m cũng kh&ocirc;ng ngoại lệ.<br />\r\n<br />\r\nC&oacute; lẽ cũng chỉ c&oacute; sắt th&eacute;p thẳng nam Mạc Thi&ecirc;n chỉ nh&igrave;n n&agrave;ng một lần cũng kh&ocirc;ng để &yacute; nữa, d&ugrave; đ&atilde; qua 16 năm nhưng hắn vẫn kh&ocirc;ng qu&ecirc;n được h&igrave;nh b&oacute;ng của nữ nh&acirc;n kia cho d&ugrave; n&agrave;ng c&ugrave;ng hắn c&oacute; lẽ vĩnh viễn kh&ocirc;ng thể gặp lại, cho d&ugrave; một ng&agrave;y hắn cường đại đến c&oacute; thể trở lại thế giới kia nhưng l&uacute;c đ&oacute; cũng sợ kh&ocirc;ng biết khi n&agrave;o, hoặc c&oacute; lẽ kh&ocirc;ng bao giờ l&agrave;m được.<br />\r\n<br />\r\nHắn biết cho d&ugrave; l&agrave; ph&aacute;p thần cảnh cũng chưa chắc c&oacute; thể dễ d&agrave;ng xuy&ecirc;n qua những vị diện kh&aacute;c , huống chi l&agrave; một c&aacute;i vị diện m&agrave; m&igrave;nh kh&ocirc;ng x&aacute;c định được vị tr&iacute; cụ thể.<br />\r\n<br />\r\nNh&igrave;n một ch&uacute;t Mạc Ph&agrave;m, ph&aacute;t hiện Mạc Ph&agrave;m c&ograve;n tại chết chăm ch&uacute; v&agrave;o nữ l&atilde;o sư trước ngực, nước bọt đều nhanh chảy ra, xem chừng lại đang nghĩ một ch&uacute;t việc kh&ocirc;ng thể lộ ra ngo&agrave;i! Mạc Thi&ecirc;n cũng kh&ocirc;ng chịu nổi, trực tiếp hướng về ph&iacute;a Mạc Ph&agrave;m một c&uacute; đ&aacute;, Mạc Ph&agrave;m mặc d&ugrave; đau, nhưng nh&igrave;n một ch&uacute;t Mạc Thi&ecirc;n b&ecirc;n cạnh khu&ocirc;n mặt đ&atilde; đen lại cũng &yacute; thức được.<br />\r\n<br />\r\nQu&aacute; mất mặt!<br />\r\n&quot;! &quot;<br />\r\n&ldquo;Mọi người tốt! Ta l&agrave; c&aacute;c ngươi t&acirc;n l&atilde;o sư, ta gọi Đường Nguyệt.<br />\r\n<br />\r\n<br />\r\n&rdquo; Đường Nguyệt l&atilde;o sư n&oacute;i<br />\r\nLiền Đường Nguyệt &acirc;m thanh cũng l&agrave; t&ecirc; dại, đồng thời mang theo một tia đ&ugrave;a giỡn ngữ kh&iacute;, khiến cho c&aacute;c bạn học c&agrave;ng th&ecirc;m kh&ocirc;ng c&aacute;ch n&agrave;o tự kềm chế, đồng thời trong l&ograve;ng của bọn hắn cũng kế hoạch ra nh&acirc;n sinh hi vọng: Kiếm tiền, tu luyện, cưới Đường Nguyệt! !<br />\r\n&ldquo;Tốt, để cho ta nh&igrave;n một ch&uacute;t, lớp học c&oacute; mấy vị l&agrave; Hỏa hệ?&rdquo; Đường Nguyệt n&oacute;i<br />\r\n&ldquo;Ta l&agrave; Hỏa hệ&rdquo; Chu Mẫn vượt l&ecirc;n trước đứng l&ecirc;n<br />\r\n&ldquo;&Acirc;n, c&ograve;n g&igrave; nữa kh&ocirc;ng?&rdquo;<br />\r\n&ldquo;Ta l&agrave;!&rdquo; Sau đ&oacute; x&oacute; xỉnh Mạc Ph&agrave;m chậm r&atilde;i đứng l&ecirc;n<br />\r\n&quot;Ngươi t&ecirc;n l&agrave; g&igrave;, đồng học.<br />\r\n<br />\r\n&rdquo; Đường Nguyệt đối với Mạc Ph&agrave;m n&oacute;i<br />\r\n&ldquo;Mạc Ph&agrave;m&rdquo;<br />\r\n&ldquo;Đi, ta đ&atilde; biết, Mạc Ph&agrave;m đồng học, ngươi b&acirc;y giờ c&oacute; thể kết nối mấy ng&ocirc;i sao tử?&rdquo;<br />\r\n&ldquo;5 khỏa&rdquo;<br />\r\n&ldquo;C&aacute;c ngươi ngồi xuống trước đ&atilde;&rdquo; Đường Nguyệt tr&ecirc;n mặt lộ ra một tia bất đắc dĩ<br />\r\nV&igrave; c&aacute;i g&igrave; Chu Mẫn ta dễ d&agrave;ng như vậy liền c&oacute; thể đo&aacute;n được thực lực của n&agrave;ng.<br />\r\n<br />\r\nM&agrave;, Mạc Ph&agrave;m ta lại kh&ocirc;ng nh&igrave;n thấy hạn mức cao nhất? Đường Nguyệt nh&igrave;n chăm ch&uacute; Mạc Ph&agrave;m, xem ra, gia hỏa n&agrave;y chắc chắn c&ograve;n ẩn giấu thứ g&igrave;.<br />\r\n<br />\r\nĐương nhi&ecirc;n l&agrave; Mạc Thi&ecirc;n đ&atilde; căn dặn hắn phải biết ẩn giấu thực lực, hắn cũng l&agrave; nghe theo, hắn cảm thấy giả heo ăn thịt hổ cũng rất tốt.<br />\r\n<br />\r\n&ldquo;Đường Nguyệt l&atilde;o sư! Ch&uacute;ng ta muốn nh&igrave;n một ch&uacute;t ng&agrave;i thi triển ma ph&aacute;p!&rdquo; Ph&iacute;a dưới kh&ocirc;ng biết ai h&ocirc; c&acirc;u, tiếp đ&oacute; to&agrave;n lớp liền theo ồn &agrave;o l&ecirc;n.<br />\r\n<br />\r\n&ldquo;Tốt a, liền thỏa m&atilde;n c&aacute;c ngươi.<br />\r\n<br />\r\n&rdquo;<br />\r\nĐường Nguyệt quan s&aacute;t thực chiến con rối &ldquo;Hỏa Tư &middot; Thi&ecirc;u đốt&rdquo; Trong nh&aacute;y mắt con rối liền to&agrave;n th&acirc;n bị ngọn lửa bao tr&ugrave;m, thẳng đến đốt sạch sẽ<br />\r\n<br />\r\n&ldquo;Đ&acirc;y ch&iacute;nh l&agrave; Hỏa Tư &middot; Thi&ecirc;u đốt, Hỏa hệ sơ giai ma ph&aacute;p&rdquo; Đường Nguyệt n&oacute;i<br />\r\n&ldquo;Oa! Lợi hại lợi hại!&rdquo;<br />\r\nMạc Ph&agrave;m cũng kh&ocirc;ng n&oacute;i c&aacute;i g&igrave;, chỉ l&agrave; đơn thuần nh&igrave;n một ch&uacute;t con rối, nghĩ thầm: Lợi hại như vậy! Vậy ta cũng muốn nhanh l&ecirc;n tu luyện!<br />\r\n&quot;! &quot;<br />\r\nMấy th&aacute;ng sau<br />\r\nV&agrave;o l&uacute;c ban đ&ecirc;m<br />\r\nMạc Ph&agrave;m vẫn tại tr&ecirc;n s&acirc;n thượng tu luyện, Mạc Ph&agrave;m mặc d&ugrave; n&oacute;i hỏa hệ đ&atilde; 7 ng&ocirc;i sao tử , nhưng c&ograve;n chưa kịp sử dụng tới, cho n&ecirc;n vẫn l&agrave; c&oacute; ch&uacute;t vụng về, m&agrave; L&ocirc;i hệ cũng sắp tu luyện th&agrave;nh sơ giai.<br />\r\n<br />\r\nM&agrave; Hỏa hệ, liền 7 ng&ocirc;i sao tử đều kh&ocirc;ng th&agrave;nh c&ocirc;ng, cho n&ecirc;n muốn cố gắng tu luyện hơn.<br />\r\n<br />\r\nMột th&aacute;ng sau ch&iacute;nh l&agrave; ma ph&aacute;p khảo hạch<br />\r\nCũng nghe Tiết l&atilde;o sư n&oacute;i qua<br />\r\nNếu như tại ma ph&aacute;p trong khảo hạch được D cấp người, sẽ trực tiếp bị khuy&ecirc;n lui<br />\r\nHắn c&ugrave;ng l&atilde;o ca đều c&oacute; song hệ, l&uacute;c trước hắn cũng l&uacute;c v&ocirc; t&igrave;nh ph&aacute;t hiện l&atilde;o ca ph&oacute;ng th&iacute;ch l&ocirc;i ấn.<br />\r\n<br />\r\nKhả năng hắn bị rớt l&agrave; kh&ocirc;ng nhưng hắn cũng vẫn lu&ocirc;n cố gắng, v&igrave; sau khi hắn thấy l&atilde;o ca mới 4 th&aacute;ng đ&atilde; c&oacute; thể ph&oacute;ng th&iacute;ch ma ph&aacute;p, so hắn c&ograve;n sớm hơn 1 th&aacute;ng hơn nữa l&agrave; cả 2 hệ c&oacute; thể ph&oacute;ng th&iacute;ch.<br />\r\n<br />\r\nHắn nhưng kh&ocirc;ng biết đ&oacute; l&agrave; Mạc Thi&ecirc;n cố &yacute; để cho hắn biết để k&iacute;ch th&iacute;ch đấu ch&iacute; của hắn.<br />\r\n<br />\r\n<br />\r\nNgười kh&aacute;c kh&ocirc;ng biết Mạc Ph&agrave;m c&oacute; hệ kh&aacute;c, Mạc Ph&agrave;m cũng kh&ocirc;ng muốn để cho bị người ta biết, cho n&ecirc;n kh&ocirc;ng thể l&agrave;m g&igrave; kh&aacute;c hơn l&agrave; d&ugrave;ng Hỏa hệ tới khảo hạch.<br />\r\n<br />\r\nĐiều n&agrave;y cũng l&agrave;m cho Mạc Ph&agrave;m kh&ocirc;ng thể kh&ocirc;ng cố gắng, nhưng Mạc Ph&agrave;m kh&ocirc;ng biết l&agrave;, Đường Nguyệt l&atilde;o sư nhưng l&agrave; &acirc;m thầm ch&uacute; &yacute; Mạc Ph&agrave;m.<br />\r\n<br />\r\nMạc Thi&ecirc;n cũng &acirc;m thầm ch&uacute; &yacute; l&atilde;o đệ, hắn thấy được l&atilde;o đệ trưởng th&agrave;nh cũng rất nhanh nếu hắn kh&ocirc;ng c&oacute; mặt ngo&agrave;i c&oacute; lẽ so l&atilde;o đệ k&eacute;m rất nhiều, hắn cũng ph&aacute;t hiện Đường Nguyệt cũng đang ch&uacute; &yacute; Mạc Ph&agrave;m, cũng may hắn c&oacute; thể &aacute;p chế tu vi rất tốt kh&ocirc;ng bị ph&aacute;t hiện, sau mấy th&aacute;ng hắn cũng đ&atilde; đột ph&aacute; tinh thần cảnh giới l&ecirc;n cấp 5 hắn kh&ocirc;ng nghĩ tới đột ph&aacute; cấp 5 tinh thần lực rất hố vậy m&agrave; cần tới 1 vạn điểm tu luyện, c&aacute;i n&agrave;y khiến hắn miệng phun hương th&ocirc;m một đoạn thời gian, tuy nhi&ecirc;n sau đ&oacute; hắn cũng ph&aacute;t hiện tinh thần cảnh giới tới cấp 4 tốc độ tu luyện tăng l&ecirc;n rất kinh khủng trực tiếp tăng gấp 4 lần th&agrave;nh 100 điểm 1 giờ, hắn b&acirc;y giờ cũng đ&atilde; tinh thần lực cấp 5 l&agrave; 200 điểm 1 giờ, hắn b&acirc;y giờ điểm tu luyện cũng c&ograve;n 6 vạn cũng c&ograve;n thiếu 4 vạn nữa c&oacute; thể đủ l&ecirc;n cấp 6 tinh thần cảnh, chỉ cần 5 ng&agrave;y hắn c&oacute; thể đủ thăng cấp.<br />\r\n<br />\r\nHắn ph&aacute;t hiện muốn tu luyện nhanh th&igrave; gia tăng tinh thần cảnh giới l&agrave; phương ph&aacute;p tốt nhất.<br />\r\n<br />\r\nC&ograve;n về v&igrave; sao hắn chưa sử dụng Ma kh&iacute; l&agrave; v&igrave; Bảng ngo&agrave;i , hắn chỉ c&oacute; thể lựa chọn<br />\r\nmột ma khi duy nhất, n&ecirc;n hắn ph&ugrave; hợp nhất vẫn l&agrave; loại h&igrave;nh ma kh&iacute; c&oacute; thể trưởng th&agrave;nh giống như Mạc Ph&agrave;m.<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng suy nghĩ nhiều, gần 1 năm n&agrave;y hắn đi l&agrave;m thợ săn đi săn giết y&ecirc;u ma kiếm tinh ph&aacute;ch, giết mấy trăm con y&ecirc;u ma nhưng m&agrave; tỉ lệ rớt xuống tinh ph&aacute;ch cũng thấp đ&aacute;ng thương chỉ c&oacute; 7 tinh ph&aacute;ch n&ocirc; bộc cấp c&ugrave;ng 1 tinh ph&aacute;ch chiến tướng cấp chỉ đổi được hơn 1 vạn điểm, tinh ph&aacute;ch n&ocirc; bộc 100 m&agrave; chiến tướng l&agrave; 10000, l&uacute;c mới đổi điểm xong hắn su&yacute;t ch&uacute;t nữa ngay giữa đ&aacute;m đ&ocirc;ng chửi to, cũng may hắn kiềm chế nổi.<br />\r\n<br />\r\nHắn cũng cảm thấy muốn nhờ v&agrave;o săn giết y&ecirc;u ma kiếm điểm tu h&agrave;nh c&ograve;n kh&ocirc;ng bằng ở nh&agrave; th&agrave;nh thật tu luyện c&ograve;n tốt hơn</span></span></p>', 1, '2022-03-16 03:26:35', '2022-03-16 03:26:35'),
(18, 2, 'Chương 1: Sơn biên tiểu thôn', 'chuong-1-son-bien-tieu-thon', '<p>qfqfqfqrqr</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>&nbsp;trợn trừng hai mắt, nh&igrave;n chằm chằm v&agrave;o n&oacute;c nh&agrave; được tạo th&agrave;nh từ cỏ dại v&agrave; b&ugrave;n trộn lẫn. To&agrave;n th&acirc;n hắn được tr&ugrave;m bởi một c&aacute;i &aacute;o b&ocirc;ng đ&atilde; cũ, ố v&agrave;ng, nh&igrave;n kh&ocirc;ng c&ograve;n ra h&igrave;nh dạng ban đ&acirc;u, phảng phất t&aacute;n ph&aacute;t ra một &iacute;t m&ugrave;i ẩm mốc.<br />\r\n<br />\r\nB&ecirc;n cạnh hắn c&ograve;n c&oacute; một người nữa, l&agrave; nhị ca H&agrave;n Ch&uacute;, đang ngủ rất say sưa. Thỉnh thoảng c&oacute; tiếng ng&aacute;y nh&egrave; nhẹ ph&aacute;t ra từ đ&oacute;.<br />\r\n<br />\r\nC&aacute;ch giường chừng nửa trượng, l&agrave; một v&aacute;ch tường đất đổ n&aacute;t, v&igrave; thời gian đ&atilde; qu&aacute; l&acirc;u, tr&ecirc;n v&aacute;ch tường đ&atilde; xuất hiện v&agrave;i vết nứt d&agrave;i. Từ những vết nứt đ&oacute;, lo&aacute;ng tho&aacute;ng truyền đến thanh &acirc;m o&aacute;n th&aacute;n của H&agrave;n mẫu, ngo&agrave;i ra c&ograve;n c&oacute; thanh &acirc;m H&agrave;n phụ đang h&uacute;t thuốc rất l&agrave; hấp dẫn.<br />\r\n<br />\r\n&#39;Anh ngố&#39; từ từ nhắm đ&ocirc;i mắt c&oacute; ch&uacute;t bức bối lại. Muốn thật nhanh ch&igrave;m v&agrave;o giấc ngủ s&acirc;u. Trong l&ograve;ng hắn biết r&otilde; r&agrave;ng, nếu b&acirc;y giờ m&agrave; c&ograve;n kh&ocirc;ng ngủ ngay, ng&agrave;y mai kh&ocirc;ng thể n&agrave;o dậy sớm được, cũng kh&ocirc;ng thể c&ugrave;ng đ&aacute;m bạn c&ugrave;ng đi đốn củi được.<br />\r\n<br />\r\n<em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>&nbsp;họ H&agrave;n t&ecirc;n Lập, loại danh tự c&oacute; &yacute; nghĩa như thế n&agrave;y cha mẹ hắn kh&ocirc;ng c&oacute; khả năng đưa ra. C&aacute;i n&agrave;y l&agrave; do phụ th&acirc;n hắn d&ugrave;ng rượu oa đầu chế bởi th&ocirc; lương, cầu l&atilde;o Trương trong th&ocirc;n đặt cho.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&atilde;o Trương khi c&ograve;n trẻ, đ&atilde; từng l&agrave;m thư đồng mấy năm cho một nh&agrave; c&oacute; tiền trong th&agrave;nh. L&agrave; người duy nhất trong th&ocirc;n nhận biết được v&agrave;i chữ. T&ecirc;n gọi của hầu hết tiểu h&agrave;i tử trong th&ocirc;n, đều l&agrave; do l&atilde;o Trương đặt cho.<br />\r\n<br />\r\nH&agrave;n Lập bị người trong th&ocirc;n gọi l&agrave;&nbsp;<em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>&nbsp;kh&ocirc;ng phải l&agrave; do hắn ngố hay đần thật sự, ngược lại, hắn c&ograve;n l&agrave; đứa trẻ th&ocirc;ng minh nhất l&agrave;ng, ngo&agrave;i ra tr&ocirc;ng hắn so với những đứa trẻ kh&aacute;c trong l&agrave;ng kh&ocirc;ng c&oacute; g&igrave; kh&aacute;c biệt. Trừ những người trong nh&agrave; ra, hắn rất &iacute;t khi nghe thấy nguời ta gọi t&ecirc;n ch&iacute;nh thức H&agrave;n Lập của hắn, m&agrave; hầu như chỉ l&agrave;&nbsp;<em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>, v&agrave; c&aacute;i t&ecirc;n&nbsp;<em><em><em><em><em>&quot;Anh ngố&quot;</em></em></em></em></em>&nbsp;n&agrave;y vẫn được sử dụng cho đến tận b&acirc;y giờ.<br />\r\n<br />\r\nSở dĩ H&agrave;n Lập bị mọi người ban cho hỗn danh&nbsp;<em><em>&quot;anh ngố&quot;</em></em>&nbsp;l&agrave; v&igrave; trong th&ocirc;n vốn đ&atilde; c&oacute; một&nbsp;<em>&quot;anh ngốc&quot;</em>&nbsp;rồi.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Điều n&agrave;y cũng kh&ocirc;ng c&oacute; g&igrave; to t&aacute;t cả, tất cả những đứa trẻ kh&aacute;c trong th&ocirc;n đều c&oacute; hỗn danh như&nbsp;<em>&quot;cẩu oa&quot;</em>&nbsp;hay&nbsp;<em>&quot;nhị đản&quot;</em>, so với danh tự&nbsp;<em><em>&quot;anh ngố&quot;</em></em>&nbsp;th&igrave; c&ograve;n kh&oacute; nghe hơn.<br />\r\n<br />\r\nCũng bởi vậy, H&agrave;n Lập mặc d&ugrave; kh&ocirc;ng th&iacute;ch c&aacute;ch xưng h&ocirc; n&agrave;y nhưng cũng chỉ c&oacute; thể tự an ủi m&igrave;nh m&agrave; th&ocirc;i.<br />\r\n<br />\r\nH&agrave;n Lập bề ngo&agrave;i tr&ocirc;ng kh&ocirc;ng được vừa mắt, da tay th&igrave; đen đ&uacute;a, đ&iacute;ch thực l&agrave; một đứa trẻ b&igrave;nh thường chốn l&agrave;ng qu&ecirc;. Tuy nhi&ecirc;n, nội t&acirc;m của cậu b&eacute; th&igrave; kh&ocirc;ng hề n&ocirc;ng nổi, so với những đứa b&eacute; c&ugrave;ng lứa tuổi th&igrave; ch&iacute;n chắn hơn nhiều. Hắn từ nhỏ đ&atilde; hướng tới thế giới phồn hoa b&ecirc;n ngo&agrave;i, mơ rằng c&oacute; một ng&agrave;y, hắn c&oacute; thể ra khỏi th&ocirc;n l&agrave;ng, đi xem xem c&aacute;i thế giới ph&ugrave; hoa m&agrave; l&atilde;o Trương thường n&oacute;i đến.<br />\r\n<br />\r\nKhi H&agrave;n Lập nghĩ đến &yacute; tưởng n&agrave;y, hắn kh&ocirc;ng d&aacute;m đề cập ra cho người kh&aacute;c biết. Nếu kh&ocirc;ng, nhất định l&agrave;m cho mọi người trong th&ocirc;n cảm thấy ngạc nhi&ecirc;n, một tiểu h&agrave;i tử miệng c&ograve;n chưa kh&ocirc; m&ugrave;i sữa, thế m&agrave; d&aacute;m mơ tưởng đến những &yacute; nghĩ xa vời m&agrave; ngay cả một người lớn cũng chưa d&aacute;m nghĩ đến. Cần phải biết rằng, những đưa trẻ kh&aacute;c c&ugrave;ng tuổi với H&agrave;n Lập th&igrave; tầm tuổi n&agrave;y chỉ biết đuổi g&agrave;, bắt ch&oacute;, tất nhi&ecirc;n l&agrave; ở đ&acirc;y sẽ kh&ocirc;ng n&oacute;i đến những kẻ c&oacute; &yacute; nghĩ tha huơng cầu thực.<br />\r\n<br />\r\nGia đ&igrave;nh H&agrave;n Lập c&oacute; bảy miệng ăn, tr&ecirc;n hắn c&oacute; hai vị huynh trưởng, một tỷ tỷ, hắn trong nh&agrave; đứng thứ tư, ngo&agrave;i ra hắn c&ograve;n c&oacute; một tiểu muội muội nữa. Năm nay hắn vừa mới mười tuổi, gia cảnh bần h&agrave;n, cả năm cũng kh&ocirc;ng c&oacute; mấy bữa được ăn no. Mọi người trong nh&agrave; đều chỉ mong được ăn đủ no, mặc đủ ấm.<br />\r\n<br />\r\nH&agrave;n Lập l&uacute;c n&agrave;y, đang mơ mơ m&agrave;ng m&agrave;ng, tuy ngủ m&agrave; chưa ngủ, trong đầu vẫn c&ograve;n phảng phất &yacute; niệm: Ng&agrave;y mai l&ecirc;n n&uacute;i, nhất định sẽ mang về cho tiểu muội muội m&agrave; hắn y&ecirc;u thương nhất thật nhiều hồng tương quả*, loại quả m&agrave; muội muội hắn th&iacute;ch nhất.<br />\r\n<br />\r\nV&agrave;o giữa trưa ng&agrave;y thứ hai, H&agrave;n Lập dưới &aacute;nh nắng ch&oacute;i trang, lưng g&ugrave;i b&oacute; củi cao bằng nửa người hắn, trước ngực th&igrave; &ocirc;m một nắm đầy hồng tương quả, đang từ ngọn n&uacute;i trở về nh&agrave;. L&uacute;c n&agrave;y, hắn kh&ocirc;ng hề biết rằng trong nh&agrave; đang c&oacute; một vị kh&aacute;ch đến chơi, m&agrave; vị kh&aacute;ch n&agrave;y, ch&iacute;nh l&agrave; người cải biến vận mệnh của hắn.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Vị qu&iacute; kh&aacute;ch n&agrave;y, c&ugrave;ng hắn c&oacute; mối quan hệ huyết thống rất gần, &ocirc;ng ta ch&iacute;nh l&agrave; tam th&uacute;c ruột của hắn.<br />\r\n<br />\r\nNghe n&oacute;i, trong v&ugrave;ng, tại tửu l&acirc;u ở tiểu th&agrave;nh phụ cận, được nguời ta t&iacute;n nhiệm đề bạt l&agrave;m đại chưởng quĩ, ch&iacute;nh l&agrave; người m&agrave; cha mẹ hắn thường n&oacute;i. H&agrave;n gia trong v&ograve;ng trăm năm trở lại đ&acirc;y, mới lại c&oacute; thể xuất hiện một người như tam th&uacute;c của H&agrave;n Lập.<br />\r\n<br />\r\nH&agrave;n Lập từ nhỏ cho đến giờ, gặp mặt vị tam th&uacute;c n&agrave;y cũng chỉ v&agrave;i lần. Đại ca của hắn được đi theo một l&atilde;o thợ r&egrave;n trong th&agrave;nh để học việc cũng l&agrave; do vị tam th&uacute;c n&agrave;y giới thiệu cho. Vị tam th&uacute;c n&agrave;y c&ograve;n thường xuy&ecirc;n giấu mọi người cấp cho cha mẹ hắn đồ ăn thức uống, chiếu cố tận t&igrave;nh gia đ&igrave;nh hắn. Cũng ch&iacute;nh v&igrave; vậy, ấn tượng của H&agrave;n Lập đối với vị tam th&uacute;c n&agrave;y rất l&agrave; tốt, hắn cũng biết rằng tuy cha mẹ hắn kh&ocirc;ng n&oacute;i ra miệng nhưng trong t&acirc;m cũng rất cảm k&iacute;ch.<br />\r\n<br />\r\nĐại ca hắn ch&iacute;nh l&agrave; niềm ki&ecirc;u h&atilde;nh của cả nh&agrave;, nghe n&oacute;i l&agrave;m thợ r&egrave;n học đồ, kh&ocirc;ng kể ăn ở, mỗi th&aacute;ng c&ograve;n nhận được ba mươi đồng bạc trắng, đợi đến l&uacute;c xuất sư, c&oacute; người thu&ecirc; th&igrave; tiền kiếm được c&ograve;n nhiều hơn nữa.<br />\r\n<br />\r\nMỗi khi cha mẹ đề cập đến đại ca, thần th&aacute;i đều bay bổng, tr&ocirc;ng kh&aacute;c hẳn so với thường ng&agrave;y. H&agrave;n Lập tuổi tuy nhỏ, nhưng cũng h&acirc;m mộ kh&ocirc;ng th&ocirc;i, c&ocirc;ng việc vừa l&ograve;ng sớm đ&atilde; c&oacute; rồi, đ&oacute; ch&iacute;nh l&agrave; theo một vị thủ nghệ sư ph&oacute; trong tiểu th&agrave;nh học tập nấu ăn, sau đ&oacute; sẽ trở th&agrave;nh một người nấu ăn c&oacute; tay nghề.<br />\r\n<br />\r\nNgay khi H&agrave;n Lập nh&igrave;n thấy một người to&agrave;n th&acirc;n diện y phục mới, khu&ocirc;n mặt b&eacute;o tr&ograve;n, th&igrave; biết ngay đ&oacute; l&agrave; tam th&uacute;c của m&igrave;nh, t&acirc;m l&yacute; v&ocirc; c&ugrave;ng hưng phấn.<br />\r\n<br />\r\nBỏ lại đ&aacute;m củi ra sau nh&agrave; xong, liền tiến l&ecirc;n nh&agrave; l&agrave;m lễ tham kiến tam th&uacute;c, ngoan ngo&atilde;n cất tiếng ch&agrave;o:&nbsp;<em>&quot;tam th&uacute;c hảo&quot;</em>, rồi sau đ&oacute; đứng y&ecirc;n một b&ecirc;n, nghe phụ mẫu v&agrave; tam th&uacute;c tr&ograve; chuyện phiếm.<br />\r\n<br />\r\nTam th&uacute;c cười cười nh&igrave;n H&agrave;n Lập, đ&aacute;nh gi&aacute; hắn một hồi, lu&ocirc;n miệng khen hắn những lời như l&agrave;&nbsp;<em>&quot;nghe lời&quot;</em>&nbsp;với&nbsp;<em>&quot;hiểu việc&quot;</em>, sau đ&oacute; lại quay đầu lại, tiếp tục tr&ograve; chuyện với phụ mẫu hắn về mục đ&iacute;ch chuyến đi lần n&agrave;y của l&atilde;o.<br />\r\n<br />\r\nH&agrave;n Lập tuổi c&ograve;n chưa lớn hẳn, n&ecirc;n khi nghe tam th&uacute;c n&oacute;i hắn cũng kh&ocirc;ng hiểu hết, chỉ l&agrave; hiểu được đại kh&aacute;i m&agrave; th&ocirc;i.<br />\r\n<br />\r\nNguy&ecirc;n lai l&agrave; tam th&uacute;c của hắn l&agrave;m việc ở một tiểu l&acirc;u, m&agrave; tiểu l&acirc;u n&agrave;y lại thuộc về một bang ph&aacute;i giang hồ c&oacute; t&ecirc;n l&agrave;&nbsp;<em>&quot;Thất huyền m&ocirc;n&quot;</em>. M&ocirc;n ph&aacute;i n&agrave;y chia ra l&agrave;m ngoại m&ocirc;n v&agrave; nội m&ocirc;n. C&aacute;ch đ&acirc;y kh&ocirc;ng l&acirc;u, tam th&uacute;c của hắn cũng đ&atilde; ch&iacute;nh thức trở th&agrave;nh đệ tử ngoại m&ocirc;n của m&ocirc;n ph&aacute;i đ&oacute;, v&agrave; c&oacute; thể đứng ra đề cử h&agrave;i đồng nhỏ tuổi ( từ bảy đến mười hai tuổi) tham gia khảo nghiệm chi&ecirc;u thu đệ tử của Thất huyền m&ocirc;n.<br />\r\n<br />\r\nCứ năm năm một lần, Thất huyền m&ocirc;n lại tổ chức kỳ thi chi&ecirc;u l&atilde;m đệ tử, m&agrave; cuộc thi n&agrave;y sẽ được tổ chức trong th&aacute;ng tới. Với một người kh&ocirc;ng kh&eacute;o, nhanh nhạy lại kh&ocirc;ng c&oacute; con c&aacute;i, tự nhi&ecirc;n phải nghĩ đến đứa ch&aacute;u H&agrave;n Lập c&oacute; độ tuổi th&iacute;ch hợp rồi.<br />\r\n<br />\r\nCha của H&agrave;n Lập vốn thận trọng, nghe đến những từ như&nbsp;<em>&quot;giang hồ&quot;</em>&nbsp;<em>&quot;m&ocirc;n ph&aacute;i&quot;</em>, trong l&ograve;ng c&oacute; ch&uacute;t do dự, kh&ocirc;ng đưa ra được chủ &yacute;, liền vớ ngay lấy điếu c&agrave;y, r&iacute;t l&ecirc;n mấy tiếng&nbsp;<em><em>&quot;ba th&aacute;p&quot;</em></em>&nbsp;<em><em>&quot;ba th&aacute;p&quot;</em></em>, sau đ&oacute; ngồi y&ecirc;n tại chỗ, kh&ocirc;ng n&oacute;i c&acirc;u g&igrave; nữa.<br />\r\n<br />\r\nTheo lời tam th&uacute;c n&oacute;i, th&igrave; trong phương vi&ecirc;n mấy trăm dặm, Thất huyền m&ocirc;n l&agrave; m&ocirc;n ph&aacute;i xếp thứ nhất, thứ hai g&igrave; đ&oacute;.<br />\r\n<br />\r\nChỉ cần trở th&agrave;nh đệ tử nội m&ocirc;n, chẳng những sau n&agrave;y, vừa miễn ph&iacute; tập v&otilde;, vừa kh&ocirc;ng phải lo lắng chuyện ăn uống, m&agrave; mỗi th&aacute;ng c&ograve;n kiếm được một hai lượng bạc lẻ nữa. Hơn nữa cho d&ugrave; kh&ocirc;ng tr&uacute;ng tuyển trở th&agrave;nh đệ tử nội m&ocirc;n, th&igrave; cũng c&oacute; hi vọng trở th&agrave;nh đệ tử ngoại m&ocirc;n giống như tam th&uacute;c của hắn, chuy&ecirc;n lo việc l&agrave;m sinh &yacute; cho Thất huyền m&ocirc;n.<br />\r\n<br />\r\nNghe đến c&oacute; khả năng mỗi th&aacute;ng kiếm được một hai lượng bạc trắng, lại c&ograve;n c&oacute; thể c&oacute; cơ hội trở th&agrave;nh người như tam th&uacute;c, H&agrave;n phụ cuối c&ugrave;ng cũng đưa ra chủ &yacute;, đ&aacute;p ứng lời đề nghị của tam th&uacute;c.<br />\r\n<br />\r\nTam th&uacute;c thấy H&agrave;n phụ đ&aacute;p ứng rồi, trong l&ograve;ng rất cao hứng. Liền lưu lại v&agrave;i lượng bạc, n&oacute;i th&aacute;ng sau sẽ đến dẫn H&agrave;n Lập đi, trong khoảng thời gian n&agrave;y, cho hắn ăn uống tốt hơn một ch&uacute;t, bồi bổ th&acirc;n thể để c&ograve;n c&oacute; thể tham gia ứng th&iacute;. Cuối c&ugrave;ng, tam th&uacute;c c&ugrave;ng cha mẹ hắn ch&agrave;o tạm biệt nhau, xoa xoa đầu hắn rồi ra khỏi cửa đi về.<br />\r\n<br />\r\nH&agrave;n Lập mặc d&ugrave; nghe kh&ocirc;ng ho&agrave;n to&agrave;n hiểu những g&igrave; tam th&uacute;c vừa n&oacute;i, nhưng c&oacute; khả năng v&agrave;o th&agrave;nh, c&oacute; thể kiếm tiền th&igrave; hắn nghe l&agrave; hiểu được.<br />\r\n<br />\r\nNguyện vọng của hắn từ trước đến nay, mắt thấy c&oacute; thể thực hiện được, hắn trong mấy buổi tối li&ecirc;n tiếp, hưng phấn n&ecirc;n ngủ kh&ocirc;ng y&ecirc;n.<br />\r\n<br />\r\nMột th&aacute;ng sau, tam th&uacute;c của hắn theo đ&uacute;ng thời gian ước định quay lại th&ocirc;n để dẫn H&agrave;n Lập đi. Trước khi đi, H&agrave;n phụ dặn d&ograve;, động vi&ecirc;n, ch&uacute;c ph&uacute;c H&agrave;n Lập: L&agrave;m người phải th&agrave;nh thật, gặp chuyện th&igrave; n&ecirc;n nhẫn nhịn, kh&ocirc;ng n&ecirc;n c&ugrave;ng người kh&aacute;c tranh chấp, m&agrave; H&agrave;n mẫu dặn hắn cần phải ch&uacute; &yacute; th&acirc;n thể, ăn ngủ cho tốt.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngồi tr&ecirc;n xe ngựa, nh&igrave;n th&acirc;n ảnh cha mẹ xa dần, H&agrave;n Lập cắn chặt h&agrave;m răng, cố gắng k&igrave;m n&eacute;n kh&ocirc;ng cho nước mắt chảy ra ngo&agrave;i.<br />\r\n<br />\r\nTuy hắn so với những đứa trẻ c&ugrave;ng trang lứa th&igrave; ch&iacute;n chắn hơn, nhưng d&ugrave; sao th&igrave; hắn vẫn c&ograve;n l&agrave; một đứa trẻ mới mười tuổi, lần đầu ti&ecirc;n xa nh&agrave; l&agrave;m cho hắn cảm thấy c&oacute; ch&uacute;t b&agrave;ng ho&agrave;ng, thương cảm. Trong t&acirc;m l&yacute; c&ograve;n non dại của hắn đ&atilde; hạ quyết t&acirc;m, đợi đến khi kiếm được tiền rồi sẽ ngay lập tức rong ngựa trở về với cha mẹ, kh&ocirc;ng xa rời nhau nữa.<br />\r\n<br />\r\nH&agrave;n Lập từ trước kh&ocirc;ng c&oacute; nghĩ đến, chỉ l&agrave; sau khi rời khỏi th&ocirc;n l&agrave;ng hắn mới nhận ra, tiền đ&atilde; kh&ocirc;ng c&ograve;n &yacute; nghĩa với hắn như l&uacute;c xưa nữa rồi. M&agrave; hắn mặc nhi&ecirc;n kh&ocirc;ng biết rằng, con đường vận mệnh của hắn kh&ocirc;ng giống như những người b&igrave;nh thường. Con đường tu ti&ecirc;n của hắn bắt đầu từ đ&acirc;y.<br />\r\n<br />\r\n* hồng tương quả: H&igrave;nh như l&agrave; quả trứng c&aacute;</span></span></p>', 1, '2022-03-22 08:14:31', '2022-03-22 15:37:26');
INSERT INTO `chapters` (`id`, `book_id`, `name`, `slug`, `description`, `content`, `active`, `created_at`, `updated_at`) VALUES
(19, 15, 'Chương 8: Lễ Nhập Môn', 'chuong-8-le-nhap-mon', '<p>qfasdasdqrqrqr</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">4 th&aacute;ng sau.Quảng trường Nh&acirc;n H&atilde;nh.Trung t&acirc;m quảng trường, rất đ&ocirc;ng người đang chờ để được kiểm tra tư chất.<br />\r\nHai b&ecirc;n bục c&oacute; khoảng mười s&aacute;u bục kh&aacute;c, tượng trưng cho mười ẩn m&ocirc;n v&agrave; s&aacute;u m&ocirc;n ph&aacute;i lớn nhất v&ugrave;ng.<br />\r\n<br />\r\nB&ecirc;n phải n&oacute; c&oacute; một bục của Nhật Thần Điện v&agrave; bục b&ecirc;n cạnh của Nguyệt Thần Điện, b&ecirc;n tr&aacute;i c&oacute; bục của Thi&ecirc;n Thần Cung v&agrave; Địa Ma Cung.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&igrave;m m&atilde;i n&oacute; mới thấy được bục của Vạn Hoa cốc, một bục kh&aacute; kh&aacute;c biệt v&igrave; c&oacute; th&ecirc;m một vườn hoa ph&iacute;a trước, v&agrave; tr&ecirc;n bục chỉ c&oacute; nữ, trong đ&oacute; người dẫn đầu l&agrave; một nữ tử, Quốc t&igrave;m hiểu tu vi người n&agrave;y th&igrave; nhận được kết quả...!- B&iacute;p! Cấp bậc qu&aacute; cao n&ecirc;n kh&ocirc;ng thể kiểm tra.Thế n&agrave;y th&igrave; Quốc nghĩ: - Chết cmnr! Cấp cao thế n&agrave;y sợ m&igrave;nh b&aacute;o danh xong cho m&igrave;nh ăn một chưởng gục lu&ocirc;n!Từng đứa một l&ecirc;n tr&ecirc;n một c&aacute;i bục đ&atilde; được dựng sẵn.Theo như th&ocirc;ng tin của Quốc c&oacute; được từ Bằng Hữu, tư chất gồm c&aacute;c loại từ thấp đến cao l&agrave; Binh Nhất - Sĩ Quan - Tướng Qu&acirc;n - Đại Tướng - Đại Vương - Đại Đế - Đế Vương.Tới lượt n&oacute;...!- Huỳnh Kiến Quốc! - Tư chất Đế Vương!Một tiếng nổ b&ugrave;ng l&ecirc;n, chủ yếu l&agrave; từ người của Huỳnh gia v&agrave; người h&acirc;m mộ Quốc.<br />\r\n<br />\r\n<br />\r\nTới lượt những đứa kh&aacute;c, c&oacute; đứa tới được tư chất Đại Vương l&agrave; c&ugrave;ng, ri&ecirc;ng anh trai Quốc l&agrave; Kiến Gia c&oacute; tư chất Đại Đế.V&igrave; sao mọi người lại reo h&ograve;? V&igrave; người ta gọi chung t&aacute;m tộc xung quanh Nhị thập Cửu T&uacute; l&agrave; Đế Vương Tộc, ai c&oacute; tư chất Đế Vương ở Nhị Thập Cửu T&uacute; c&oacute; thể biến th&agrave;nh bất kỳ tộc n&agrave;o trong số đ&oacute;.<br />\r\n<br />\r\nTuy nhi&ecirc;n cũng c&oacute; một số trường hợp như l&agrave; Thượng sư &Aacute;nh S&aacute;ng Legenire c&oacute; tư chất Đế Vương nhưng kh&ocirc;ng thể biến h&igrave;nh, kh&ocirc;ng ai biết nhưng Quốc biết tại sao, đ&oacute; l&agrave; v&igrave; Legenire kh&ocirc;ng c&oacute; m&aacute;u Thần, Ti&ecirc;n, Ma hay Quỷ v&agrave; Long, L&acirc;n, Quy, Phụng thể m&agrave; chỉ l&agrave; Nh&acirc;n.<br />\r\n<br />\r\n- B&acirc;y giờ, đến phần nhập m&ocirc;n, mỗi người chọn một m&ocirc;n ph&aacute;i m&agrave; m&igrave;nh muốn theo học v&agrave; chờ đợi sự đồng &yacute; của c&aacute;c chưởng m&ocirc;n.</span></span><br />\r\n&nbsp;</p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><ins><ins><ins><iframe frameborder=\"0\" height=\"0\" id=\"aswift_3\" name=\"aswift_3\" scrolling=\"no\" src=\"https://googleads.g.doubleclick.net/pagead/ads?client=ca-pub-3829250631432207&amp;output=html&amp;h=280&amp;slotname=6345663334&amp;adk=8246782&amp;adf=4209436308&amp;pi=t.ma~as.6345663334&amp;w=1140&amp;fwrn=4&amp;fwrnh=100&amp;lmt=1647962854&amp;rafmt=1&amp;psa=1&amp;format=1140x280&amp;url=https%3A%2F%2Ftruyenfull.vn%2Fba-vuong-vu-tru%2Fchuong-8%2F&amp;fwr=0&amp;fwrattr=true&amp;rpe=1&amp;resp_fmts=3&amp;wgl=1&amp;adsid=ChAI8OflkQYQ-rPukdKX2LspEjkAVRpIwXraAe6pOX2d6sUxvgkt43Dv96IRBKuin9FbPTG6i8DkWEUTZAslQWCSYsa7vRtz0mosSYM&amp;uach=WyJXaW5kb3dzIiwiMTQuMC4wIiwieDg2IiwiIiwiOTkuMC40ODQ0Ljc0IixbXSxudWxsLG51bGwsIjY0IixbWyIgTm90IEE7QnJhbmQiLCI5OS4wLjAuMCJdLFsiQ2hyb21pdW0iLCI5OS4wLjQ4NDQuNzQiXSxbIkdvb2dsZSBDaHJvbWUiLCI5OS4wLjQ4NDQuNzQiXV0sZmFsc2Vd&amp;dt=1647962868331&amp;bpp=4&amp;bdt=1949&amp;idt=5&amp;shv=r20220317&amp;mjsv=m202203160101&amp;ptt=9&amp;saldr=aa&amp;abxe=1&amp;cookie=ID%3D650f3694456a24f1-226f339d19d100ca%3AT%3D1647960592%3ART%3D1647960592%3AS%3DALNI_MZHtX5a03vizqcBjcq1nZjmd-zB_w&amp;prev_fmts=0x0%2C1140x280&amp;nras=1&amp;correlator=6812152620254&amp;frm=20&amp;pv=1&amp;ga_vid=723971894.1647960592&amp;ga_sid=1647962868&amp;ga_hid=1943110105&amp;ga_fc=1&amp;u_tz=420&amp;u_his=6&amp;u_h=864&amp;u_w=1536&amp;u_ah=816&amp;u_aw=1536&amp;u_cd=24&amp;u_sd=1.25&amp;dmc=8&amp;adx=190&amp;ady=3315&amp;biw=1519&amp;bih=746&amp;scr_x=0&amp;scr_y=400&amp;eid=44759876%2C44759927%2C44759837%2C31065371%2C44760494%2C31063247%2C31065654&amp;oid=2&amp;pvsid=627287931430022&amp;pem=1&amp;tmod=2144006766&amp;uas=1&amp;nvt=1&amp;ref=https%3A%2F%2Ftruyenfull.vn%2Fba-vuong-vu-tru%2F&amp;eae=0&amp;fc=1920&amp;brdim=0%2C0%2C0%2C0%2C1536%2C0%2C1536%2C816%2C1536%2C746&amp;vis=1&amp;rsz=o%7C%7CeEbr%7C&amp;abl=CS&amp;pfx=0&amp;fu=128&amp;bc=31&amp;ifi=4&amp;uci=a!4&amp;btvi=1&amp;fsb=1&amp;xpc=OoUcLH8VYS&amp;p=https%3A//truyenfull.vn&amp;dtd=M\" width=\"1140\"></iframe></ins></ins></ins></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">- Huỳnh Kiến Quốc, con chọn m&ocirc;n ph&aacute;i n&agrave;o? - Vạn Hoa Cốc!Một trường im lặng, bầu kh&ocirc;ng kh&iacute; bỗng trở n&ecirc;n u &aacute;m.<br />\r\n<br />\r\nQuốc biết rằng m&igrave;nh đ&atilde; n&oacute;i ra một điều ngu ngốc, v&igrave; Vạn hoa Cốc vốn dĩ chỉ thu nhận đệ tử l&agrave; nữ, như Kiều c&ocirc; nương n&oacute; gặp mấy th&aacute;ng trước th&igrave; được, chứ như n&oacute; đi v&agrave;o l&agrave; bị người ta cho ăn một chưởng ngủm củ tỏi ngay tại cổng v&agrave;o &agrave;, chưa kể hiện giờ nữ tử trước đ&oacute; đang nh&igrave;n hắn v&agrave; lăm lăm nắm đấm.<br />\r\n<br />\r\n- Đừng lo, ta c&oacute; lệnh b&agrave;i của cốc chủ Vạn Hoa Cốc, n&ecirc;n ta c&oacute; quyền b&aacute;i nhập v&agrave;o Vạn Hoa Cốc.N&oacute;i tới đ&acirc;y, Quốc nghe được v&agrave;i tiếng chửi n&oacute; từ ph&iacute;a bọn nh&oacute;c kia, nhưng n&oacute; mặc kệ, tiến tới bục của Vạn Hoa cốc, đưa ra tấm lệnh b&agrave;i cho nữ tử đại diện.N&agrave;ng xem x&eacute;t tấm lệnh b&agrave;i một ch&uacute;t rồi bảo n&oacute; l&agrave; một canh nữa đến đ&acirc;y để b&aacute;i m&ocirc;n.Về nh&agrave;, n&oacute; thu xếp đồ đạc, kể cũng kh&ocirc;ng nhiều, chỉ c&oacute; mấy cuốn s&aacute;ch về v&ocirc; h&igrave;nh học m&agrave; Tấn cho n&oacute; h&ocirc;m qua, mấy cuốn s&aacute;ch về hữu h&igrave;nh học của Thắng, v&agrave;i bộ c&ocirc;ng ph&aacute;p, mấy bộ quần &aacute;o.<br />\r\n<br />\r\n<br />\r\nXong xu&ocirc;i, n&oacute; xuống Tr&ugrave; ph&ograve;ng ăn ch&uacute;t ch&aacute;o rồi đi đến quảng trường.L&uacute;c n&agrave;y, quảng trường Nh&acirc;n H&atilde;nh đầy người, đa số l&agrave; đến để b&aacute;i m&ocirc;n, phần c&ograve;n lại hoặc đi học hỏi kinh nghiệm, ch&agrave;o hỏi nhau hoặc l&agrave; tiễn biệt người th&acirc;n.N&oacute; bước đến chỗ Vạn Hoa cốc, l&uacute;c n&agrave;y c&aacute;i bục đ&atilde; h&oacute;a th&agrave;nh một con thuyền khổng lồ, chở đầy người, tất cả đều l&agrave; nữ, n&oacute; ngại ch&agrave;o hỏi n&ecirc;n đi thẳng l&ecirc;n t&agrave;u.Vừa bước l&ecirc;n t&agrave;u, n&oacute; chọn một khoảng trống rồi lấy c&acirc;y đ&agrave;n guitar mua trong B&aacute; Vương thương h&agrave;nh ra đ&aacute;nh một bản, nhờ kiếp ở Tr&aacute;i Đất c&oacute; học l&oacute;m guitar ngo&agrave;i ch&ugrave;a n&ecirc;n giờ biết đ&aacute;nh một ch&uacute;t.<br />\r\n<br />\r\nXui sao, n&oacute; muốn lẩn tr&aacute;nh, m&agrave; n&oacute; đ&agrave;n hay qu&aacute; cho n&ecirc;n mấy c&ocirc; nương gần đ&oacute; cứ bu lại, n&oacute; kệ, cứ miệt m&agrave;i đ&aacute;nh, ai m&agrave; biết được l&agrave; n&oacute; đang cố t&igrave;nh lấy le với g&aacute;i....4 canh sau.Vạn Hoa cốc.N&oacute; đang đứng trước một t&ograve;a n&uacute;i cao sừng sững, xung quanh l&agrave; rất nhiều n&uacute;i nhỏ, tr&ecirc;n mỗi t&ograve;a n&uacute;i lại c&oacute; v&agrave;i t&ograve;a kiến tr&uacute;c kiểu Trung Hoa cổ, c&oacute; &iacute;t nhất trăm t&ograve;a kiến tr&uacute;c kiểu như vậy tr&ecirc;n một ngọn cao sơn.<br />\r\n<br />\r\n- Bằng Hữu! M&agrave;y c&ograve;n ở đ&oacute; kh&ocirc;ng? - G&igrave; vậy ba? Đang ngủ.L&acirc;u nay kh&ocirc;ng thấy Bằng Hữu l&ecirc;n tiếng, n&oacute; tưởng Bằng Hữu pay khỏi Hệ thống rồi, ai d&egrave; đ&acirc;u...!- Mấy l&acirc;u nay đi ăn tiệc ở nh&agrave; tụi bạn, qu&ecirc;n mất ti&ecirc;u c&oacute; thằng cha đang ng&oacute;ng chờ ở nh&agrave;.<br />\r\n<br />\r\n- Sao b&acirc;y giờ m&agrave;y n&oacute;i như thể tao l&agrave; c&aacute;i thằng cha đang ng&oacute;ng chờ ở nh&agrave; thế? - Th&igrave; thằng cha đ&oacute; l&agrave; cậu chủ đ&oacute;, ăn tiệc về mệt qu&aacute; ngủ lu&ocirc;n.<br />\r\n<br />\r\n- Kệ mẹ m&agrave;y, mở ra th&ocirc;ng số....Chủ nh&acirc;n: Ho&agrave;ng QuốcTư chất: Đế VươngThể chất: Long L&acirc;n Quy Phụng thể.Huyết mạch: Thần Ti&ecirc;n Ma Quỷ huyết.Chức nghiệp: Kh&ocirc;ng.Hồn cảnh: Ma Ảnh trung kỳ đỉnh phongTi&ecirc;n cảnh: Ti&ecirc;n Tướng hậu kỳ.Thần cảnh: Trụ cảnh tứ tinh.Ma cảnh: Ma Tướng tam qu&acirc;n.Quỷ cảnh: Tiểu Quỷ trung kỳ.Kh&iacute; cảnh: V&ocirc; H&igrave;nh thể sơ kỳ.Huyết cảnh: V&ocirc; Huyết đỉnh phong.Thể cảnh: B&aacute;n Gi&aacute;p cảnh.Ngũ H&agrave;nh cảnh: Sơ cấp.Nguy&ecirc;n Tố cảnh: Hồng Thủy hậu kỳ.Linh cảnh: Linh Nam cấp 5.B&aacute;t Qu&aacute;i cảnh: Th&aacute;i Cực trung kỳ.Đạo cảnh: Sơ cấp....!- Quốc ơi, &ocirc;ng nội tới đ&oacute;n con.<br />\r\n<br />\r\n<br />\r\n- Ạch.<br />\r\n<br />\r\nL&agrave;m g&igrave; vậy &ocirc;ng nội? - Hai th&aacute;ng nữa c&oacute; cuộc thi li&ecirc;n gia tộc, gia tộc ta tuyển con đi thi, v&igrave; mấy gia tộc b&ecirc;n kia nghe n&oacute;i Huỳnh gia ta c&oacute; tư chất Đế Vương m&agrave; kh&ocirc;ng lật b&agrave;i, bảo ta nu&ocirc;i hoa lồng k&iacute;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Con thu xếp đồ đạc đi để về nh&agrave; luyện..</span></span></p>', 1, '2022-03-22 08:32:14', '2022-03-22 15:36:16');
INSERT INTO `chapters` (`id`, `book_id`, `name`, `slug`, `description`, `content`, `active`, `created_at`, `updated_at`) VALUES
(20, 3, 'Chương 1: Lộ Hiểu Nguyệt', 'chuong-1-lo-hieu-nguyet', '<p>afaf</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trời chiều rọi qua cửa sổ cabin đập thẳng v&agrave;o mắt, t&ocirc;i mới nhận ra đ&atilde; chạng vạng tối. Nh&igrave;n ra ngo&agrave;i lần nữa, thấy mặt s&ocirc;ng đ&atilde; bị sắc đỏ của mặt trời bao tr&ugrave;m, trời v&agrave; nước c&ugrave;ng một m&agrave;u, đỏ rực như m&aacute;u.<br />\r\n<br />\r\n&ldquo;Hiểu Nguyệt, đến đảo Nhật Canh rồi.&rdquo; Phương To&agrave;n đứng ở cửa sổ b&ecirc;n kia, quay đầu sang n&oacute;i với t&ocirc;i. T&ocirc;i nh&igrave;n sang hướng Đ&ocirc;ng Bắc, bờ nam s&ocirc;ng Lạc Quỳ mới lờ mờ đ&oacute; m&agrave; giờ đ&atilde; hiện r&otilde;, một mảng m&agrave;u xanh l&aacute; xen &aacute;nh v&agrave;ng, nh&igrave;n kỹ một ch&uacute;t sẽ thấy hoa hướng dương lộng lẫy với những c&aacute;nh hoa m&agrave;u v&agrave;ng vươn cao giữa kh&ocirc;ng trung. Giờ trời đ&atilde; ngả về T&acirc;y, từng đo&aacute; hoa hướng về ph&iacute;a c&oacute; &aacute;nh s&aacute;ng, khảm ra viền hoa m&agrave;u v&agrave;ng kim.<em>T&ocirc;i chợt nhớ ra L&ecirc; Nhật Hu&acirc;n bảo c&oacute; thể tuỳ &yacute; sử dụng điện thoại, t&ocirc;i b&egrave;n cầm m&aacute;y b&agrave;n nội bộ l&ecirc;n. Mẹ đang ở nh&agrave;, t&ocirc;i vội n&oacute;i v&agrave;i c&acirc;u, d&ugrave; sao d&ugrave;ng điện thoại người kh&aacute;c để gọi đường d&agrave;i cũng kh&ocirc;ng tốt lắm, sau đ&oacute; b&egrave;n c&uacute;p m&aacute;y.</em><em>&ldquo;&Agrave;, Hiểu Nguyệt, Vạn Nam Phong cũng học trường ch&uacute;ng ta đấy, em cũng phải gọi l&agrave; đ&agrave;n anh mới đ&uacute;ng.&rdquo; Phương To&agrave;n nhận ra t&ocirc;i kh&ocirc;ng vui n&ecirc;n vội giải ho&agrave;, &ldquo;Đ&agrave;n anh Vạn cũng l&agrave;m việc ở Cảnh Nặc, chức vị cao hơn anh&hellip;&rdquo;</em><br />\r\n<br />\r\nKhởi h&agrave;nh từ s&aacute;ng sớm tới chạng vạng tối cuối c&ugrave;ng cũng đ&atilde; cập bến. T&ocirc;i cười với Phương To&agrave;n, thần kinh mệt mỏi nhưng c&oacute; phần phấn kh&iacute;ch v&igrave; đ&atilde; đến được nơi cần đến. Cảnh quan thi&ecirc;n nhi&ecirc;n v&agrave; kiến tr&uacute;c hiện r&otilde; mồn một trước mắt l&agrave;m dấy l&ecirc;n sự kỳ vọng trong t&ocirc;i, c&oacute; vẻ như nghe lời đ&agrave;n anh tới đ&acirc;y l&agrave; một quyết định đ&uacute;ng đắn. Sống 1 tuần ở đ&acirc;y hẳn sẽ kh&ocirc;ng uổng ph&iacute;, t&ocirc;i nghĩ thầm.&ldquo;Gọi t&ocirc;i Hiểu Nguyệt l&agrave; được rồi&rdquo; Để &yacute; nhầm người rồi, bẽ mặt qu&aacute;. Nhưng d&ugrave; sao anh ta cũng xứng với H&agrave; U Đ&agrave;m.<br />\r\n<br />\r\n&ldquo;Sao vậy, mệt rồi &agrave;?&rdquo; Cuối c&ugrave;ng Phương To&agrave;n cũng ra d&aacute;ng đ&agrave;n anh của đo&agrave;n, tỏ ra quan t&acirc;m, &ldquo;Xin lỗi nh&eacute;, anh sơ &yacute; qu&aacute;, kh&ocirc;ng nghĩ tới Hiểu Nguyệt l&agrave; nữ sinh n&ecirc;n thể chất kh&ocirc;ng thể so được với nam sinh&hellip;&rdquo;<em>Vạn Nam Phong lập tức kh&ocirc;ng d&aacute;m n&oacute;i g&igrave; nữa, t&ocirc;i nh&igrave;n thấy người tr&ecirc;n b&agrave;n đa phần đều c&oacute; ch&uacute;t hả h&ecirc;.T&ocirc;i bật cười, &ldquo;Đ&agrave;n anh, mục ti&ecirc;u của anh l&agrave; bảo vệ những c&ocirc; g&aacute;i yếu đuối trong thi&ecirc;n hạ m&agrave;, nhưng em r&otilde; r&agrave;ng c&ograve;n c&aacute;ch chữ yếu đuối xa lắm, anh kh&ocirc;ng cần phải thận trọng thế đ&acirc;u.&rdquo;</em><em>Phương To&agrave;n n&oacute;i mẹ H&agrave; U Đ&agrave;m l&agrave; quản gia tr&ecirc;n đảo n&agrave;y, b&igrave;nh thường tr&ecirc;n đảo Nhật Canh chỉ c&oacute; mẹ c&ocirc; ấy ở lại, H&agrave; U Đ&agrave;m thi thoảng sẽ đến thăm. Nh&agrave; họ Cảnh chỉ đến đ&acirc;y nghỉ ph&eacute;p khi v&agrave;o h&egrave;, H&agrave; U Đ&agrave;m vẫn c&ograve;n đi học th&igrave; tự xưng l&agrave; người gi&uacute;p việc thời vụ rồi đến gi&uacute;p mẹ &iacute;t việc vặt v&atilde;nh. Thực ra H&agrave; U Đ&agrave;m đ&atilde; nhiều lần khuy&ecirc;n mẹ rời khỏi đảo, nhưng mẹ H&agrave; ki&ecirc;n quyết kh&ocirc;ng chịu.</em><br />\r\n<br />\r\nT&ocirc;i bật cười, &ldquo;Đ&agrave;n anh, mục ti&ecirc;u của anh l&agrave; bảo vệ những c&ocirc; g&aacute;i yếu đuối trong thi&ecirc;n hạ m&agrave;, nhưng em r&otilde; r&agrave;ng c&ograve;n c&aacute;ch chữ yếu đuối xa lắm, anh kh&ocirc;ng cần phải thận trọng thế đ&acirc;u.&rdquo;<em>Hả? Giống ai cơ?</em>&ldquo;Sao lại kh&ocirc;ng l&ecirc;n mạng được? C&oacute; khi n&agrave;o anh chưa cắm d&acirc;y kh&ocirc;ng?&rdquo; L&ecirc; Nhật Hu&acirc;n kh&ocirc;ng th&egrave;m để &yacute; tới việc anh ta đang thở hồng hộc m&agrave; chỉ chậm r&atilde;i hỏi.<br />\r\n<br />\r\n&ldquo;Anh đang quan t&acirc;m đến em đấy! Đ&uacute;ng l&agrave;&hellip;&rdquo; Phương To&agrave;n bắt đầu lải nhải, t&ocirc;i chỉ cảm thấy ch&acirc;n m&igrave;nh lắc lư rồi thuyền bỗng dừng lại, cắt ngang tiếng ph&agrave;n n&agrave;n của Phương To&agrave;n.<em>&ldquo;Điện thoại cũng kh&ocirc;ng c&oacute; t&iacute;n hiệu, chỗ qu&aacute;i g&igrave; thế n&agrave;y!&rdquo; Vạn Nam Phong vẫn c&ograve;n ph&agrave;n n&agrave;n.</em><em>&ldquo;Phương To&agrave;n, sao muộn thế n&agrave;y mới đến? Ai cha, lần n&agrave;y c&ograve;n dẫn theo người đẹp đến &agrave;? Bạn g&aacute;i hả?&rdquo; Một c&ocirc; g&aacute;i rất xinh đẹp chừng 24, 25 tuổi đứng l&ecirc;n, &aacute;nh mắt khiến người ta kh&ocirc;ng thể kh&ocirc;ng ch&igrave;m v&agrave;o trong đ&oacute;. C&ocirc; ấy ch&agrave;o Phương To&agrave;n, Phương To&agrave;n cũng cười đ&aacute;p lại c&ocirc; ấy, xem ra cả hai rất th&acirc;n thiết với nhau. T&ocirc;i nghĩ c&oacute; lẽ c&ocirc; ấy l&agrave; một trong hai chủ nh&acirc;n hiện tại của tập đo&agrave;n Cảnh Nặc, L&ecirc; Nhật Hu&acirc;n. B&igrave;nh thường nghe Phương To&agrave;n nhắc tới c&ocirc; ấy cũng hơi mơ hồ, giờ đ&atilde; thấy được người thật mới hiểu tại sao anh kh&ocirc;ng n&oacute;i g&igrave; nhiều &mdash; C&ocirc; ấy đẹp th&igrave; đẹp thật, nhưng lại mang theo một ch&uacute;t ph&ugrave; phiếm, n&oacute;i mới v&agrave;i c&acirc;u m&agrave; đ&atilde; nh&aacute;y mắt 4 5 lần với Phương To&agrave;n. B&igrave;nh thường Phương To&agrave;n cũng hay đ&ugrave;a, nhưng từ trước tới nay vẫn lu&ocirc;n lịch sự v&agrave; tốt bụng. Tuy anh ấy vẫn treo nụ cười tr&ecirc;n m&ocirc;i, nhưng dường như đ&atilde; c&oacute; phần mất tự nhi&ecirc;n.</em><br />\r\n<br />\r\nĐến rồi, đảo Nhật Canh m&agrave; đ&agrave;n anh đ&atilde; n&oacute;i, một mảnh đất thi&ecirc;n nhi&ecirc;n vẫn chưa bị ph&aacute; huỷ ho&agrave;n to&agrave;n.<em>T&ocirc;i lắc đầu, quăng suy nghĩ n&agrave;y ra khỏi đầu m&igrave;nh. Cha thường n&oacute;i với t&ocirc;i rằng &ldquo;Ba c&aacute;i vui th&igrave; trẻ, ba c&aacute;i bẽ th&igrave; gi&agrave;&rdquo;, vẫn n&ecirc;n cố gắng đừng nghĩ g&igrave; nhiều nữa th&igrave; hơn.</em><br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i đi theo H&agrave; U Đ&agrave;m l&ecirc;n bờ, c&ocirc; ấy bước kh&ocirc;ng nhanh, nhưng kh&ocirc;ng hiểu sao l&ecirc;n bờ hơi kh&oacute;, c&oacute; lẽ l&agrave; do bước ch&acirc;n của c&ocirc; ấy. N&oacute;i thật th&igrave; l&uacute;c đầu tr&ocirc;ng thấy c&oacute; người đứng ở bờ Nam đ&oacute;n m&igrave;nh v&agrave; đ&agrave;n anh, t&ocirc;i c&oacute; hơi ngạc nhi&ecirc;n. V&oacute;c người c&ocirc; ấy kh&aacute; cao, nhưng d&aacute;ng người th&igrave; gầy đến mức yếu đuối. Cộng th&ecirc;m bộ v&aacute;y trắng c&agrave;ng đem lại cảm gi&aacute;c nhỏ b&eacute;, v&agrave; cả m&aacute;i t&oacute;c đen chạm vai mang theo phong c&aacute;ch cổ điển nữa. T&ocirc;i thực sự kh&ocirc;ng thể tưởng tượng được chủ nh&acirc;n đảo Nhật Canh đang nghĩ g&igrave; m&agrave; lại để một c&ocirc; g&aacute;i như thế ra đ&oacute;n kh&aacute;ch, lại c&ograve;n bắt c&ocirc; ấy l&aacute;i thuyền tới nữa! Lẽ n&agrave;o đ&agrave;n &ocirc;ng tr&ecirc;n đảo n&agrave;y chết hết rồi &agrave;? Nếu l&agrave; t&ocirc;i, chắc chắn t&ocirc;i kh&ocirc;ng thể t&agrave;n nhẫn như vậy được.<em>Cứ như thế, n&oacute;i chung cơm tối cũng ăn vui vẻ. Mẹ H&agrave; U Đ&agrave;m kh&ocirc;ng l&ecirc;n d&ugrave;ng bữa chung, 9 người ch&uacute;ng t&ocirc;i, 3 cặp đ&ocirc;i cộng th&ecirc;m t&ocirc;i, Phương To&agrave;n v&agrave; Cảnh Thịnh. Trừ t&ocirc;i ra, mọi người đều đ&atilde; c&oacute; c&ocirc;ng ăn việc l&agrave;m, hơn nữa đa phần đều l&agrave;m ở Cảnh Nặc n&ecirc;n kh&aacute; th&acirc;n thiết nhau, n&oacute;i tới n&oacute;i lui cũng khiến bầu kh&ocirc;ng kh&iacute; trở n&ecirc;n th&acirc;n thiện hơn. C&oacute; lẽ v&igrave; c&oacute; t&ocirc;i ở đ&acirc;y n&ecirc;n họ đều tr&aacute;nh b&agrave;n luận tới chuyện kinh tế v&agrave; những tr&ograve; đ&ugrave;a bẩn thỉu, Vạn Nam Phong thậm ch&iacute; c&ograve;n t&igrave;m một số chủ đề về nh&agrave; trường, gi&aacute;o sư c&aacute;c thứ để n&oacute;i chuyện với t&ocirc;i. Anh ta lớn hơn Phương To&agrave;n 2 kh&oacute;a, biết chuyện cũ của Hoa Đại hơn ch&uacute;ng t&ocirc;i nhiều. T&ocirc;i kh&ocirc;ng thể n&agrave;o bắt được trọng t&acirc;m c&acirc;u chuyện của anh ta, lại c&ograve;n phải nhận lấy &aacute;nh mắt th&ugrave; địch của Lương Địch.</em><em>(*) Thiết bị để truy nhập Internet tốc độ cao v&agrave; sử dụng c&aacute;c dịch vụ tr&ecirc;n Internet một c&aacute;ch nhanh hơn.</em><br />\r\n<br />\r\nPhương To&agrave;n n&oacute;i mẹ H&agrave; U Đ&agrave;m l&agrave; quản gia tr&ecirc;n đảo n&agrave;y, b&igrave;nh thường tr&ecirc;n đảo Nhật Canh chỉ c&oacute; mẹ c&ocirc; ấy ở lại, H&agrave; U Đ&agrave;m thi thoảng sẽ đến thăm. Nh&agrave; họ Cảnh chỉ đến đ&acirc;y nghỉ ph&eacute;p khi v&agrave;o h&egrave;, H&agrave; U Đ&agrave;m vẫn c&ograve;n đi học th&igrave; tự xưng l&agrave; người gi&uacute;p việc thời vụ rồi đến gi&uacute;p mẹ &iacute;t việc vặt v&atilde;nh. Thực ra H&agrave; U Đ&agrave;m đ&atilde; nhiều lần khuy&ecirc;n mẹ rời khỏi đảo, nhưng mẹ H&agrave; ki&ecirc;n quyết kh&ocirc;ng chịu.<em>&ldquo;Hả? Thương hương tiếc ngọc &agrave;?&rdquo; L&ecirc; Nhật Hu&acirc;n gh&eacute; s&aacute;t Phương To&agrave;n, m&oacute;ng tay lướt tr&ecirc;n bả vai anh ấy, &ldquo;Chỉ l&agrave; v&agrave;i ba c&acirc;u đ&ugrave;a mở chuyện th&ocirc;i m&agrave; anh đ&atilde; đau l&ograve;ng rồi. Đ&acirc;u cần phải trọng sắc khinh bạn thế &mdash;&rdquo;</em><em>&ldquo;Anh Lạc, c&oacute; nghe U Đ&agrave;m nhắc tới anh, nhưng đ&acirc;y lại l&agrave; lần đầu gặp mặt.&rdquo; Phương To&agrave;n vươn tay ra, &ldquo;Quả nhi&ecirc;n l&agrave; vẻ ngo&agrave;i phi ph&agrave;m, U Đ&agrave;m cũng biết chọn người.&rdquo;</em><br />\r\n<br />\r\nĐảo hoang, phụ nữ lớn tuổi. Đầu &oacute;c tư duy của t&ocirc;i bắt đầu vận động, tưởng tượng ra cuộc sống hằng ng&agrave;y của mẹ H&agrave; tr&ecirc;n đảo n&agrave;y. Khu hoa hướng dương rộng lớn thế n&agrave;y, c&oacute; phải l&agrave; do b&agrave; ấy gieo xuống trong c&ocirc; đơn kh&ocirc;ng? Hoa hướng dương th&igrave; hướng về ph&iacute;a mặt trời, c&ograve;n mẹ H&agrave; th&igrave; hướng đến nơi n&agrave;o?Mặt t&ocirc;i hơi ửng đỏ l&ecirc;n, t&ocirc;i thấy như c&oacute; lửa n&oacute;ng hơ qua m&aacute; m&igrave;nh, cực kỳ xấu hổ. T&ocirc;i vội lắc đầu xua tay: &ldquo;Kh&ocirc;ng kh&ocirc;ng kh&ocirc;ng! T&ocirc;i với đ&agrave;n anh kh&ocirc;ng phải quan hệ như thế đ&acirc;u! Mọi người hiểu lầm rồi!&rdquo;Đảo hoang, phụ nữ lớn tuổi. Đầu &oacute;c tư duy của t&ocirc;i bắt đầu vận động, tưởng tượng ra cuộc sống hằng ng&agrave;y của mẹ H&agrave; tr&ecirc;n đảo n&agrave;y. Khu hoa hướng dương rộng lớn thế n&agrave;y, c&oacute; phải l&agrave; do b&agrave; ấy gieo xuống trong c&ocirc; đơn kh&ocirc;ng? Hoa hướng dương th&igrave; hướng về ph&iacute;a mặt trời, c&ograve;n mẹ H&agrave; th&igrave; hướng đến nơi n&agrave;o?</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dưới sự dẫn đường của H&agrave; U Đ&agrave;m, ch&uacute;ng t&ocirc;i rất nhanh đ&atilde; đến gần kiến tr&uacute;c duy nhất tr&ecirc;n đảo &mdash; Biệt thự Lạc Quỳ. Đ&agrave;n anh đ&atilde; từng nhắc tới to&agrave; nh&agrave; c&oacute; kiến tr&uacute;c nổi danh trong giới n&agrave;y nhiều lần: Tạo h&igrave;nh như hoa hướng dương, sảnh lớn h&igrave;nh tr&ograve;n, cạnh ngo&agrave;i l&agrave; một v&ograve;ng căn ph&ograve;ng h&igrave;nh c&aacute;nh hoa. M&agrave;u sắc tươi s&aacute;ng m&agrave; đậm, bố cục kỳ lạ, thực sự l&agrave; một kiệt t&aacute;c hiếm thấy.<em>Dưới sự dẫn đường của H&agrave; U Đ&agrave;m, ch&uacute;ng t&ocirc;i rất nhanh đ&atilde; đến gần kiến tr&uacute;c duy nhất tr&ecirc;n đảo &mdash; Biệt thự Lạc Quỳ. Đ&agrave;n anh đ&atilde; từng nhắc tới to&agrave; nh&agrave; c&oacute; kiến tr&uacute;c nổi danh trong giới n&agrave;y nhiều lần: Tạo h&igrave;nh như hoa hướng dương, sảnh lớn h&igrave;nh tr&ograve;n, cạnh ngo&agrave;i l&agrave; một v&ograve;ng căn ph&ograve;ng h&igrave;nh c&aacute;nh hoa. M&agrave;u sắc tươi s&aacute;ng m&agrave; đậm, bố cục kỳ lạ, thực sự l&agrave; một kiệt t&aacute;c hiếm thấy.</em><em>Phương To&agrave;n cười khổ: &ldquo;Chị L&ecirc; ơi, Hiểu Nguyệt l&agrave; đ&agrave;n em của t&ocirc;i m&agrave;. Em ấy c&ograve;n nhỏ tuổi lại chưa trải đời, c&ocirc; đừng c&oacute; l&ocirc;i em ấy ra đ&ugrave;a nữa.&rdquo;</em><br />\r\n<br />\r\nKiến tr&uacute;c xuất sắc chưa chắc l&agrave; nơi dễ ở, đ&acirc;y l&agrave; suy nghĩ đầu ti&ecirc;n của t&ocirc;i khi bước v&agrave;o sảnh lớn. Sảnh lớn h&igrave;nh tr&ograve;n, xung quanh đều l&agrave; cửa ph&ograve;ng, thực sự kh&ocirc;ng phải kiểu bố cục khiến người ta thoải m&aacute;i. Sự tương phản giữa sảnh lớn m&agrave;u đen v&agrave; những căn ph&ograve;ng m&agrave;u v&agrave;ng b&ecirc;n ngo&agrave;i cũng khiến một kẻ kh&ocirc;ng phải &ldquo;nghệ sĩ&rdquo; như t&ocirc;i cảm thấy kh&oacute; hiểu. Trong sảnh được trang tr&iacute; cực kỳ hoa lệ, dưới đ&egrave;n ch&ugrave;m thuỷ tinh c&oacute; v&agrave;i người đang ngồi tr&ecirc;n bộ ghế s&ocirc; pha cao cấp tr&ograve; chuyện, thấy ch&uacute;ng t&ocirc;i bước v&agrave;o, c&oacute; 3 người đứng dậy, c&ograve;n lại vẫn ngồi.<em>&ldquo;Anh đang quan t&acirc;m đến em đấy! Đ&uacute;ng l&agrave;&hellip;&rdquo; Phương To&agrave;n bắt đầu lải nhải, t&ocirc;i chỉ cảm thấy ch&acirc;n m&igrave;nh lắc lư rồi thuyền bỗng dừng lại, cắt ngang tiếng ph&agrave;n n&agrave;n của Phương To&agrave;n.</em><br />\r\n<br />\r\n&ldquo;Phương To&agrave;n, sao muộn thế n&agrave;y mới đến? Ai cha, lần n&agrave;y c&ograve;n dẫn theo người đẹp đến &agrave;? Bạn g&aacute;i hả?&rdquo; Một c&ocirc; g&aacute;i rất xinh đẹp chừng 24, 25 tuổi đứng l&ecirc;n, &aacute;nh mắt khiến người ta kh&ocirc;ng thể kh&ocirc;ng ch&igrave;m v&agrave;o trong đ&oacute;. C&ocirc; ấy ch&agrave;o Phương To&agrave;n, Phương To&agrave;n cũng cười đ&aacute;p lại c&ocirc; ấy, xem ra cả hai rất th&acirc;n thiết với nhau. T&ocirc;i nghĩ c&oacute; lẽ c&ocirc; ấy l&agrave; một trong hai chủ nh&acirc;n hiện tại của tập đo&agrave;n Cảnh Nặc, L&ecirc; Nhật Hu&acirc;n. B&igrave;nh thường nghe Phương To&agrave;n nhắc tới c&ocirc; ấy cũng hơi mơ hồ, giờ đ&atilde; thấy được người thật mới hiểu tại sao anh kh&ocirc;ng n&oacute;i g&igrave; nhiều &mdash; C&ocirc; ấy đẹp th&igrave; đẹp thật, nhưng lại mang theo một ch&uacute;t ph&ugrave; phiếm, n&oacute;i mới v&agrave;i c&acirc;u m&agrave; đ&atilde; nh&aacute;y mắt 4 5 lần với Phương To&agrave;n. B&igrave;nh thường Phương To&agrave;n cũng hay đ&ugrave;a, nhưng từ trước tới nay vẫn lu&ocirc;n lịch sự v&agrave; tốt bụng. Tuy anh ấy vẫn treo nụ cười tr&ecirc;n m&ocirc;i, nhưng dường như đ&atilde; c&oacute; phần mất tự nhi&ecirc;n.Cảnh Thịnh biến sắc, t&ocirc;i nh&igrave;n thấy thế th&igrave; sự nghi ngờ lại c&agrave;ng tăng th&ecirc;m. Vạn Nam Phong bị Cảnh Thịnh v&agrave; L&ecirc; Nhật Hu&acirc;n cướp lời n&ecirc;n sắc mặt hết sức kh&oacute; coi. Ch&uacute;ng t&ocirc;i n&oacute;i chuyện được một l&aacute;t, anh ta đ&atilde; bu&ocirc;ng b&aacute;t: &ldquo;T&ocirc;i ăn no rồi, về ph&ograve;ng đ&acirc;y.&rdquo;<br />\r\n<br />\r\n&ldquo;Hiểu Nguyệt, đ&acirc;y l&agrave; L&ecirc; Nhật Hu&acirc;n, l&atilde;nh đạo trực tiếp của anh, một trong hai người phụ tr&aacute;ch tập đo&agrave;n Cảnh Nặc. C&ograve;n một vị nữa l&agrave; Cảnh Thịnh&hellip;&rdquo; Anh giới thiệu người đ&agrave;n &ocirc;ng kh&aacute;c đang đứng l&ecirc;n với t&ocirc;i, sau đ&oacute; th&igrave; giới thiệu t&ocirc;i với họ, &ldquo;Đ&acirc;y l&agrave; đ&agrave;n em của t&ocirc;i, Lộ Hiểu Nguyệt.&rdquo;<em>(*) Thiết bị để truy nhập Internet tốc độ cao v&agrave; sử dụng c&aacute;c dịch vụ tr&ecirc;n Internet một c&aacute;ch nhanh hơn.</em><br />\r\n<br />\r\n&ldquo;C&ocirc; học Đại học Hoa Hối ư? Khoa n&agrave;o vậy?&rdquo; L&uacute;c &aacute;nh mắt t&ocirc;i đang dừng tr&ecirc;n người t&ecirc;n Cảnh Thịnh, một người đ&agrave;n &ocirc;ng đang ngồi đột nhi&ecirc;n hỏi.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Khoa Kỹ thuật m&ocirc;i trường, l&agrave; đ&agrave;n em trực tiếp của đ&agrave;n anh Phương To&agrave;n.&rdquo; T&ocirc;i đ&aacute;p, trong l&ograve;ng hơi kh&oacute; chịu. Người đ&agrave;n &ocirc;ng đ&oacute; diện mạo cũng được xem l&agrave; đẹp trai nhưng th&aacute;i độ lại cực kỳ th&ocirc; lỗ, n&oacute;i chuyện như thể ra lệnh vậy, mang theo ch&uacute;t cảm gi&aacute;c trịch thượng.Ăn cơm, ăn cơm th&ocirc;i.<em>Kiến tr&uacute;c xuất sắc chưa chắc l&agrave; nơi dễ ở, đ&acirc;y l&agrave; suy nghĩ đầu ti&ecirc;n của t&ocirc;i khi bước v&agrave;o sảnh lớn. Sảnh lớn h&igrave;nh tr&ograve;n, xung quanh đều l&agrave; cửa ph&ograve;ng, thực sự kh&ocirc;ng phải kiểu bố cục khiến người ta thoải m&aacute;i. Sự tương phản giữa sảnh lớn m&agrave;u đen v&agrave; những căn ph&ograve;ng m&agrave;u v&agrave;ng b&ecirc;n ngo&agrave;i cũng khiến một kẻ kh&ocirc;ng phải &ldquo;nghệ sĩ&rdquo; như t&ocirc;i cảm thấy kh&oacute; hiểu. Trong sảnh được trang tr&iacute; cực kỳ hoa lệ, dưới đ&egrave;n ch&ugrave;m thuỷ tinh c&oacute; v&agrave;i người đang ngồi tr&ecirc;n bộ ghế s&ocirc; pha cao cấp tr&ograve; chuyện, thấy ch&uacute;ng t&ocirc;i bước v&agrave;o, c&oacute; 3 người đứng dậy, c&ograve;n lại vẫn ngồi.</em><br />\r\n<br />\r\nAnh ta hơi biến sắc, khẽ lẩm bẩm g&igrave; đ&oacute; nhưng t&ocirc;i vẫn c&oacute; thể nghe được: &ldquo;Cũng l&agrave; Kỹ thuật m&ocirc;i trường của Hoa Hối, cũng hơi&hellip; Gi&ocirc;ng giống&hellip;&rdquo;<em>&ldquo;Hiểu Nguyệt, đến đảo Nhật Canh rồi.&rdquo; Phương To&agrave;n đứng ở cửa sổ b&ecirc;n kia, quay đầu sang n&oacute;i với t&ocirc;i. T&ocirc;i nh&igrave;n sang hướng Đ&ocirc;ng Bắc, bờ nam s&ocirc;ng Lạc Quỳ mới lờ mờ đ&oacute; m&agrave; giờ đ&atilde; hiện r&otilde;, một mảng m&agrave;u xanh l&aacute; xen &aacute;nh v&agrave;ng, nh&igrave;n kỹ một ch&uacute;t sẽ thấy hoa hướng dương lộng lẫy với những c&aacute;nh hoa m&agrave;u v&agrave;ng vươn cao giữa kh&ocirc;ng trung. Giờ trời đ&atilde; ngả về T&acirc;y, từng đo&aacute; hoa hướng về ph&iacute;a c&oacute; &aacute;nh s&aacute;ng, khảm ra viền hoa m&agrave;u v&agrave;ng kim.</em><br />\r\n<br />\r\nHả? Giống ai cơ?<em>&ldquo;Hiểu Nguyệt lần đầu tới ở tr&ecirc;n đảo, Cảnh Thịnh, anh nhất định phải đối xử tốt với c&ocirc; ấy đấy nh&eacute;.&rdquo; L&ecirc; Nhật Hu&acirc;n n&oacute;i với Cảnh Thịnh nhưng &aacute;nh mắt lại nh&igrave;n về ph&iacute;a t&ocirc;i, mang theo ch&uacute;t &yacute; vị s&acirc;u xa, &ldquo;Hiểu Nguyệt, tr&ecirc;n đảo n&agrave;y em muốn đi đ&acirc;u cũng được. Chẳng phải em muốn khảo s&aacute;t sao? Cứ nghi&ecirc;m t&uacute;c nghi&ecirc;n cứu mấy thứ &ldquo;đặc biệt&rdquo; đ&oacute; đi!&rdquo;</em><br />\r\n<br />\r\n&ldquo;&Agrave;, Hiểu Nguyệt, Vạn Nam Phong cũng học trường ch&uacute;ng ta đấy, em cũng phải gọi l&agrave; đ&agrave;n anh mới đ&uacute;ng.&rdquo; Phương To&agrave;n nhận ra t&ocirc;i kh&ocirc;ng vui n&ecirc;n vội giải ho&agrave;, &ldquo;Đ&agrave;n anh Vạn cũng l&agrave;m việc ở Cảnh Nặc, chức vị cao hơn anh&hellip;&rdquo;<em>Ch&uacute;ng t&ocirc;i tiếp tục ăn cơm, d&ugrave; hơi mất hứng nhưng rất nhanh đ&atilde; kh&ocirc;i phục lại trạng th&aacute;i n&aacute;o nhiệt. L&uacute;c mọi người đang vui vẻ, t&ocirc;i nghe thấy một tiếng &ldquo;Ầm&rdquo; vang l&ecirc;n từ cửa ph&ograve;ng Vạn Nam Phong, Vạn Nam Phong lập tức vọt ra ngo&agrave;i.</em><br />\r\n<br />\r\nAnh ấy lại giới thiệu tiếp, một c&ocirc; g&aacute;i đang ngồi kh&aacute;c l&agrave; bạn g&aacute;i của Vạn Nam Phong, Lương Địch. C&ocirc; ta trang điểm kh&ocirc;ng tệ, nh&igrave;n qua cũng c&oacute; v&agrave;i phần nhan sắc. Bạn trai của L&ecirc; Nhật Hu&acirc;n t&ecirc;n l&agrave; Hứa N&atilde;i Phu, một c&aacute;i t&ecirc;n hơi lạ tai. T&ocirc;i kh&ocirc;ng thể kh&ocirc;ng thừa nhận, chỉ cần nh&igrave;n tho&aacute;ng qua đ&atilde; biết họ l&agrave; một cặp rồi, một c&ocirc; g&aacute;i ngả ngớn v&agrave; một ch&agrave;ng trai tuỳ tiện ranh m&atilde;nh, &aacute;nh mắt hắn ta nh&igrave;n t&ocirc;i như ch&oacute; săn nh&igrave;n thấy con mồi vậy, kh&ocirc;ng khỏi khiến t&ocirc;i cảm thấy gh&eacute;t bỏ.<br />\r\n<br />\r\nTrong số 5 người đ&atilde; c&oacute; 2 cặp t&igrave;nh nh&acirc;n, chỉ c&oacute; Cảnh Thịnh l&agrave; độc th&acirc;n. T&ocirc;i nh&igrave;n sang H&agrave; U Đ&agrave;m đang đứng một b&ecirc;n, cảm thấy c&ocirc; ấy lại kh&aacute; xứng đ&ocirc;i với người t&ecirc;n Cảnh Thịnh lịch sự n&agrave;y, kh&ocirc;ng biết họ c&oacute; quan hệ mập mờ g&igrave; kh&ocirc;ng.&ldquo;C&ocirc; học Đại học Hoa Hối ư? Khoa n&agrave;o vậy?&rdquo; L&uacute;c &aacute;nh mắt t&ocirc;i đang dừng tr&ecirc;n người t&ecirc;n Cảnh Thịnh, một người đ&agrave;n &ocirc;ng đang ngồi đột nhi&ecirc;n hỏi.<br />\r\n<br />\r\n&ldquo;U Đ&agrave;m, b&aacute;c g&aacute;i gọi em đến ph&ograve;ng bếp gi&uacute;p k&igrave;a.&rdquo; Một c&aacute;nh cửa trong v&ograve;ng tr&ograve;n mở ra, một người đ&agrave;n &ocirc;ng đi tới b&ecirc;n cạnh H&agrave; U Đ&agrave;m rồi hạ giọng n&oacute;i. H&agrave; U Đ&agrave;m cười với anh ta: &ldquo;Được rồi, em đi ngay đ&acirc;y.&rdquo;<em>T&ocirc;i bước tới cạnh cửa sổ, k&eacute;o r&egrave;m cửa ra. Kh&ocirc;ng biết lựa chọn n&agrave;y l&agrave; đ&uacute;ng hay sai nữa.</em><br />\r\n<br />\r\nHai mắt t&ocirc;i s&aacute;ng l&ecirc;n, 4 người đ&agrave;n &ocirc;ng trong ph&ograve;ng kh&aacute;ch n&agrave;y, Phương To&agrave;n, Cảnh Thịnh, Vạn Nam Phong, Hứa N&atilde;i Phu, d&ugrave; kh&iacute; chất hay diện mạo đều cực kỳ xuất ch&uacute;ng. Nhưng người đ&agrave;n &ocirc;ng n&agrave;y vừa bước ra đ&atilde; ngay lập tức l&agrave;m lu mờ những người c&ograve;n lại. Chiếc &aacute;o sơ mi đơn giản c&ugrave;ng quần b&ograve; &ocirc;m lấy d&aacute;ng người ti&ecirc;u chuẩn của anh ta, dưới m&aacute;i t&oacute;c đen hơi d&agrave;i l&agrave; một đ&ocirc;i mắt s&aacute;ng đến kinh người. Anh ta để &yacute; tới &aacute;nh mắt t&ocirc;i n&ecirc;n nở nụ cười với t&ocirc;i, trong t&iacute;ch tắc kh&ocirc;ng gian đều tối sầm lại, chỉ c&ograve;n &aacute;nh s&aacute;ng từ anh ta.<em>L&uacute;c t&ocirc;i thả ống nghe xuống, đ&egrave;n đỏ &ldquo;Đang d&ugrave;ng&rdquo; vẫn đang lo&eacute; s&aacute;ng, lập tức s&ocirc;i m&aacute;u, một l&uacute;c l&acirc;u sau mới k&igrave;m lại được &mdash; D&ugrave; sao t&ocirc;i cũng chỉ l&agrave; một &ldquo;kh&aacute;ch trọ&rdquo; nho nhỏ, l&agrave;m g&igrave; c&oacute; quyền giận chứ.</em><br />\r\n<br />\r\nT&ocirc;i kh&ocirc;ng k&igrave;m được m&agrave; cười đ&aacute;p lại anh ta, anh ta hơi giật m&igrave;nh, n&eacute;t mặt bỗng thay đổi, mắt nh&igrave;n nh&igrave;n t&ocirc;i cũng hơi trầm xuống. T&ocirc;i nh&igrave;n thấy &aacute;nh mắt n&agrave;y, trong l&ograve;ng hơi bất an. Kh&ocirc;ng phải l&agrave; &aacute;nh mắt dơ bẩn như của Vạn Nam Phong v&agrave; Hứa N&atilde;i Phu, nhưng&hellip;<em>&ldquo;&Agrave;! Qu&ecirc;n gọi điện về nh&agrave; rồi!&rdquo; Qu&ecirc;n gọi b&aacute;o b&igrave;nh an, d&ugrave; cha mẹ lu&ocirc;n rất y&ecirc;n t&acirc;m về t&ocirc;i, nhưng vẫn n&ecirc;n gọi điện để trấn an họ. T&ocirc;i cầm điện thoại l&ecirc;n, m&agrave;n h&igrave;nh điện thoại hiển thị đang t&igrave;m kiếm t&iacute;n hiệu. T&ocirc;i nh&iacute;u m&agrave;y lại, sao đến cả Thần Ch&acirc;u H&agrave;nh* cũng k&eacute;m thế n&agrave;y chứ? Đến cả t&iacute;n hiệu c&ograve;n kh&ocirc;ng c&oacute;, thế m&agrave; tự xưng l&agrave; Internet mạnh nhất.</em><br />\r\n<br />\r\n&ldquo;C&ocirc; l&agrave; c&ocirc; Đường đ&uacute;ng kh&ocirc;ng? T&ocirc;i l&agrave; Lạc Ph&agrave;m, bạn trai của U Đ&agrave;m.&rdquo; Anh ta lập tức rời mắt đi, gật đầu với t&ocirc;i ch&agrave;o hỏi.<br />\r\n<br />\r\n&ldquo;Gọi t&ocirc;i Hiểu Nguyệt l&agrave; được rồi&rdquo; Để &yacute; nhầm người rồi, bẽ mặt qu&aacute;. Nhưng d&ugrave; sao anh ta cũng xứng với H&agrave; U Đ&agrave;m.<br />\r\n<br />\r\n&ldquo;Anh Lạc, c&oacute; nghe U Đ&agrave;m nhắc tới anh, nhưng đ&acirc;y lại l&agrave; lần đầu gặp mặt.&rdquo; Phương To&agrave;n vươn tay ra, &ldquo;Quả nhi&ecirc;n l&agrave; vẻ ngo&agrave;i phi ph&agrave;m, U Đ&agrave;m cũng biết chọn người.&rdquo;<em>(*)Thẻ/sim điện thoại.</em><br />\r\n<br />\r\nPhương To&agrave;n v&agrave; Lạc Ph&agrave;m tr&ograve; chuyện v&agrave;i c&acirc;u, t&ocirc;i lại cảm nhận được r&otilde; r&agrave;ng c&oacute; một &aacute;nh mắt kh&ocirc;ng thiện cảm g&igrave;, kh&ocirc;ng phải nh&igrave;n t&ocirc;i m&agrave; l&agrave; nh&igrave;n bọn họ &mdash; Hoặc c&oacute; thể kh&ocirc;ng chỉ vỏn vẹn một &aacute;nh mắt. T&ocirc;i nh&igrave;n theo hướng &aacute;nh mắt đ&oacute;, đối phương lập tức rời mắt đi, kh&ocirc;ng nh&igrave;n ra được l&agrave; ai hết.<em>&ldquo;Nam Phong, chẳng ai &eacute;p anh tới chỗ qu&aacute;i quỷ n&agrave;y hết.&rdquo; L&ecirc; Nhật Hu&acirc;n giận t&aacute;i mặt, &ldquo;Anh muốn đi cứ đi, l&uacute;c n&agrave;o cũng được.&rdquo;</em><br />\r\n<br />\r\nT&ocirc;i c&oacute; được đ&agrave;n anh kể v&agrave;i c&acirc;u, chỉ biết h&ograve;n đảo n&agrave;y được tập đo&agrave;n Cảnh Nặc phụ tr&aacute;ch khai ph&aacute; &mdash; Đ&acirc;y l&agrave; lời giải th&iacute;ch c&ocirc;ng khai, n&oacute;i c&aacute;ch kh&aacute;c th&igrave; đ&acirc;y l&agrave; h&ograve;n đảo ri&ecirc;ng của Gi&aacute;m đốc tập đo&agrave;n Cảnh Nặc &mdash; M&agrave; đ&agrave;n anh đang đảm nhiệm một chức vụ quan trong trong tập đo&agrave;n Cảnh Nặc, tất nhi&ecirc;n sẽ c&oacute; quan hệ tốt với hai người đang phụ tr&aacute;ch Cảnh Nặc l&agrave; L&ecirc; Nhật Hu&acirc;n v&agrave; Cảnh Thịnh, v&igrave; thế cũng được mời đến nghỉ ph&eacute;p tr&ecirc;n đảo. Thường ng&agrave;y đ&agrave;n anh hay kể cho t&ocirc;i nghe về phong cảnh thi&ecirc;n nhi&ecirc;n v&agrave; d&acirc;n phong tr&ecirc;n đảo Nhật Canh, trước đ&acirc;y t&ocirc;i nghĩ chỉ cần nghỉ trong nh&agrave; nhỏ l&agrave; được, kh&ocirc;ng cần phiền tới chủ nh&acirc;n n&ecirc;n cũng đi theo tới. Nhưng giờ nh&igrave;n lại dường như mọi thứ kh&ocirc;ng hề đơn giản như trong tưởng tượng của t&ocirc;i ch&uacute;t n&agrave;o, tr&ecirc;n đảo chỉ c&oacute; một biệt thự h&igrave;nh hoa hướng dương n&agrave;y l&agrave; c&oacute; thể ở, m&agrave; người trong n&agrave;y&hellip; H&igrave;nh như cũng kh&aacute; phức tạp.<em>Nếu&hellip;</em><br />\r\n<br />\r\nT&ocirc;i vẫn lu&ocirc;n k&eacute;n chọn những người m&igrave;nh tiếp x&uacute;c, nếu c&oacute; &yacute; đồ xấu với t&ocirc;i th&igrave; t&ocirc;i lại c&agrave;ng b&agrave;i x&iacute;ch. Vỗ nhẹ tr&aacute;n, lại bắt đầu thấy đau đầu rồi đ&acirc;y.<em>T&ocirc;i nằm tr&ecirc;n giường, nh&igrave;n bầu trời gần sụp tối &mdash; Ph&ograve;ng n&agrave;y c&oacute; một c&aacute;i cửa sổ s&aacute;t đất h&igrave;nh bầu dục hơi đ&aacute;ng sợ, đem lại cho người ta cảm gi&aacute;c kh&ocirc;ng c&oacute; lấy ch&uacute;t ri&ecirc;ng tư n&agrave;o hết. Phương To&agrave;n n&oacute;i với t&ocirc;i rằng từ ph&ograve;ng c&oacute; thể nh&igrave;n ra được thế giới bao la b&ecirc;n ngo&agrave;i, đ&uacute;ng l&agrave; kh&ocirc;ng ngoa ch&uacute;t n&agrave;o. Nhưng l&uacute;c n&agrave;y ngo&agrave;i cửa sổ chỉ c&oacute; b&oacute;ng c&acirc;y ảm đạm v&agrave; một mảng hoa hướng dương qu&aacute; mức rực rỡ khiến l&ograve;ng t&ocirc;i cảm thấy hơi ngột ngạt. Cảm thấy rất buồn bực, nhưng kh&ocirc;ng hiểu l&agrave; do đ&acirc;u. Nỗi bất an trong l&ograve;ng c&agrave;ng l&uacute;c c&agrave;ng lớn, dường như t&ocirc;i đ&atilde; bỏ s&oacute;t chuyện g&igrave; đ&oacute;.&ldquo;Sao vậy, mệt rồi &agrave;?&rdquo; Cuối c&ugrave;ng Phương To&agrave;n cũng ra d&aacute;ng đ&agrave;n anh của đo&agrave;n, tỏ ra quan t&acirc;m, &ldquo;Xin lỗi nh&eacute;, anh sơ &yacute; qu&aacute;, kh&ocirc;ng nghĩ tới Hiểu Nguyệt l&agrave; nữ sinh n&ecirc;n thể chất kh&ocirc;ng thể so được với nam sinh&hellip;&rdquo;</em></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&ldquo;Đ&uacute;ng l&agrave;, Phương To&agrave;n, anh l&agrave;m bảo vệ cho người đẹp kiểu g&igrave; thế hả, c&ocirc; Đường đi đường x&oacute;c nảy cả ng&agrave;y, đồ ngốc như anh th&igrave; chịu nổi chứ sao c&ocirc; ấy chịu được, c&ograve;n kh&ocirc;ng mau dẫn người ta đi nghỉ đi k&igrave;a?&rdquo; L&ecirc; Nhật Hu&acirc;n để &yacute; tới động t&aacute;c của t&ocirc;i, đến nắm lấy t&ocirc;i, &ldquo;C&ocirc; Đường&hellip; T&ocirc;i gọi c&ocirc; l&agrave; Hiểu Nguyệt c&oacute; được kh&ocirc;ng?&rdquo;<br />\r\n<br />\r\nT&ocirc;i khẽ gật đầu: &ldquo;Đừng gọi l&agrave; c&ocirc; Đường nữa, t&ocirc;i kh&ocirc;ng nhận ra đ&acirc;u.&rdquo;<br />\r\n<br />\r\n&ldquo;Hiểu Nguyệt, c&ocirc; với Phương To&agrave;n ở chung hay l&agrave; ở 2 ph&ograve;ng?&rdquo; C&ocirc; ta rất thoải m&aacute;i hỏi t&ocirc;i.<em>Anh ấy lại giới thiệu tiếp, một c&ocirc; g&aacute;i đang ngồi kh&aacute;c l&agrave; bạn g&aacute;i của Vạn Nam Phong, Lương Địch. C&ocirc; ta trang điểm kh&ocirc;ng tệ, nh&igrave;n qua cũng c&oacute; v&agrave;i phần nhan sắc. Bạn trai của L&ecirc; Nhật Hu&acirc;n t&ecirc;n l&agrave; Hứa N&atilde;i Phu, một c&aacute;i t&ecirc;n hơi lạ tai. T&ocirc;i kh&ocirc;ng thể kh&ocirc;ng thừa nhận, chỉ cần nh&igrave;n tho&aacute;ng qua đ&atilde; biết họ l&agrave; một cặp rồi, một c&ocirc; g&aacute;i ngả ngớn v&agrave; một ch&agrave;ng trai tuỳ tiện ranh m&atilde;nh, &aacute;nh mắt hắn ta nh&igrave;n t&ocirc;i như ch&oacute; săn nh&igrave;n thấy con mồi vậy, kh&ocirc;ng khỏi khiến t&ocirc;i cảm thấy gh&eacute;t bỏ.</em><br />\r\n<br />\r\nMặt t&ocirc;i hơi ửng đỏ l&ecirc;n, t&ocirc;i thấy như c&oacute; lửa n&oacute;ng hơ qua m&aacute; m&igrave;nh, cực kỳ xấu hổ. T&ocirc;i vội lắc đầu xua tay: &ldquo;Kh&ocirc;ng kh&ocirc;ng kh&ocirc;ng! T&ocirc;i với đ&agrave;n anh kh&ocirc;ng phải quan hệ như thế đ&acirc;u! Mọi người hiểu lầm rồi!&rdquo;<em>&ldquo;Nam Phong, anh vẫn n&ecirc;n chăm s&oacute;c Lương Địch cho tốt đi!&rdquo; Cảnh Thịnh ra mặt ho&agrave; giải, rồi lại quay sang n&oacute;i với Phương To&agrave;n, &ldquo;Phương To&agrave;n, mai t&ocirc;i cũng muốn đưa đ&agrave;n em của anh đi dạo tr&ecirc;n đảo một ch&uacute;t, tận t&igrave;nh thể hiện l&agrave; một người chủ th&acirc;n thiện, được kh&ocirc;ng?&rdquo;</em><br />\r\n<br />\r\n&ldquo;Kh&ocirc;ng phải quan hệ &ldquo;đ&oacute;&rdquo;, đ&oacute; l&agrave; quan hệ &ldquo;n&agrave;o&rdquo; vậy?&rdquo; L&ecirc; Nhật Hu&acirc;n n&oacute;i xong xo&aacute;y mắt v&agrave;o t&ocirc;i, &ldquo;To&agrave;n c&oacute; người bạn g&aacute;i xinh đẹp thế n&agrave;y m&agrave; ch&uacute;ng t&ocirc;i cũng kh&ocirc;ng biết, c&oacute; phải hơi qu&aacute; đ&aacute;ng rồi kh&ocirc;ng? Anh n&oacute;i xem n&ecirc;n phạt thế n&agrave;o đ&acirc;y?&rdquo;<br />\r\n<br />\r\nPhương To&agrave;n cười khổ: &ldquo;Chị L&ecirc; ơi, Hiểu Nguyệt l&agrave; đ&agrave;n em của t&ocirc;i m&agrave;. Em ấy c&ograve;n nhỏ tuổi lại chưa trải đời, c&ocirc; đừng c&oacute; l&ocirc;i em ấy ra đ&ugrave;a nữa.&rdquo;<em>Trong đầu t&ocirc;i bỗng dấy l&ecirc;n suy nghĩ n&agrave;y, h&ograve;n đảo n&agrave;y thực sự qu&aacute; giống với h&ograve;n đảo hoang. Giờ điện thoại lại xảy ra vấn đề, phương thức li&ecirc;n lạc với thế giới b&ecirc;n ngo&agrave;i chỉ c&oacute; một chiếc cano m&agrave; th&ocirc;i đ&uacute;ng kh&ocirc;ng?&ldquo;Đường d&acirc;y bị hư rồi sao?&rdquo; H&agrave; U Đ&agrave;m xen v&agrave;o, &ldquo;Chuyện b&igrave;nh thường m&agrave;, tr&ecirc;n đảo n&agrave;y &iacute;t người ở, Cục viễn th&ocirc;ng cũng hay v&ocirc; cớ ngắt đường d&acirc;y điện thoại lắm.&rdquo;</em><br />\r\n<br />\r\n&ldquo;Hả? Thương hương tiếc ngọc &agrave;?&rdquo; L&ecirc; Nhật Hu&acirc;n gh&eacute; s&aacute;t Phương To&agrave;n, m&oacute;ng tay lướt tr&ecirc;n bả vai anh ấy, &ldquo;Chỉ l&agrave; v&agrave;i ba c&acirc;u đ&ugrave;a mở chuyện th&ocirc;i m&agrave; anh đ&atilde; đau l&ograve;ng rồi. Đ&acirc;u cần phải trọng sắc khinh bạn thế &mdash;&rdquo;<em>&ldquo;Được chứ được chứ, vậy phiền anh chăm s&oacute;c cho con nh&oacute;c n&agrave;y nh&eacute;.&rdquo; Phương To&agrave;n vội n&oacute;i, sau đ&oacute; liếc nh&igrave;n t&ocirc;i một c&aacute;i.</em><br />\r\n<br />\r\nTay c&ocirc; ta vừa b&oacute;p Phương To&agrave;n th&igrave; b&ecirc;n cạnh đ&atilde; truyền tới tiếng hừ lạnh. Lần n&agrave;y t&ocirc;i phản ứng cực nhanh, nh&igrave;n sang ngay, l&agrave; Hứa N&atilde;i Phu. Cũng đ&uacute;ng, bạn g&aacute;i của m&igrave;nh đang liếc mắt đưa t&igrave;nh với người đ&agrave;n &ocirc;ng kh&aacute;c, thực sự hơi kh&oacute; chịu m&agrave;.&ldquo;Hiểu Nguyệt, c&ocirc; với Phương To&agrave;n ở chung hay l&agrave; ở 2 ph&ograve;ng?&rdquo; C&ocirc; ta rất thoải m&aacute;i hỏi t&ocirc;i.Trong số 5 người đ&atilde; c&oacute; 2 cặp t&igrave;nh nh&acirc;n, chỉ c&oacute; Cảnh Thịnh l&agrave; độc th&acirc;n. T&ocirc;i nh&igrave;n sang H&agrave; U Đ&agrave;m đang đứng một b&ecirc;n, cảm thấy c&ocirc; ấy lại kh&aacute; xứng đ&ocirc;i với người t&ecirc;n Cảnh Thịnh lịch sự n&agrave;y, kh&ocirc;ng biết họ c&oacute; quan hệ mập mờ g&igrave; kh&ocirc;ng.<br />\r\n<br />\r\n&ldquo;Chị hai ơi, giờ c&ocirc; đang l&atilde;ng ph&iacute; thời gian của mọi người đấy. Hiểu Nguyệt ở ph&ograve;ng n&agrave;o thế? T&ocirc;i ở ph&ograve;ng cạnh em ấy l&agrave; được rồi.&rdquo; Phương To&agrave;n trở lại trọng t&acirc;m c&acirc;u chuyện.<em>Anh ta hơi biến sắc, khẽ lẩm bẩm g&igrave; đ&oacute; nhưng t&ocirc;i vẫn c&oacute; thể nghe được: &ldquo;Cũng l&agrave; Kỹ thuật m&ocirc;i trường của Hoa Hối, cũng hơi&hellip; Gi&ocirc;ng giống&hellip;&rdquo;</em><br />\r\n<br />\r\n&ldquo;Đ&atilde; sắp xếp xong xu&ocirc;i rồi, anh lo c&aacute;i g&igrave; hả!&rdquo; L&ecirc; Nhật Hu&acirc;n k&eacute;o t&ocirc;i lại đi tới một trong những căn ph&ograve;ng xung quanh.<em>&ldquo;Hiểu Nguyệt, đ&acirc;y l&agrave; L&ecirc; Nhật Hu&acirc;n, l&atilde;nh đạo trực tiếp của anh, một trong hai người phụ tr&aacute;ch tập đo&agrave;n Cảnh Nặc. C&ograve;n một vị nữa l&agrave; Cảnh Thịnh&hellip;&rdquo; Anh giới thiệu người đ&agrave;n &ocirc;ng kh&aacute;c đang đứng l&ecirc;n với t&ocirc;i, sau đ&oacute; th&igrave; giới thiệu t&ocirc;i với họ, &ldquo;Đ&acirc;y l&agrave; đ&agrave;n em của t&ocirc;i, Lộ Hiểu Nguyệt.&rdquo;</em><br />\r\n<br />\r\n&ldquo;C&ocirc; nghỉ ch&uacute;t đi, một l&aacute;t th&igrave; ra ăn cơm tối. Nhớ lấy ch&igrave;a kho&aacute; kho&aacute; ph&ograve;ng lại.&rdquo; L&ecirc; Nhật Hu&acirc;n đặt ch&igrave;a kho&aacute; xuống rồi l&ugrave;i ra khỏi ph&ograve;ng.Vạn Nam Phong nghe t&ocirc;i n&oacute;i mục đ&iacute;ch lần n&agrave;y đến chủ yếu l&agrave; để &ldquo;khảo s&aacute;t m&ocirc;i trường tr&ecirc;n đảo&rdquo; th&igrave; lại &acirc;n cần muốn dẫn t&ocirc;i đi. Lương Địch nghe anh ta n&oacute;i vậy, đ&ocirc;i mắt hung hăng nh&igrave;n t&ocirc;i chằm chặp, dường như hận kh&ocirc;ng thể d&ugrave;ng &aacute;nh mắt để chặt người t&ocirc;i ra vậy.<br />\r\n<br />\r\nT&ocirc;i nằm tr&ecirc;n giường, nh&igrave;n bầu trời gần sụp tối &mdash; Ph&ograve;ng n&agrave;y c&oacute; một c&aacute;i cửa sổ s&aacute;t đất h&igrave;nh bầu dục hơi đ&aacute;ng sợ, đem lại cho người ta cảm gi&aacute;c kh&ocirc;ng c&oacute; lấy ch&uacute;t ri&ecirc;ng tư n&agrave;o hết. Phương To&agrave;n n&oacute;i với t&ocirc;i rằng từ ph&ograve;ng c&oacute; thể nh&igrave;n ra được thế giới bao la b&ecirc;n ngo&agrave;i, đ&uacute;ng l&agrave; kh&ocirc;ng ngoa ch&uacute;t n&agrave;o. Nhưng l&uacute;c n&agrave;y ngo&agrave;i cửa sổ chỉ c&oacute; b&oacute;ng c&acirc;y ảm đạm v&agrave; một mảng hoa hướng dương qu&aacute; mức rực rỡ khiến l&ograve;ng t&ocirc;i cảm thấy hơi ngột ngạt. Cảm thấy rất buồn bực, nhưng kh&ocirc;ng hiểu l&agrave; do đ&acirc;u. Nỗi bất an trong l&ograve;ng c&agrave;ng l&uacute;c c&agrave;ng lớn, dường như t&ocirc;i đ&atilde; bỏ s&oacute;t chuyện g&igrave; đ&oacute;.<br />\r\n<br />\r\n&ldquo;&Agrave;! Qu&ecirc;n gọi điện về nh&agrave; rồi!&rdquo; Qu&ecirc;n gọi b&aacute;o b&igrave;nh an, d&ugrave; cha mẹ lu&ocirc;n rất y&ecirc;n t&acirc;m về t&ocirc;i, nhưng vẫn n&ecirc;n gọi điện để trấn an họ. T&ocirc;i cầm điện thoại l&ecirc;n, m&agrave;n h&igrave;nh điện thoại hiển thị đang t&igrave;m kiếm t&iacute;n hiệu. T&ocirc;i nh&iacute;u m&agrave;y lại, sao đến cả Thần Ch&acirc;u H&agrave;nh* cũng k&eacute;m thế n&agrave;y chứ? Đến cả t&iacute;n hiệu c&ograve;n kh&ocirc;ng c&oacute;, thế m&agrave; tự xưng l&agrave; Internet mạnh nhất.Đến rồi, đảo Nhật Canh m&agrave; đ&agrave;n anh đ&atilde; n&oacute;i, một mảnh đất thi&ecirc;n nhi&ecirc;n vẫn chưa bị ph&aacute; huỷ ho&agrave;n to&agrave;n.<br />\r\n<br />\r\n<em>&ldquo;Kh&ocirc;ng phải quan hệ &ldquo;đ&oacute;&rdquo;, đ&oacute; l&agrave; quan hệ &ldquo;n&agrave;o&rdquo; vậy?&rdquo; L&ecirc; Nhật Hu&acirc;n n&oacute;i xong xo&aacute;y mắt v&agrave;o t&ocirc;i, &ldquo;To&agrave;n c&oacute; người bạn g&aacute;i xinh đẹp thế n&agrave;y m&agrave; ch&uacute;ng t&ocirc;i cũng kh&ocirc;ng biết, c&oacute; phải hơi qu&aacute; đ&aacute;ng rồi kh&ocirc;ng? Anh n&oacute;i xem n&ecirc;n phạt thế n&agrave;o đ&acirc;y?&rdquo;(*)Thẻ/sim điện thoại.</em><em>&ldquo;Đ&uacute;ng l&agrave;, Phương To&agrave;n, anh l&agrave;m bảo vệ cho người đẹp kiểu g&igrave; thế hả, c&ocirc; Đường đi đường x&oacute;c nảy cả ng&agrave;y, đồ ngốc như anh th&igrave; chịu nổi chứ sao c&ocirc; ấy chịu được, c&ograve;n kh&ocirc;ng mau dẫn người ta đi nghỉ đi k&igrave;a?&rdquo; L&ecirc; Nhật Hu&acirc;n để &yacute; tới động t&aacute;c của t&ocirc;i, đến nắm lấy t&ocirc;i, &ldquo;C&ocirc; Đường&hellip; T&ocirc;i gọi c&ocirc; l&agrave; Hiểu Nguyệt c&oacute; được kh&ocirc;ng?&rdquo;</em><em>(*)Thẻ/sim điện thoại.</em><br />\r\n<br />\r\nT&ocirc;i chợt nhớ ra L&ecirc; Nhật Hu&acirc;n bảo c&oacute; thể tuỳ &yacute; sử dụng điện thoại, t&ocirc;i b&egrave;n cầm m&aacute;y b&agrave;n nội bộ l&ecirc;n. Mẹ đang ở nh&agrave;, t&ocirc;i vội n&oacute;i v&agrave;i c&acirc;u, d&ugrave; sao d&ugrave;ng điện thoại người kh&aacute;c để gọi đường d&agrave;i cũng kh&ocirc;ng tốt lắm, sau đ&oacute; b&egrave;n c&uacute;p m&aacute;y.<em>Với lại giờ muốn ra kiểm tra chiếc thuyền kia th&igrave; cũng đ&atilde; trễ rồi, kh&ocirc;ng th&iacute;ch hợp lắm&hellip; Huống hồ g&igrave; t&ocirc;i chỉ l&agrave; một người ngo&agrave;i.</em><br />\r\n<br />\r\nL&uacute;c t&ocirc;i thả ống nghe xuống, đ&egrave;n đỏ &ldquo;Đang d&ugrave;ng&rdquo; vẫn đang lo&eacute; s&aacute;ng, lập tức s&ocirc;i m&aacute;u, một l&uacute;c l&acirc;u sau mới k&igrave;m lại được &mdash; D&ugrave; sao t&ocirc;i cũng chỉ l&agrave; một &ldquo;kh&aacute;ch trọ&rdquo; nho nhỏ, l&agrave;m g&igrave; c&oacute; quyền giận chứ.<br />\r\n<br />\r\nT&ocirc;i bước tới cạnh cửa sổ, k&eacute;o r&egrave;m cửa ra. Kh&ocirc;ng biết lựa chọn n&agrave;y l&agrave; đ&uacute;ng hay sai nữa.<em>&ldquo;Chị hai ơi, giờ c&ocirc; đang l&atilde;ng ph&iacute; thời gian của mọi người đấy. Hiểu Nguyệt ở ph&ograve;ng n&agrave;o thế? T&ocirc;i ở ph&ograve;ng cạnh em ấy l&agrave; được rồi.&rdquo; Phương To&agrave;n trở lại trọng t&acirc;m c&acirc;u chuyện.</em><br />\r\n<br />\r\nCứ như thế, n&oacute;i chung cơm tối cũng ăn vui vẻ. Mẹ H&agrave; U Đ&agrave;m kh&ocirc;ng l&ecirc;n d&ugrave;ng bữa chung, 9 người ch&uacute;ng t&ocirc;i, 3 cặp đ&ocirc;i cộng th&ecirc;m t&ocirc;i, Phương To&agrave;n v&agrave; Cảnh Thịnh. Trừ t&ocirc;i ra, mọi người đều đ&atilde; c&oacute; c&ocirc;ng ăn việc l&agrave;m, hơn nữa đa phần đều l&agrave;m ở Cảnh Nặc n&ecirc;n kh&aacute; th&acirc;n thiết nhau, n&oacute;i tới n&oacute;i lui cũng khiến bầu kh&ocirc;ng kh&iacute; trở n&ecirc;n th&acirc;n thiện hơn. C&oacute; lẽ v&igrave; c&oacute; t&ocirc;i ở đ&acirc;y n&ecirc;n họ đều tr&aacute;nh b&agrave;n luận tới chuyện kinh tế v&agrave; những tr&ograve; đ&ugrave;a bẩn thỉu, Vạn Nam Phong thậm ch&iacute; c&ograve;n t&igrave;m một số chủ đề về nh&agrave; trường, gi&aacute;o sư c&aacute;c thứ để n&oacute;i chuyện với t&ocirc;i. Anh ta lớn hơn Phương To&agrave;n 2 kh&oacute;a, biết chuyện cũ của Hoa Đại hơn ch&uacute;ng t&ocirc;i nhiều. T&ocirc;i kh&ocirc;ng thể n&agrave;o bắt được trọng t&acirc;m c&acirc;u chuyện của anh ta, lại c&ograve;n phải nhận lấy &aacute;nh mắt th&ugrave; địch của Lương Địch.<br />\r\n<br />\r\nT&ocirc;i chỉ c&oacute; thể khẽ cười khổ, d&ugrave; c&ocirc; c&oacute; xem Vạn Nam Phong như bảo vật th&igrave; cũng kh&ocirc;ng c&oacute; nghĩa cả thế giới n&agrave;y đều muốn cướp bảo vật của c&ocirc; m&agrave;? T&ocirc;i vẫn chưa mất mặt tới mức đ&oacute; đ&acirc;u!<br />\r\n<br />\r\nC&ocirc; g&aacute;i &agrave;, chẳng lẽ v&igrave; một chữ t&igrave;nh m&agrave; c&ocirc; c&oacute; thể m&ugrave; qu&aacute;ng như thế sao?Ch&uacute;ng t&ocirc;i đi theo H&agrave; U Đ&agrave;m l&ecirc;n bờ, c&ocirc; ấy bước kh&ocirc;ng nhanh, nhưng kh&ocirc;ng hiểu sao l&ecirc;n bờ hơi kh&oacute;, c&oacute; lẽ l&agrave; do bước ch&acirc;n của c&ocirc; ấy. N&oacute;i thật th&igrave; l&uacute;c đầu tr&ocirc;ng thấy c&oacute; người đứng ở bờ Nam đ&oacute;n m&igrave;nh v&agrave; đ&agrave;n anh, t&ocirc;i c&oacute; hơi ngạc nhi&ecirc;n. V&oacute;c người c&ocirc; ấy kh&aacute; cao, nhưng d&aacute;ng người th&igrave; gầy đến mức yếu đuối. Cộng th&ecirc;m bộ v&aacute;y trắng c&agrave;ng đem lại cảm gi&aacute;c nhỏ b&eacute;, v&agrave; cả m&aacute;i t&oacute;c đen chạm vai mang theo phong c&aacute;ch cổ điển nữa. T&ocirc;i thực sự kh&ocirc;ng thể tưởng tượng được chủ nh&acirc;n đảo Nhật Canh đang nghĩ g&igrave; m&agrave; lại để một c&ocirc; g&aacute;i như thế ra đ&oacute;n kh&aacute;ch, lại c&ograve;n bắt c&ocirc; ấy l&aacute;i thuyền tới nữa! Lẽ n&agrave;o đ&agrave;n &ocirc;ng tr&ecirc;n đảo n&agrave;y chết hết rồi &agrave;? Nếu l&agrave; t&ocirc;i, chắc chắn t&ocirc;i kh&ocirc;ng thể t&agrave;n nhẫn như vậy được.<br />\r\n<br />\r\nVạn Nam Phong nghe t&ocirc;i n&oacute;i mục đ&iacute;ch lần n&agrave;y đến chủ yếu l&agrave; để &ldquo;khảo s&aacute;t m&ocirc;i trường tr&ecirc;n đảo&rdquo; th&igrave; lại &acirc;n cần muốn dẫn t&ocirc;i đi. Lương Địch nghe anh ta n&oacute;i vậy, đ&ocirc;i mắt hung hăng nh&igrave;n t&ocirc;i chằm chặp, dường như hận kh&ocirc;ng thể d&ugrave;ng &aacute;nh mắt để chặt người t&ocirc;i ra vậy.<br />\r\n<br />\r\n&ldquo;Nam Phong, anh vẫn n&ecirc;n chăm s&oacute;c Lương Địch cho tốt đi!&rdquo; Cảnh Thịnh ra mặt ho&agrave; giải, rồi lại quay sang n&oacute;i với Phương To&agrave;n, &ldquo;Phương To&agrave;n, mai t&ocirc;i cũng muốn đưa đ&agrave;n em của anh đi dạo tr&ecirc;n đảo một ch&uacute;t, tận t&igrave;nh thể hiện l&agrave; một người chủ th&acirc;n thiện, được kh&ocirc;ng?&rdquo;&ldquo;Khoa Kỹ thuật m&ocirc;i trường, l&agrave; đ&agrave;n em trực tiếp của đ&agrave;n anh Phương To&agrave;n.&rdquo; T&ocirc;i đ&aacute;p, trong l&ograve;ng hơi kh&oacute; chịu. Người đ&agrave;n &ocirc;ng đ&oacute; diện mạo cũng được xem l&agrave; đẹp trai nhưng th&aacute;i độ lại cực kỳ th&ocirc; lỗ, n&oacute;i chuyện như thể ra lệnh vậy, mang theo ch&uacute;t cảm gi&aacute;c trịch thượng.<br />\r\n<br />\r\n&ldquo;Được chứ được chứ, vậy phiền anh chăm s&oacute;c cho con nh&oacute;c n&agrave;y nh&eacute;.&rdquo; Phương To&agrave;n vội n&oacute;i, sau đ&oacute; liếc nh&igrave;n t&ocirc;i một c&aacute;i.<br />\r\n<br />\r\n&ldquo;Hiểu Nguyệt lần đầu tới ở tr&ecirc;n đảo, Cảnh Thịnh, anh nhất định phải đối xử tốt với c&ocirc; ấy đấy nh&eacute;.&rdquo; L&ecirc; Nhật Hu&acirc;n n&oacute;i với Cảnh Thịnh nhưng &aacute;nh mắt lại nh&igrave;n về ph&iacute;a t&ocirc;i, mang theo ch&uacute;t &yacute; vị s&acirc;u xa, &ldquo;Hiểu Nguyệt, tr&ecirc;n đảo n&agrave;y em muốn đi đ&acirc;u cũng được. Chẳng phải em muốn khảo s&aacute;t sao? Cứ nghi&ecirc;m t&uacute;c nghi&ecirc;n cứu mấy thứ &ldquo;đặc biệt&rdquo; đ&oacute; đi!&rdquo;&ldquo;C&ocirc; nghỉ ch&uacute;t đi, một l&aacute;t th&igrave; ra ăn cơm tối. Nhớ lấy ch&igrave;a kho&aacute; kho&aacute; ph&ograve;ng lại.&rdquo; L&ecirc; Nhật Hu&acirc;n đặt ch&igrave;a kho&aacute; xuống rồi l&ugrave;i ra khỏi ph&ograve;ng.<br />\r\n<br />\r\nCảnh Thịnh biến sắc, t&ocirc;i nh&igrave;n thấy thế th&igrave; sự nghi ngờ lại c&agrave;ng tăng th&ecirc;m. Vạn Nam Phong bị Cảnh Thịnh v&agrave; L&ecirc; Nhật Hu&acirc;n cướp lời n&ecirc;n sắc mặt hết sức kh&oacute; coi. Ch&uacute;ng t&ocirc;i n&oacute;i chuyện được một l&aacute;t, anh ta đ&atilde; bu&ocirc;ng b&aacute;t: &ldquo;T&ocirc;i ăn no rồi, về ph&ograve;ng đ&acirc;y.&rdquo;<br />\r\n<br />\r\nT&ocirc;i nh&igrave;n về ph&iacute;a Lương Địch, c&ocirc; ta hơi nh&uacute;c nh&iacute;ch, dường như cũng muốn đi, lập tức bĩu m&ocirc;i một c&aacute;i rồi quay đầu đi kh&ocirc;ng th&egrave;m để &yacute; tới anh ta nữa. Vạn Nam Phong rời khỏi bữa tiệc, lấy ch&igrave;a kho&aacute; mở cửa ph&ograve;ng m&igrave;nh ra rồi v&agrave;o trong.<br />\r\n<br />\r\nCh&uacute;ng t&ocirc;i tiếp tục ăn cơm, d&ugrave; hơi mất hứng nhưng rất nhanh đ&atilde; kh&ocirc;i phục lại trạng th&aacute;i n&aacute;o nhiệt. L&uacute;c mọi người đang vui vẻ, t&ocirc;i nghe thấy một tiếng &ldquo;Ầm&rdquo; vang l&ecirc;n từ cửa ph&ograve;ng Vạn Nam Phong, Vạn Nam Phong lập tức vọt ra ngo&agrave;i.<br />\r\n<br />\r\n&ldquo;C&oacute; chuyện g&igrave; vậy, sao đột nhi&ecirc;n lại kh&ocirc;ng l&ecirc;n mạng được rồi? L&ecirc; Nhật Hu&acirc;n, c&ocirc; l&agrave;m c&aacute;i qu&aacute;i g&igrave; vậy hả?&rdquo; Anh ta tức giận h&eacute;t to.<br />\r\n<br />\r\n&ldquo;Sao lại kh&ocirc;ng l&ecirc;n mạng được? C&oacute; khi n&agrave;o anh chưa cắm d&acirc;y kh&ocirc;ng?&rdquo; L&ecirc; Nhật Hu&acirc;n kh&ocirc;ng th&egrave;m để &yacute; tới việc anh ta đang thở hồng hộc m&agrave; chỉ chậm r&atilde;i hỏi.<br />\r\n<br />\r\n&ldquo;Đ&acirc;y c&oacute; phải l&agrave; lần đầu ti&ecirc;n t&ocirc;i d&ugrave;ng c&aacute;i ADSL* rẻ r&aacute;ch tr&ecirc;n đảo n&agrave;y đ&acirc;u, sao c&oacute; thể chưa cắm được? C&oacute; phải l&agrave; đ&aacute;m quỷ n&agrave;y lại c&oacute; vấn đề rồi kh&ocirc;ng?&rdquo; Vạn Nam Phong n&oacute;i với c&ocirc; ta.<br />\r\n<br />\r\n<em>(*) Thiết bị để truy nhập Internet tốc độ cao v&agrave; sử dụng c&aacute;c dịch vụ tr&ecirc;n Internet một c&aacute;ch nhanh hơn.</em><br />\r\n<br />\r\n&ldquo;Anh hỏi t&ocirc;i cũng v&ocirc; &iacute;ch, t&ocirc;i c&oacute; phải l&agrave; Cục viễn th&ocirc;ng đ&acirc;u.&rdquo; L&ecirc; Nhật Hu&acirc;n đứng dậy, nhấc ống nghe điện thoại trong sảnh lớn l&ecirc;n, &ldquo;Chỉ cần gọi một c&uacute; điện thoại th&ocirc;i m&agrave;, l&agrave;m g&igrave; m&agrave; k&iacute;ch động vậy hả?&rdquo;<br />\r\n<br />\r\n&ldquo;Hả?&rdquo; B&agrave;n tay đang quay số điện thoại của L&ecirc; Nhật Hu&acirc;n d&ugrave;ng lại, &ldquo;Kh&ocirc;ng c&oacute; t&iacute;n hiệu ư?&rdquo;<br />\r\n<br />\r\n&ldquo;Đường d&acirc;y bị hư rồi sao?&rdquo; H&agrave; U Đ&agrave;m xen v&agrave;o, &ldquo;Chuyện b&igrave;nh thường m&agrave;, tr&ecirc;n đảo n&agrave;y &iacute;t người ở, Cục viễn th&ocirc;ng cũng hay v&ocirc; cớ ngắt đường d&acirc;y điện thoại lắm.&rdquo;<br />\r\n<br />\r\n&ldquo;Đệch! Kh&ocirc;ng phải đ&atilde; trả tiền cho họ rồi sao? Đ&uacute;ng l&agrave; ch&oacute; m&aacute; thật!&rdquo; Vạn Nam Phong chửi, &ldquo;Hậu quả lũng đoạn n&agrave;y, China Mobile, hừ!&rdquo;<em>T&ocirc;i kh&ocirc;ng k&igrave;m được m&agrave; cười đ&aacute;p lại anh ta, anh ta hơi giật m&igrave;nh, n&eacute;t mặt bỗng thay đổi, mắt nh&igrave;n nh&igrave;n t&ocirc;i cũng hơi trầm xuống. T&ocirc;i nh&igrave;n thấy &aacute;nh mắt n&agrave;y, trong l&ograve;ng hơi bất an. Kh&ocirc;ng phải l&agrave; &aacute;nh mắt dơ bẩn như của Vạn Nam Phong v&agrave; Hứa N&atilde;i Phu, nhưng&hellip;</em><br />\r\n<br />\r\n&ldquo;Được rồi được rồi, mai t&ocirc;i với U Đ&agrave;m sang b&ecirc;n đ&oacute; n&oacute;i chuyện một ch&uacute;t l&agrave; được m&agrave;.&rdquo; Lạc Ph&agrave;m khẽ cười, &ldquo;R&aacute;ng chịu 1 ng&agrave;y đi.&rdquo;<br />\r\n<br />\r\n&ldquo;Điện thoại cũng kh&ocirc;ng c&oacute; t&iacute;n hiệu, chỗ qu&aacute;i g&igrave; thế n&agrave;y!&rdquo; Vạn Nam Phong vẫn c&ograve;n ph&agrave;n n&agrave;n.<br />\r\n&ldquo;Nam Phong, chẳng ai &eacute;p anh tới chỗ qu&aacute;i quỷ n&agrave;y hết.&rdquo; L&ecirc; Nhật Hu&acirc;n giận t&aacute;i mặt, &ldquo;Anh muốn đi cứ đi, l&uacute;c n&agrave;o cũng được.&rdquo;<br />\r\n<br />\r\nVạn Nam Phong lập tức kh&ocirc;ng d&aacute;m n&oacute;i g&igrave; nữa, t&ocirc;i nh&igrave;n thấy người tr&ecirc;n b&agrave;n đa phần đều c&oacute; ch&uacute;t hả h&ecirc;.<br />\r\n<br />\r\n&mdash; C&oacute; thật l&agrave; muốn đi l&uacute;c n&agrave;o cũng được kh&ocirc;ng?<em>&ldquo;Hả?&rdquo; B&agrave;n tay đang quay số điện thoại của L&ecirc; Nhật Hu&acirc;n d&ugrave;ng lại, &ldquo;Kh&ocirc;ng c&oacute; t&iacute;n hiệu ư?&rdquo;</em><br />\r\n<br />\r\nTrong đầu t&ocirc;i bỗng dấy l&ecirc;n suy nghĩ n&agrave;y, h&ograve;n đảo n&agrave;y thực sự qu&aacute; giống với h&ograve;n đảo hoang. Giờ điện thoại lại xảy ra vấn đề, phương thức li&ecirc;n lạc với thế giới b&ecirc;n ngo&agrave;i chỉ c&oacute; một chiếc cano m&agrave; th&ocirc;i đ&uacute;ng kh&ocirc;ng?<br />\r\n<br />\r\nNếu&hellip;<br />\r\n<br />\r\nT&ocirc;i lắc đầu, quăng suy nghĩ n&agrave;y ra khỏi đầu m&igrave;nh. Cha thường n&oacute;i với t&ocirc;i rằng &ldquo;Ba c&aacute;i vui th&igrave; trẻ, ba c&aacute;i bẽ th&igrave; gi&agrave;&rdquo;, vẫn n&ecirc;n cố gắng đừng nghĩ g&igrave; nhiều nữa th&igrave; hơn.<br />\r\n<br />\r\nVới lại giờ muốn ra kiểm tra chiếc thuyền kia th&igrave; cũng đ&atilde; trễ rồi, kh&ocirc;ng th&iacute;ch hợp lắm&hellip; Huống hồ g&igrave; t&ocirc;i chỉ l&agrave; một người ngo&agrave;i.<em>C&ocirc; g&aacute;i &agrave;, chẳng lẽ v&igrave; một chữ t&igrave;nh m&agrave; c&ocirc; c&oacute; thể m&ugrave; qu&aacute;ng như thế sao?</em><br />\r\n<br />\r\nĂn cơm, ăn cơm th&ocirc;i.</span></span></p>\r\n\r\n<p>&nbsp;</p>', 1, '2022-03-24 07:55:59', '2022-03-24 07:55:59');
INSERT INTO `chapters` (`id`, `book_id`, `name`, `slug`, `description`, `content`, `active`, `created_at`, `updated_at`) VALUES
(21, 16, 'Chương 1', 'chuong-1', '<p>dgdshyer</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi xoa xoa huyệt th&aacute;i dương, c&oacute; ch&uacute;t mờ mịt nh&igrave;n khung cảnh lạ lẫm xung quanh.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trước đ&oacute; một gi&acirc;y c&ograve;n ngồi trước m&aacute;y t&iacute;nh đ&aacute;nh ph&oacute; bản, gi&acirc;y tiếp theo con chuột c&ugrave;ng m&agrave;n h&igrave;nh trước mặt tất cả đều kh&ocirc;ng thấy, xung quanh cũng kh&ocirc;ng phải l&agrave; ph&ograve;ng ngủ quen thuộc, hắn tựa hồ đang ở... giữa sườn một ngọn n&uacute;i?</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nh&igrave;n nh&igrave;n bốn ph&iacute;a, Lục Qu&acirc;n Hi lơ đ&atilde;ng c&uacute;i đầu, mới hậu tri hậu gi&aacute;c ph&aacute;t hiện, quần &aacute;o hắn đang mặc kh&ocirc;ng phải của hắn, m&agrave; l&agrave; phục sức dị vực gần lộ cả lồng ngực, vừa nh&igrave;n đ&atilde; biết kh&ocirc;ng phải l&agrave; vật b&igrave;nh thường.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">N&agrave;y nh&igrave;n hơi quen mắt, r&otilde; r&agrave;ng l&agrave; l&uacute;c n&atilde;y khi hắn đang chơi game online v&otilde;ng du Kiếm Tam, l&agrave; trang bị nh&acirc;n vật của hắn!</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi l&agrave; một đảng PVP hung t&agrave;n, cơ bản kh&ocirc;ng đ&aacute;nh ph&oacute; bản, mỗi ng&agrave;y vừa đăng nhập v&agrave;o liền đi đ&aacute;nh người chơi, rốt cuộc hỗn hợp đủ để c&oacute; một bộ Li&ecirc;n Th&agrave;nh x&iacute;ch t&acirc;m, đ&aacute;nh m&atilde;n tinh tinh luyện tinh chế v&agrave; đ&aacute;, đang chuẩn bị tao bao l&ecirc;n mặt một phen, liền bị người k&eacute;o đi bổ sung nh&acirc;n lực đi đ&aacute;nh ph&oacute; bản, sau đ&oacute;... liền biến th&agrave;nh thế n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cho n&ecirc;n n&oacute;i, hắn l&agrave; đang chạy theo mốt, đặt ch&acirc;n l&ecirc;n chuyến bay d&agrave;nh cho người mang theo nh&acirc;n vật tr&ograve; chơi sao?</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi cảm gi&aacute;c, bản th&acirc;n cảm thấy được sự độc &aacute;c của thế giới.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&uacute;c hắn đang chuẩn bị chấp nhận t&igrave;nh cảnh của bản th&acirc;n, một đạo k&igrave;nh phong mang theo s&aacute;t kh&iacute; hướng về ph&iacute;a hắn đ&aacute;nh tới, Lục Qu&acirc;n Hi liền theo bản năng muốn d&ugrave;ng Huyễn Quang Bộ tr&aacute;nh ra, nhưng m&agrave;...</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">[ kỹ năng hệ thống chưa mở ra.]</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">--- C&aacute;i g&igrave; m&agrave; kỹ năng hệ thống chưa mở ra?!</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngay sau đ&oacute;, Lục Qu&acirc;n Hi thấy hoa mắt, chỉ kịp nh&igrave;n thấy bộ mặt y&ecirc;u nghiệt của người kia, liền nằm thi.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">[Hệ thống: Ng&agrave;i tại C&ocirc;n Lu&acirc;n sơn bị [Ngọc La S&aacute;t] t&agrave;n nhẫn s&aacute;t hại.]</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi: &quot;...&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">..... Kh&ocirc;ng n&oacute;i một lời liền giết hắn như vậy, n&agrave;y kh&ocirc;ng phải l&agrave; &aacute;c mộng kh&oacute; khăn sao?! Lần đầu ti&ecirc;n xuy&ecirc;n việt liền gặp thử th&aacute;ch khả năng t&acirc;m l&yacute; thừa nhận sự kiện n&agrave;y của người mới, Lục Qu&acirc;n Hi cảm gi&aacute;c tr&aacute;i tim m&igrave;nh c&agrave;ng th&ecirc;m cứng rắn.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bất qu&aacute;... C&aacute;i t&ecirc;n Ngọc La S&aacute;t n&agrave;y như thế n&agrave;o nghe quen tai vậy? Bạch ph&aacute;t ma nữ Luyện Ngh&ecirc; Thường c&oacute; vẻ l&agrave; gọi t&ecirc;n n&agrave;y? Nhưng hung thủ giết hắn r&otilde; r&agrave;ng l&agrave; một thiếu ni&ecirc;n...</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hung thủ Ngọc La S&aacute;t b&igrave;nh tĩnh b&agrave;ng quan c&ugrave;ng tiếp x&uacute;c th&acirc;n mật với thi thể của m&igrave;nh, Lục Qu&acirc;n Hi lại cẩn thận nhớ lại một ch&uacute;t nội dung &iacute;t ỏi của mấy cuốn tiểu thuyết v&otilde; hiệp m&igrave;nh đọc kh&ocirc;ng nhiều lắm l&uacute;c sơ trung, chắc đ&acirc;y l&agrave; nh&acirc;n vật trong [Lục Tiểu Phụng truyền kỳ], kh&ocirc;ng r&otilde; diện mạo, c&oacute; vẻ th&acirc;n phận l&agrave; gi&aacute;o chủ g&igrave; g&igrave; đ&oacute;. Thời gian đ&atilde; qua l&acirc;u m&agrave; đ&acirc;y cũng kh&ocirc;ng phải nh&acirc;n vật ch&iacute;nh, thật sự hắn kh&ocirc;ng nhớ r&otilde; lắm.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng sai, t&igrave;nh huống hiện tại hắn giống như linh hồn phi&ecirc;u tại ph&iacute;a tr&ecirc;n &quot;thi thể&quot;, c&oacute; thể đem t&igrave;nh trạng &quot;chết đi&quot; xem r&otilde; r&agrave;ng thấu đ&aacute;o.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngọc La S&aacute;t nh&igrave;n qua bất qu&aacute; mười ba mười bốn tuổi, tr&ecirc;n người vết m&aacute;u loang lỗ, tựa hồ vừa trải qua một cuộc ch&eacute;m giết thảm thiết.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi nh&igrave;n hắn thật cẩn thận đi đến ph&iacute;a trước &quot;thi thể&quot; của m&igrave;nh, lại ngoan ngoan bổ mấy đao [...], sau khi x&aacute;c định người đ&atilde; chết, mới kiểm tra đến khối &quot;thi thể&quot; n&agrave;y.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kiếm Tam l&agrave; game online, đồ vật n&agrave;y nọ tự nhi&ecirc;n đều l&agrave; đặt ở trong t&uacute;i, Ngọc La S&aacute;t đương nhi&ecirc;n c&aacute;i g&igrave; cũng lấy kh&ocirc;ng được.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng hề nh&igrave;n Ngọc La S&aacute;t, Lục Qu&acirc;n Hi cẩn thận xem x&eacute;t &quot;thi thể&quot; bản th&acirc;n. C&ugrave;ng trong tr&ograve; chơi giống nhau như đ&uacute;c, chỉ l&agrave; trong tay thiếu song đao, bị giết xong lại bổ mấy đao cũng kh&ocirc;ng ph&aacute; hư mỹ cảm. Lục Qu&acirc;n Hi ở trong l&ograve;ng gật gật đầu, kh&ocirc;ng hổ l&agrave; tr&ograve; chơi xuất phẩm th&acirc;n thể.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thiếu ni&ecirc;n Ngọc La S&aacute;t sau khi đi xa, Lục Qu&acirc;n Hi tại ph&iacute;a tr&ecirc;n &quot;thi thể&quot; chuyển chuyển, sau đ&oacute; thử tiến đi v&agrave;o.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">[Ng&agrave;i đ&atilde; tử vong, c&oacute; hay kh&ocirc;ng về doanh địa sống lại?]</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Th&igrave; ra kh&ocirc;ng c&oacute; t&ugrave;y chọn tại chỗ, loại t&igrave;nh huống n&agrave;y chỉ c&oacute; thể chọn &quot;c&oacute;&quot;.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tại khoảnh khắc hắn lựa chọn, trước mắt đột nhi&ecirc;n l&oacute;a l&ecirc;n, xung quanh liền biến th&agrave;nh khung cảnh kh&aacute;c.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hoặc l&agrave; n&oacute;i, hắn về tới &quot;doanh địa&quot;, đang ngồi tr&ecirc;n mặt đất.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đ&acirc;y l&agrave; một gian thạch thất, bốn ph&iacute;a bịt k&iacute;n, tại một g&oacute;c trong thạch thất c&oacute; rất nhiều c&aacute;t đ&aacute; chất đống, kh&ocirc;ng biết v&ugrave;i lấp c&aacute;i g&igrave;.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi vừa t&iacute;nh to&aacute;n đứng l&ecirc;n đi điều tra xung quanh một ch&uacute;t, liền nghe tiếng vang truyền đến từ cửa đ&aacute;, rất nhanh liền mở ra, ngay sau đ&oacute; một đống người đi v&agrave;o.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi thực b&igrave;nh tĩnh quay đầu nh&igrave;n lại, thần sắc kh&ocirc;ng hề kinh hoảng.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">--- mang theo nh&acirc;n vật tr&ograve; chơi xuy&ecirc;n v*** + xuất hiện sự t&igrave;nh chết đều t&ugrave;y tiện sống lại, c&ograve;n c&aacute;i g&igrave; đ&aacute;ng kinh ngạc?</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lấy Trương V&ocirc; Kỵ cầm đầu Minh Gi&aacute;o mọi người lại lắp lắp kinh h&atilde;i.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mới vừa Lục Đại ph&aacute;i v&acirc;y c&ocirc;ng Đỉnh Quang Minh, nguy cơ vừa giải trừ c&ograve;n chưa được thả lỏng, đợt hai m&ocirc;n ph&aacute;i lấy l&iacute; do &quot;ti&ecirc;u diệt t&agrave;n dư Ma Gi&aacute;o&quot; ngụy trang nh&acirc;n l&uacute;c Đỉnh Quang Minh ch&aacute;y nh&agrave; l&ecirc;n h&ocirc;i của. Trương V&ocirc; Kỵ rơi v&agrave;o đường c&ugrave;ng, chỉ c&oacute; thể tiếp nhận chức vụ Minh Gi&aacute;o gi&aacute;o chủ, khiến mọi người Minh Gi&aacute;o lui v&agrave;o mật đạo tr&ecirc;n Đỉnh Quang Minh tạm thời tr&aacute;nh n&eacute;.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sau khi tiến v&agrave;o mật đạo, mang theo Dương Ti&ecirc;u đợi người tiến v&agrave;o thạch thất vợ chồng Dương Đỉnh Thi&ecirc;n tử vong, thời điểm trước khi tiến v&agrave;o, hắn c&ugrave;ng Tiểu Chi&ecirc;u đem vợ chồng Dương Đỉnh Thi&ecirc;n hợp t&aacute;ng, sau khi đi ra đ&oacute;ng k&iacute;n cửa đ&aacute;, bởi vậy c&oacute; thể x&aacute;c định b&ecirc;n trong kh&ocirc;ng thể c&oacute; người.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cho n&ecirc;n hiện tại khi hắn thấy một h&agrave;i tử với đ&ocirc;i mắt dị sắc b&igrave;nh thản kh&ocirc;ng gợn s&oacute;ng nh&igrave;n m&igrave;nh, thật bị dọa sợ.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">H&agrave;i tử kia bất qu&aacute; mới 8-9 tuổi, tr&ecirc;n người bọc một th&acirc;n y b&agrave;o kh&ocirc;ng hợp thể, khu&ocirc;n mặt tinh xảo, l&agrave;n da trắng n&otilde;n, chỉ l&agrave; khu&ocirc;n mặt kh&ocirc;ng ch&uacute;t biểu t&igrave;nh khiến người c&oacute; ch&uacute;t kinh h&atilde;i, nhất l&agrave; tại trong mật thất c&oacute; người chết.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dương Ti&ecirc;u trong mắt chợt l&oacute;e s&aacute;t kh&iacute;, nhỏ giọng n&oacute;i: &quot;Gi&aacute;o chủ, để thủ hạ đi lấy hắn xuống.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trương V&ocirc; Kỵ dừng một ch&uacute;t , ngăn cản Dương Ti&ecirc;u: &quot;Dương tả sứ, vẫn l&agrave; ta đi đi.&quot; N&oacute;i xong cũng kh&ocirc;ng đợi mọi người phản ứng, liền đi tới, ngồi xuống trức mặt tiểu h&agrave;i tử kia: &quot;Tiểu đệ đệ, ngươi t&ecirc;n l&agrave; g&igrave;? Như thế n&agrave;o lại xuất hiện ở trong n&agrave;y?&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tiểu đệ đệ? Lục Qu&acirc;n Hi chớp mắt, d&ugrave;ng dư quang kh&oacute;e mắt tho&aacute;ng nh&igrave;n trang phục Li&ecirc;n Th&agrave;nh x&iacute;ch biến lớn c&ugrave;ng với h&agrave;o bao, thực b&igrave;nh tĩnh tiếp nhận sự thật sau khi sống lại sẽ biến nhỏ .</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">--- Nếu hắn lại chết một lần, đợi đến thời điểm khi sống lại c&oacute; thể sẽ biến lại th&agrave;nh b&agrave;o thai hay kh&ocirc;ng?</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Một b&ecirc;n ph&aacute;t t&aacute;n tư duy, một b&ecirc;n bất động thanh sắc quan s&aacute;t vị tr&iacute; v&agrave; ho&agrave;n cảnh của bản th&acirc;n c&ugrave;ng với đ&aacute;m người cổ đại đứng tại cửa đ&aacute; mang sắc mặt đề ph&ograve;ng, Lục Qu&acirc;n Hi r&uacute;t ra kết luận, thanh ni&ecirc;n trước mặt n&agrave;y hẳn l&agrave; t&acirc;m địa thiện lương hơn nữa địa vị trong đ&aacute;m người cũng rất cao.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Sau n&agrave;y, nếu kh&ocirc;ng muốn nghiệm chứng sau khi chết đi sống lại c&oacute; thể hay kh&ocirc;ng tho&aacute;i h&oacute;a th&agrave;nh b&agrave;o thai, đương nhi&ecirc;n nếu từ nay muốn đi theo người tốt, trước n&ecirc;n l&agrave;m r&agrave;m r&otilde; t&igrave;nh huống hiện tại rồi mặt kh&aacute;c t&iacute;nh to&aacute;n.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&acirc;m tư vừa chuyển, biểu t&igrave;nh tr&ecirc;n mặt vẫn l&agrave; lạnh l&ugrave;ng thản nhi&ecirc;n, Lục Qu&acirc;n Hi nh&igrave;n c&oacute; ch&uacute;t chần chờ vươn tay ra, thật cẩn thận chạm v&agrave;o tay người thanh ni&ecirc;n, sau đ&oacute; do do dự dự nắm lại, mở miệng n&oacute;i: &quot;Lục Qu&acirc;n Hi&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thanh &acirc;m mềm mềm nhu nhu, đối lập với biểu t&igrave;nh l&atilde;nh đạm tr&ecirc;n mặt hắn.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trương V&ocirc; Kỵ y&ecirc;n t&acirc;m, thanh &acirc;m c&agrave;ng th&ecirc;m &ocirc;n h&ograve;a: &quot;Qu&acirc;n Hi đệ đệ, nơi n&agrave;y l&agrave; Minh Gi&aacute;o, ta l&agrave; đại gi&aacute;o chủ Trương V&ocirc; Kỵ, ngươi v&agrave;o bằng c&aacute;ch n&agrave;o?&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trương V&ocirc; Kỵ? Lục Qu&acirc;n Hi trong l&ograve;ng chợt l&oacute;e tia kinh ngạc. L&agrave; nam ch&iacute;nh trong [Ỷ Thi&ecirc;n Đồ Long K&yacute;], t&ecirc;n Trương V&ocirc; Kỵ n&agrave;y đương nhi&ecirc;n hắn nhớ rất r&otilde;, như vậy nơi n&agrave;y l&agrave; mật đạo Đỉnh Quang Minh m&agrave; t&ecirc;n phi gi&aacute;o chủ Minh Gi&aacute;o kia kh&ocirc;ng thể v&agrave;o. Nhớ lại một ch&uacute;t nội dung cốt truyện, Lục Qu&acirc;n Hi tiếp tục mặt than n&oacute;i: &quot;Đệ tử Minh Gi&aacute;o Lục Qu&acirc;n Hi, b&aacute;i kiến đại gi&aacute;o chủ.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trương V&ocirc; Kỵ sửng sốt: &quot;Ngươi cũng l&agrave; người trong Minh Gi&aacute;o?&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tiểu Chi&ecirc;u vẫn trốn ở một b&ecirc;n nhỏ giọng nhắc nhở n&oacute;i: &quot;Hắn tr&ecirc;n người tựa hồ l&agrave; trang phục người Ba Tư.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Dương Ti&ecirc;u nh&igrave;n n&agrave;ng một c&aacute;i, cũng gật đầu n&oacute;i: &quot;Ta cũng từng đ&atilde; giao thủ c&ugrave;ng bọn họ, trang phục quả thật rất giống. Ba Tư Minh Gi&aacute;o từng muốn ch&uacute;ng ta quy h&agrave;ng triều đ&igrave;nh nh&agrave; Nguy&ecirc;n, Dương gi&aacute;o chủ kh&ocirc;ng đ&aacute;p ứng. H&agrave;i tử n&agrave;y lai lịch kỳ lạ, rất c&oacute; khả năng l&agrave; đến từ C&agrave;n Kh&ocirc;n Đại Na Di.&quot;</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi nắm chặt tay Trương V&ocirc; Kỵ, c&uacute;i đầu th&acirc;n thể run nh&egrave; nhẹ khiến cho Trương V&ocirc; Kỵ t&acirc;m sinh kh&ocirc;ng đ&agrave;nh l&ograve;ng: &quot;Dương tả sứ, hắn chỉ l&agrave; h&agrave;i tử, kh&ocirc;ng bằng liền để ở b&ecirc;n người ta, nếu c&oacute; vấn đề, ta sẽ xử l&yacute;.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">[Th&agrave;nh c&ocirc;ng đạt được th&acirc;n phận d&acirc;n bản xứ, kỹ năng hệ thống mở ra, nhiệm vụ hệ thống mở ra, kh&aacute;c hệ thống mở ra, th&acirc;n thể b&aacute;n dữ liệu h&oacute;a.]</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi nh&igrave;n trang b&aacute;n trong suốt lơ lửng trước mặt, rất giống với giao diện của Kiếm Tam. Tr&ecirc;n giao diện nh&acirc;n vật l&agrave; bộ d&aacute;ng của một shota, hẳn c&ugrave;ng bộ d&aacute;ng hắn hiện tại giống nhau như đ&uacute;c. Đại kh&aacute;i h&igrave;nh thể shota trong Kiếm Tam cũng kh&ocirc;ng kh&aacute;c bao nhi&ecirc;u c&aacute;i dạng n&agrave;y đi.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đẳng cấp trước khi xuy&ecirc;n việt l&agrave; cấp 90, kỹ năng trong Phần ảnh th&aacute;nh quyết to&agrave;n bộ đều s&aacute;ng, muốn d&ugrave;ng c&aacute;c kỹ năng kh&aacute;c, c&ograve;n cần chuyển sang th&agrave;nh h&igrave;nh dạng Minh T&ocirc;n. Lục Qu&acirc;n Hi cảm gi&aacute;c, nếu hiện tại mở cửa cho hắn c&ugrave;ng Ngọc La S&aacute;t đ&aacute;nh với nhau một trận, kết quả liền thay đổi.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Kh&ocirc;ng biết l&agrave; t&iacute;nh cụ thể của hệ thống Kiếm Tam, vẫn l&agrave; Minh Gi&aacute;o kh&ocirc;ng c&oacute; lam điều duy&ecirc;n cớ, Lục Qu&acirc;n Hi tr&ecirc;n người ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; nội lực, đương nhi&ecirc;n điểm n&agrave;y mới l&agrave; nguy&ecirc;n nh&acirc;n chủ yếu khiến Dương Ti&ecirc;u kh&ocirc;ng cố phản đối Trương V&ocirc; Kỵ giết hắn.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi cảm thấy như vậy rất tốt, hắn đi đến một thế giới xa lạ, giả heo ăn thịt hổ so với bộc lộ t&agrave;i năng tốt hơn nhiều.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhấn mở giao diện nhiệm vụ, tr&ecirc;n đ&oacute; chỉ c&oacute; một nhiệm vụ.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhiệm vụ cuối c&ugrave;ng: Đem Minh Gi&aacute;o ph&aacute;t triển trở th&agrave;nh thi&ecirc;n hạ đệ nhất Ma Gi&aacute;o.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhiệm vụ cho thưởng: T&ugrave;y tiện xuy&ecirc;n việt thời kh&ocirc;ng một lần.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi mắt s&aacute;ng l&ecirc;n, nhiệm vụ cho thưởng... C&aacute;i gọi l&agrave; &quot;T&ugrave;y tiện xuy&ecirc;n việt thời kh&ocirc;ng một lần&quot; l&agrave; chỉ nếu ho&agrave;n th&agrave;nh nhiệm vụ ch&iacute;nh như vậy hắn c&oacute; thể trở về?</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">[Thỉnh ng&agrave;i trước trở th&agrave;nh gi&aacute;o chủ Minh Gi&aacute;o]</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Hiện tại gi&aacute;o chủ Minh Gi&aacute;o l&agrave; Trương V&ocirc; Kỵ, m&agrave; trong [Ỷ Thi&ecirc;n Đồ Long K&yacute;] Minh Gi&aacute;o l&agrave; Minh triều đời trước, m&agrave; Ho&agrave;ng đế khai quốc Minh Triều l&agrave; Chu Nguy&ecirc;n Chương. Mặc kệ n&agrave;o l&agrave; nhất bản Trương V&ocirc; Kỵ, cuối c&ugrave;ng đều kh&ocirc;ng c&oacute; trở th&agrave;nh ho&agrave;ng đế. Chu Nguy&ecirc;n Chương sau khi l&agrave;m ho&agrave;ng đế, kết cuộc mọi người trong Minh Gi&aacute;o như thế n&agrave;o, Lục Qu&acirc;n Hi tuy rằng kh&ocirc;ng r&otilde; r&agrave;ng nhưng cũng c&oacute; thể đo&aacute;n được.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trương V&ocirc; Kỵ kh&ocirc;ng th&iacute;ch hợp l&agrave;m ho&agrave;ng đế, m&agrave;Chu Nguy&ecirc;n Chương.... Lục Qu&acirc;n Hi c&uacute;i đầu trầm tư, nhiệm vụ của hắn l&agrave; &quot;Đem Minh Gi&aacute;o ph&aacute;t triển trở th&agrave;nh thi&ecirc;n hạ đệ nhất Ma Gi&aacute;o&quot;, nếu hắn xuy&ecirc;n sớm v&agrave;i năm, kh&ocirc;ng cần hắn ph&aacute;t triển cũng l&agrave; ch&iacute;nh ph&aacute;i k&ecirc;u đ&aacute;nh Ma Gi&aacute;o đi? Nhưng hiện tại Trương V&ocirc; Kỵ lập ch&iacute; muốn thay đổi h&igrave;nh tượngMinh Gi&aacute;o, như vậy chỉ c&oacute; thể l&agrave; sau khi Minh triều th&agrave;nh lập lại quay về ban đầu được rồi. Cứ như vậy, chỉ cần tại dưới m&iacute; mắt Chu Nguy&ecirc;n Chương bảo to&agrave;n ch&iacute;nh m&igrave;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">&quot;Qu&acirc;n Hi đệ đệ, ph&aacute;t ngốc c&aacute;i g&igrave;?&quot; Dương Bất Hối th&ograve; ng&oacute;n tay chọt chọt mặt Lục Qu&acirc;n Hi, người liền chạy ra trốn ph&iacute;a sau Trương V&ocirc; Kỵ.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi ở trong l&ograve;ng tự khen ngợi kỹ năng diễn xuất của bản th&acirc;n, sau đ&oacute; liền trở th&agrave;nh c&aacute;i đu&ocirc;i nhỏ của Trương V&ocirc; Kỵ, hầu như Trương V&ocirc; Kỵ đi đ&acirc;u hắn liền đi theo ph&iacute;a sau.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">==================================</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">T&aacute;c giả c&oacute; lời muốn n&oacute;i: Đ&agrave;o hố mới~lăn lộn cầu comment~</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lăn lộn tại chỗ cầu bao dưỡng~</span></span></p>', 1, '2022-03-31 13:12:52', '2022-03-31 13:13:49');
INSERT INTO `chapters` (`id`, `book_id`, `name`, `slug`, `description`, `content`, `active`, `created_at`, `updated_at`) VALUES
(22, 16, 'Chương 2', 'chuong-2', '<p>ưerwfse</p>', '<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Mấy ng&agrave;y sau, mọi người trong Minh Gi&aacute;o thương thế khỏi hẳn, Trương V&ocirc; Kỵ hạ lệnh ra khỏi mật đạo, hiện th&acirc;n tr&ecirc;n Đỉnh Quang Minh, bất qu&aacute; một bữa cơm c&ocirc;ng phu, vẫn tại Đỉnh Quang Minh t&aacute;c uy t&aacute;c ph&uacute;c liền đem m&ocirc;n ph&aacute;i nhỏ giết hơn ph&acirc;n nửa.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi vẫn đi theo b&ecirc;n người Trương V&ocirc; Kỵ, dưới loại t&igrave;nh huống n&agrave;y kh&ocirc;ng c&oacute; nơi n&agrave; an to&agrave;n hơn. Hắn nghi&ecirc;m t&uacute;c quan s&aacute;t cẩn thận tốc độ c&ugrave;ng với uy lực c&aacute;c chi&ecirc;u thức của v&otilde; l&acirc;m nh&acirc;n sĩ, y&ecirc;n lặng m&ocirc; phỏng lại trong đầu c&ugrave;ng người đ&aacute;nh nhau th&igrave; n&ecirc;n ứng đối thế n&agrave;o. Chỉ tiếc hắn vẫn bị gi&aacute;m thị, căn bản kh&ocirc;ng c&oacute; khả năng đi đem kỹ năng Minh Gi&aacute;o đều nghi&ecirc;m t&uacute;c xem một lần l&agrave; c&oacute; thể học được.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">L&uacute;c chơi game Kiếm Tam l&agrave; thao t&aacute;c bằng tay, cho d&ugrave; cao thủ lợi hại nhất v&otilde;ng du đi nữa, cũng kh&ocirc;ng thể cam đoan sau khi b&agrave;n ph&iacute;m v&otilde;ng du biến th&agrave;nh to&agrave;n tức v&otilde;ng du m&agrave; vẫn c&oacute; thể l&agrave;m cao thủ. Huống chi, đ&acirc;y l&agrave; một thế giới ho&agrave;n to&agrave;n ch&acirc;n thật, mỗi người đều c&oacute; suy nghĩ của ch&iacute;nh m&igrave;nh. Ngoại trừ th&acirc;n thể được số liệu ho&aacute; c&oacute; thể chết đi sống lại, Lục Qu&acirc;n Hi cảm thấy ưu thế của bản th&acirc;n cũng kh&ocirc;ng nhiều, thậm ch&iacute; ở phương diện th&acirc;n thủ so với những người trong giang hồ cả ng&agrave;y đ&aacute;nh đ&aacute;nh giết giết kia th&igrave; xa xa kh&ocirc;ng b&igrave; kịp.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Cho n&ecirc;n hiện tại hắn cần lấy được t&iacute;n nhiệm của người trongMinh Gi&aacute;o cũng c&ugrave;ng bọn họ luyện v&otilde; để b&ugrave; đắp chỗ thiếu hụt của bản th&acirc;n.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Ngay khi Lục Qu&acirc;n Hi đang suy nghĩ cẩn thận phương &aacute;n trong tương lai, Trương V&ocirc; Kỵ đi ra ngo&agrave;i đối với c&aacute;c m&ocirc;n ph&aacute;i nhỏ c&ograve;n dư lại cất cao giọng n&oacute;i: &quot;Cao thủ Minh Gi&aacute;o giờ ph&uacute;t n&agrave;y tụ hội tr&ecirc;n Đỉnh Quang Minh. Chư đại bang hội m&ocirc;n ph&aacute;i nghe, đấu đ&aacute; v&ocirc; &iacute;ch, đồng loạt bỏ binh kh&iacute; xuống đầu h&agrave;ng, tha c&aacute;c ngươi khỏi con đường chết, hảo hảo đưa c&aacute;c ngươi xuống n&uacute;i.&quot;</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trương V&ocirc; Kỵ quả nhi&ecirc;n l&agrave; người tốt. Lục Qu&acirc;n Hi mặt kh&ocirc;ng ch&uacute;t thay đổi ph&aacute;t thẻ người tốt, sau đ&oacute; nh&igrave;n Minh Gi&aacute;o đem những người kh&ocirc;ng c&oacute; &yacute; ch&iacute; chiến đấu nhất nhất đem những người n&eacute;m binh kh&iacute; trong tay trục xuất xuống n&uacute;i, cuối c&ugrave;ng lại đi đến b&ecirc;n cạnh Trương V&ocirc; Kỵ, n&acirc;ng tay k&eacute;o tay &aacute;o đối phương.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvaTBSVWJ0bFE=\" rel=\"nofollow\" target=\"_blank\"><img alt=\"\" src=\"https://iads.staticscdn.net/ads/2022/02/25/images/400x300-sxC3Vdyyg1vlH8xAI0hjKAVXES93oJPApryYQU86.jpg\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvaTBSVWJ0bFE=\" rel=\"nofollow\" target=\"_blank\">Combo tẩy da chết m&ocirc;i kết hợp giữa dầu dừa v&agrave; c&agrave; ph&ecirc;, cho l&agrave;n m&ocirc;i mềm mịn căng b&oacute;ng, gi&aacute; rẻ nhất Shopee!</a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvNWdpZ2pWcVA=\" rel=\"nofollow\" target=\"_blank\"><img alt=\"\" src=\"https://iads.staticscdn.net/ads/2022/02/25/images/400x300-8GNLv3DWmjyUBb9bsVEVtavE45maUBvY80FHEEUU.jpg\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvNWdpZ2pWcVA=\" rel=\"nofollow\" target=\"_blank\">Miếng chắn dầu mỡ bếp ga họa tiết hoạt h&igrave;nh cực cute, đảm bảo bếp sạch sẽ, vệ sinh</a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvVE16c3I3dk8=\" rel=\"nofollow\" target=\"_blank\"><img alt=\"\" src=\"https://iads.staticscdn.net/ads/2022/02/25/images/400x300-x3mDhOq3OIxdKKqA9JAUWExiqQCqGCthdDAIhLLv.jpg\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvVE16c3I3dk8=\" rel=\"nofollow\" target=\"_blank\">D&eacute;p nữ gấu đế b&aacute;nh m&igrave; 3 m&agrave;u si&ecirc;u xinh, đảm bảo mềm ch&acirc;n dễ đi với mức gi&aacute; kh&ocirc;ng thể rẻ hơn</a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvRWpZSDZNNU0=\" rel=\"nofollow\" target=\"_blank\"><img alt=\"\" src=\"https://iads.staticscdn.net/ads/2022/02/25/images/400x300-Fn49WUT6pSoJA5SzCwqCF7hjvfjGYWI9cSBhmVke.jpg\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvRWpZSDZNNU0=\" rel=\"nofollow\" target=\"_blank\">Hạt lưu hương xả vải gi&uacute;p đ&aacute;nh tan vết bẩn v&agrave; cho quần &aacute;o m&ugrave;i thơm cực dễ chịu, lưu hương suốt cả ng&agrave;y!</a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Trải qua mấy ng&agrave;y ở chung, Trương V&ocirc; Kỵ đ&atilde; quen đối với động t&aacute;c của Lục Qu&acirc;n Hi, hắn rất thuần thục k&eacute;o tay Lục Qu&acirc;n Hi. Trương V&ocirc; Kỵ khi c&ograve;n b&eacute; lớn l&ecirc;n tại Băng Hỏa Đảo, tr&ecirc;n đảo trừ bỏ hắn một ấu đồng ngoại, cũng chỉ c&oacute; cha mẹ hắn c&ugrave;ng nghĩa phụ Tạ Tốn. Sau khi rời khỏi Băng Hỏa Đảo, lại gặp phải đại nạn, một đường nhấp nh&ocirc;, l&uacute;c n&agrave;y xuất hiện h&agrave;i tử to&agrave;n t&acirc;m to&agrave;n &yacute; chỉ tin v&agrave;o hắn, b&ugrave; v&agrave;o khoảng trống cho tới nay, c&agrave;ng l&agrave;m cho hắn c&oacute; cảm gi&aacute;c thỏa m&atilde;n khi bị ỷ lại. Mấy ng&agrave;y nay ở chung hắn đ&atilde; đem Lục Qu&acirc;n Hi trở th&agrave;nh đệ đệ của m&igrave;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi nh&igrave;n tr&ecirc;n Đỉnh Quang Minh dấy l&ecirc;n ngọn lửa lớn, b&ecirc;n tai l&agrave; lời cầu nguyện của mọi người Minh Gi&aacute;o cảm tạ Minh T&ocirc;n th&aacute;nh hỏa bảo hộ, hắn kh&ocirc;ng khỏi nghĩ đến Th&aacute;nh Mộ sơn tr&ecirc;n điện Đại Quang Minh, đ&oacute; mới l&agrave; ch&acirc;n ch&iacute;nh nguy nga đồ sộ đại kh&iacute;, nếu c&oacute; thể ở nơi n&agrave;y vẽ ra bản đồ m&ocirc;n ph&aacute;i Minh Gi&aacute;o... Nghĩ qu&aacute; xa.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Về th&aacute;nh hỏa... Trong tr&ograve; chơi đệ tử Minh Gi&aacute;o tắm trong th&aacute;nh hỏa liền c&oacute; thể trực tiếp l&ecirc;n tầng cao nhất điện Đại Quang Minh, kh&ocirc;ng biết th&aacute;nh hỏa nơi n&agrave;y c&oacute; loại c&ocirc;ng hiệu n&agrave;y hay kh&ocirc;ng?</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Tiếp tục trong trạng th&aacute;i mặt than ngồi ở b&ecirc;n người Trương V&ocirc; Kỵ, Lục Qu&acirc;n Hi thực b&igrave;nh tĩnh b&agrave;ng quan ph&aacute;t triển kịch t&igrave;nh.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://shp.ee/66g7p2r\" rel=\"nofollow\" target=\"_blank\"><img alt=\"\" src=\"https://iads.staticscdn.net/ads/2022/02/11/images/400x300-LHQKM62qAxNB6uWH8RhACXlYkLlKyqdmPS3MNQ7A.jpg\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://shp.ee/66g7p2r\" rel=\"nofollow\" target=\"_blank\">&Aacute;o thun nữ si&ecirc;u rẻ đẹp cho H&egrave; 2022 - Phong c&aacute;ch H&agrave;n si&ecirc;u xịn - Duy nhất tr&ecirc;n Shopee!</a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvWnNaeWYyOE0=\" rel=\"nofollow\" target=\"_blank\"><img alt=\"\" src=\"https://iads.staticscdn.net/ads/2021/10/22/images/400x300-tXnyAn10CUrh9voP3HdYYlxAW3IZBSltS1hL0F9U.jpg\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvWnNaeWYyOE0=\" rel=\"nofollow\" target=\"_blank\">Si&ecirc;u phẩm chống l&atilde;o h&oacute;a da - Collagen 900 được 95% phụ nữ tin d&ugrave;ng tr&ecirc;n Shopee!</a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvS2kyYnJBcG4=\" rel=\"nofollow\" target=\"_blank\"><img alt=\"\" src=\"https://iads.staticscdn.net/ads/2022/02/22/images/400x300-SRxEca5NPasQOJuEdxbUOfFOtyWyTRKFi8J6rls8.jpg\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvS2kyYnJBcG4=\" rel=\"nofollow\" target=\"_blank\">Kh&ocirc;ng thể bỏ qua: Th&ugrave;ng r&aacute;c mini nắp lật h&igrave;nh ch&uacute; m&egrave;o c&oacute; tai cực cute v&agrave; nhiều c&ocirc;ng dụng cho c&aacute;c n&agrave;ng!</a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvSnRURzIzWkY=\" rel=\"nofollow\" target=\"_blank\"><img alt=\"\" src=\"https://iads.staticscdn.net/ads/2022/02/22/images/400x300-daO8or18iQVf8DvCTIeftQ2D6106Og4QI4SVh5jx.jpg\" /></a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><a href=\"https://iads.vision/gt.php?ug=aHR0cHM6Ly9leWVwLmJsb2cvSnRURzIzWkY=\" rel=\"nofollow\" target=\"_blank\">M&aacute;y x&ocirc;ng si&ecirc;u tiện lợi v&agrave; hiện đại gi&aacute; chỉ hơn 39k, đảm bảo nhanh gọn lẹ, gia đ&igrave;nh n&agrave;o cũng n&ecirc;n c&oacute;!</a></span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đầu ti&ecirc;n l&agrave; Thi&ecirc;n Ưng gi&aacute;o quay về Minh Gi&aacute;o, sau đ&oacute; l&agrave; Trương V&ocirc; Kỵ đặt ra ba quy củ: Điều thứ nhất, Minh Gi&aacute;o tr&ecirc;n dưới mỗi người giữ nghi&ecirc;m gi&aacute;o quy, vi thiện đi &aacute;c, h&agrave;nh hiệp trượng nghĩa; Gi&aacute;o ch&uacute;ng chi gian th&acirc;n &aacute;i hỗ trợ, giống như tay ch&acirc;n, kh&ocirc;ng tranh chấp v&igrave; lợi &iacute;ch c&aacute; nh&acirc;n. Điều thứ hai, c&ugrave;ng c&aacute;c m&ocirc;n ph&aacute;i Trung Nguy&ecirc;n bỏ qua hiềm kh&iacute;ch trước đ&acirc;y, kh&ocirc;ng đi g&acirc;y th&ugrave; chuốc o&aacute;n. Điều thứ ba, hắn tạm giữ chức vụ đại gi&aacute;o chủ, do t&igrave;m về được Th&aacute;nh Hỏa Lệnh thuộc về người tiếp nhận chức vụ, cũng đi hải ngoại t&igrave;m về Kim Mao Sư Vương Tạ Tốn.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi hạ quyết t&acirc;m muốn c&ugrave;ng Trương V&ocirc; Kỵ đi Băng Hỏa Đảo, hắn c&ograve;n nhớ r&otilde; trong nguy&ecirc;n t&aacute;c Minh Gi&aacute;o Th&aacute;nh Hỏa Lệnh ch&iacute;nh l&agrave; xuất hiện ngo&agrave;i hải ngoại, tuy rằng kh&ocirc;ng nhớ r&otilde; cụ thể l&agrave; tại h&ograve;n đảo n&agrave;o, nhưng đi c&ugrave;ng Trương V&ocirc; Kỵ nhất định sẽ gặp được.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Phải l&agrave;m nhanh mới tốt. Nếu nhiệm vụ l&agrave; muốn hắn trở th&agrave;nh gi&aacute;o chủ Minh Gi&aacute;o, lại đem Minh Gi&aacute;o ph&aacute;t triển th&agrave;nh thi&ecirc;n hạ đệ nhất Ma Gi&aacute;o, như vậy c&ocirc;ng ph&aacute;p Minh Gi&aacute;o trong hệ thống tr&ograve; chơi Kiếm Tam rất c&oacute; khả năng c&oacute; thể dạy cho người thế giới n&agrave;y, như vậy hắn cần t&igrave;m mỗ kiện tới một mức độ n&agrave;o đ&oacute; bị thần thoại g&igrave; đ&oacute; l&agrave;m yểm hộ.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Thực hiển nhi&ecirc;n, Th&aacute;nh Hỏa Lệnh l&agrave; vật phẩm tốt nhất.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Nhật nguyệt vi minh, nhật nguyệt t&ocirc;n sư, Minh T&ocirc;n.</span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi &aacute;nh mắt hơi hơi nheo lại, &aacute;nh mắt xuy&ecirc;n thấu qua liệt hỏa hừng hực trước mặt, phảng phất thấy sa mạc b&ecirc;n trong Th&aacute;nh Mộ sơn.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Đương nhi&ecirc;n, quan trọng nhất vẫn l&agrave; trở về hiện đại.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Bất qu&aacute; trước mắt vẫn l&agrave; thuyết phục Trương V&ocirc; Kỵ mang hắn đi theo.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\">Lục Qu&acirc;n Hi hơi hơi quay đầu, v&ocirc; t&igrave;nh nh&igrave;n tho&aacute;ng qua Tiểu Chi&ecirc;u c&uacute;i đầu, đột nhi&ecirc;n c&oacute; chủ &yacute;. Hắn c&ograve;n nhớ r&otilde; tiểu nha đầu b&ecirc;n người Trương V&ocirc; Kỵ cuối c&ugrave;ng th&agrave;nh Ba Tư Minh Gi&aacute;o Th&aacute;nh Nữ, c&oacute; vẻ như l&agrave; con g&aacute;i của Tử Sam Long, như vậy mục đ&iacute;ch hắn xuất hiện tại trung nguy&ecirc;n Minh Gi&aacute;o cũng kh&ocirc;ng đơn giản.</span></span></p>\r\n\r\n<p><span style=\"font-size:26px\"><span style=\"font-family:Arial,Helvetica,sans-serif\"><ins><ins><ins><iframe frameborder=\"0\" height=\"280\" id=\"aswift_3\" name=\"aswift_3\" scrolling=\"no\" src=\"https://googleads.g.doubleclick.net/pagead/ads?client=ca-pub-3829250631432207&amp;output=html&amp;h=280&amp;slotname=6345663334&amp;adk=2233083804&amp;adf=2716712900&amp;pi=t.ma~as.6345663334&amp;w=1140&amp;fwrn=4&amp;fwrnh=100&amp;lmt=1648732374&amp;rafmt=1&amp;psa=1&amp;format=1140x280&amp;url=https%3A%2F%2Ftruyenfull.vn%2Fma-giao-chi-chu%2Fchuong-2%2F&amp;fwr=0&amp;fwrattr=true&amp;rpe=1&amp;resp_fmts=3&amp;wgl=1&amp;adsid=ChAI8KKVkgYQtL6oysSzoZNoEkgAei4V1E-b50E4lCzhJJVSyuS8dcE_EZi4xS31s08AI2ocfJjwvsRrKRMzdZFTl4_994adbKt9xofDrng6M2m2GRjf-r5pdf0&amp;uach=Wy